float map(vec2 p, inout int index) {
    vec2 mouse = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    float k = 1.0;
    if (iMouse.z > 0.0) {
        p -= mouse;
        k = dot(p,p);
        p /= k;
        p += mouse;
    }
    const float strong_factor = 4.;
#ifdef DrawVerticalSections
    vec3 q = vec3(p.x, iTime*0.1, p.y + 1.);
#else
    vec3 q = vec3(p, mod(iTime*0.2 - 0.01, SECTION_HEIGHT - 0.02) + 0.01);
#endif
    return k*DE(q, index) * strong_factor;

}

vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 post_process(vec3 col, vec2 uv) {
  col = pow(clamp(col, 0., 1.), vec3(1.0/2.2)); 
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *= 0.5 + 0.5*pow(19.0*uv.x*uv.y*(1.0-uv.x)*(1.0-uv.y), 0.7);
  return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    config;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = 2. * uv - 1.;
    p.x *= iResolution.x / iResolution.y;
    p *= ZOOM;
    float aa  = 2.0 / clamp(iResolution.y, 400., 800.);

    int index, cid;

    float d = map(p, index);
    cid = index;


    float b = -0.4;
    float t = 10.0;
    const float lh = 2.;
    const vec3 lp = vec3(2.5, 2.5, lh);

    vec3 ro = vec3(0, 0, t);
    vec3 pp = vec3(p, 0);

    vec3 rd = normalize(pp - ro);

    vec3 ld = normalize(lp - pp);

    float bt = -(t-b)/rd.z;

    vec3  bp   = ro + bt*rd;
    vec3  srd = normalize(lp - bp);
    float bl = L2(lp - bp);

    float st = (0.0-b)/srd.z;
    vec3  sp = bp + srd*st;

    float bd = map(bp.xy, index);
    float sd = map(sp.xy, index);

    vec3 col = vec3(0);
    const float ss = 15.0;
    col       += vec3(1.)  * (1.0 - exp(-ss*(max(sd, 0.0)))) / bl;
    float l   = length(p);
#ifdef DrawLimitSet
    float hue = fract(0.75*l-0.3*iTime) + .45;
#else
    float hue = fract(0.15*l-0.3*iTime) + .5;
#endif
    float sat = .9*tanh(4.*l);
    vec3 hsv  = vec3(hue, sat, 1.0);
    vec3 bcol = hsv2rgb(hsv);

    col       *= (1.0-clamp(tanh(0.75*l), 0., .1)) * 1.3;

#ifdef DrawLimitSet
    col       = mix(col, vec3(1), smoothstep(-aa, aa, -d));
#else
    col       = mix(col, bcol, 1. - step(-d, aa));
#endif
    col       += 0.5*sqrt(bcol.zxy)*(exp(-(10.0+100.0*tanh(l))*max(d, 0.0)));

    col = post_process(col, uv);
    fragColor = vec4(col,1.0);
}

#define MAX_ITER         30
#define PI               3.141592653
#define L2(x)            dot(x, x)
#define ZOOM             3.
#define inf              -1.

// comment out this to show the circles
#define DrawLimitSet


// uncomment this to show the vertice sections
//#define DrawVerticalSections

float dihedral(float x) {
    return x == inf ? 1. : cos(PI / x);
}

vec3 dihedral(vec3 v) {
    return vec3(dihedral(v.x), dihedral(v.y), dihedral(v.z));
}

float SECTION_HEIGHT;

// For spheres n is the center, r is the radius
// For planes n is the normal vector, r is the distance between the plane and the origin
// if invert is true then the inside/outside of the sphere is interchanged (not used in this program)
// if hasRealBall is true then this virtual ball has a real ball correspondes to it
struct Ball {
    bool isplane;
    vec3 n;
    float r;
    bool invert;
    bool isRealBall;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[5] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[5] clusters;


// create a ball. by default it's the z=0 plane with normal points to the negative half
Ball defaultBall() {
    return Ball(false, vec3(0, 0, -1), 0., false, false);
}

// Distance from a point to a ball
float sdistanceToBall(vec3 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        return k;
    }
    else
        return length(p - B.n) - B.r;
}

Ball from_plane(vec3 n, float d) {
    return Ball(true, n, d, false, false);
}

Ball from_sphere(vec3 cen, float r) {
    return Ball(false, cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec3 p, Ball B, inout float scale) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * B.n;
        return false;
    }
    else {
        vec3 cen = B.n;
        float r = B.r;
        vec3 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1.0 && B.invert) || (k > 1. && !B.invert) )
            return true;
        p = k * q + cen;
        scale *= k;
        return false;
    }
}

// return distance to the scene, and get the index of the real ball hitted
float DE(vec3 p, inout int index) {
    float scale = 3.;
    for (int i = 0; i < MAX_ITER; i++) {
        bool cond = true;
        for (int k = 0; k < 5; k++) {
            cond = try_reflect(p, coclusters[k], scale) && cond;
        }

        if (cond)
            break;
    }
    index = 0;
#ifdef DrawLimitSet
    float d = abs(p.z);
    for (int j = 1; j < 5; j++) {
        if (clusters[j].isRealBall) {
            float d1 = abs(sdistanceToBall(p, clusters[j]));
            if (d1 < d) { d = d1; index = j; }
        }
    }
#else
    float d = p.z;
    for (int j = 1; j < 5; j++) {
        if (clusters[j].isRealBall) {
            float d1 = sdistanceToBall(p, clusters[j]);
            if (d1 < d) { d = d1; index = j; }
        }
    }

#endif
    return d / scale;
}

// solve M*v = b, where v = (x, y, r) is the xy-coordinate of the center of the ball,
// r is the radius of the ball.
Ball solveBall(mat3 M, vec3 b) {
    vec3 p = b * inverse(M);
    return from_sphere(vec3(p.xy, 0.), p.z);
}

// Solve a ball with its center has xy-coordinates P, and is orthogonal to other two Balls B0, B1.
// Here B0 may be either a sphere or a plane, B1 is always a sphere. 
Ball solveBall(vec2 P, Ball B0, Ball B1) {
    if (B0.isplane) {
        float z = B0.r;
        vec3 cen = vec3(P, z);
        float R = sqrt(L2(cen - B1.n) - B1.r*B1.r);
        return from_sphere(cen, R);
    }
    else {
        float r1 = B1.r;
        float r0 = B0.r;
        float z0 = B0.n.z;
        float k0 = L2(P - B0.n.xy);
        float k1 = L2(P - B1.n.xy);
        float z = (r1*r1 - r0*r0 + z0*z0 + k0 - k1) / (2.*z0);
        float R = sqrt(k1 + z*z - r1*r1);
        return from_sphere(vec3(P, z), R);
    }

}

// triangleType: must be one of 236, 244 or 333
// dihedralAngles0: labels of the edges between v0 and (v2, v3, v4)
// dihedralAngles1: labels of the edges between v1 and (v2, v3, v4)
// dihedralAngles0_1: label of the edge between (v0, v1)
// isRealBall: specify which of (v1, v2, v3, v4) has real balls
void init(int triangleType,
          vec3 dihedralAngles0,
          vec3 dihedralAngles1,
          float dihedralAngle0_1,
          bvec4 isRealBall) {
    const float s2 = sqrt(2.);
    const float s3 = sqrt(3.);
    mat3 M0, M1;
    vec3 b;
    Ball B0, B1, B2, B3, B4;
    vec3 t0 = dihedral(dihedralAngles0);
    vec3 t1 = dihedral(dihedralAngles1);
    float t01 = dihedral(dihedralAngle0_1);
    // A, B, C are the vertices of the triangle formed by mirror plane v2, v3, v4 and z=0 plane
    vec2 A, B, C;

    // the 236 case
    if (triangleType == 236) {
        A = vec2(0, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s3/2., -0.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(s3/2., 0.5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(s3/2., 0.5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s3/2., 0);
    }

    // the 244 case
    else if (triangleType == 244) {
        A = vec2(0, 0), B = vec2(0, 1), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s2/2., -s2/2., 0), s2/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(1./s2, 1./s2, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(1./s2, 1./s2, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s2/2., 0);
    }

    // the 333 case
    else {
        A = vec2(-1, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(s3/2., -.5, 0), s3/2.);
        B3 = from_plane(vec3(-s3/2., -.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(-s3/2., 0.5, t1.x), vec3(s3/2., .5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(-s3/2., 0.5, t0.x), vec3(s3/2., .5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(s3, s3, 0)/2.;
    }

    // now we solve the virtual ball B1, this can't be a plane
    B1 = solveBall(M1, b);
    invertBall(B1);

    // now we solve the virtual ball B0, this can be either a plane or a sphere
    // this depends on if all entries in dihedralAngles0 are all 2
    if (dot(dihedralAngles0, vec3(1)) == 6.) {
        B0 = from_plane(vec3(0, 0, -1), B1.r*t01);
    }
    else {
        B0 = solveBall(M0, b);
        float r1 = B1.r, r0 = B0.r;
        B0.n.z = sqrt(r0*r0 + r1*r1 + 2.*r0*r1*t01 - L2(B1.n.xy - B0.n.xy));
        invertBall(B0);
    }
    coclusters = Ball[5] (B0, B1, B2, B3, B4);

    SECTION_HEIGHT = B0.isplane ? 2.*B0.r : B0.n.z;

    //now we process the real balls
    for (int k = 0; k < 5; k++)
        clusters[k] = defaultBall();

    if (isRealBall.x) {
        clusters[1] = from_plane(vec3(0, 0, -1.), B0.n.z);
        clusters[1].isRealBall = true;
    }
    if (isRealBall.y) {
        clusters[2] = solveBall(C, B0, B1);
        clusters[2].isRealBall = true;
    }
    if (isRealBall.z) {
        clusters[3] = solveBall(A, B0, B1);
        clusters[3].isRealBall = true;
    }

    if (isRealBall.w) {
        clusters[4] = solveBall(B, B0, B1);
        clusters[4].isRealBall = true;
    }
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Boyd-Maxwell circle packings and limit set of hyperbolic Coxeter groups: rank 4/level 2 case
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

For more information check the github repo:

    https://github.com/neozhaoliang/Hyperbolic-Honeycombs

Required input:

The Coxeter diagram of a rank 4 Coxeter group G = <A, B, C, D>, with dihedral angles
m_{s,t} in the order (AB, AC, AD, BC, BD, CD).

Note:

1. CD must be finite
2. The triangle group <B, C, D> must be hyperbolic

If you see only a sphere and a small disk under it, try comment out `#define useMouseInvert` below.
*/


