    vec2(-1, 0),
    vec2(-1.2, 0)
);


vec2 applyMobius(vec2 z)
{
    vec2 z1 = cmul(mob.A, z) + mob.B;
    vec2 z2 = cmul(mob.C, z) + mob.D;
    return cdiv(z1, z2);
}

