float scene(vec3 p)
{
    vec3 op=p;

    float ground = p.y - (cos(min(100.,p.z)*.03)-1.)*2.;


    float woodCenter = sdSphere(p, 10.0);


    float best = ground;
    best=min(best,woodCenter);


    if(best==ground)
