// Sinusoidal stepped height field.
float getCellHeight(vec2 p){
    float cellHeight = (dot(sin(p*2.2 - cos(p.yx*1.4)), vec2(.25)) + .5);
    return floor(cellHeight*7.999)/90.;
}


float jigsawPiece(vec2 p, vec2 ip){
    // Random ID threshold.
    const float rnd = .5;    

    // Four random border IDs.
    vec4 idJoinNSEW = vec4(hash(ip + vec2(0, .5)), hash(ip + vec2(0, -.5)), 
                           hash(ip + vec2(.5, 0)), hash(ip + vec2(-.5, 0)));


    const float ew = .025; // Jigsaw edge width.
    const float cw = .14; // Jigsaw connector circle width.
    // Jigsaw circle offset perpendicularly from the border. If you change this,
    // find the "sR" varialbe below, and make adjustments there too.
    vec4 sR = vec4(.07); 

    vec4 sNSEW = vec4(idJoinNSEW.x>rnd? 1.: -1., idJoinNSEW.y>rnd? 1.: -1., 
                      idJoinNSEW.z>rnd? 1.: -1., idJoinNSEW.w>rnd? 1.: -1.);


    float checkerPat = (mod(ip.x + ip.y, 2.)<.5)? 1. : -1.;
    // Used to reorient tiles, depending on the checkerboard setup.
    vec2 e = checkerPat>0.? vec2(1, 0) : vec2(0, 1); 

    vec2 lROffs = vec2(2); // Large circle offset away from the center.

    float lR = length(vec2(.5, .5) -  vec2(0, -lROffs.y));

    // Move the point perpendicularly away from the border, then construct a large
    // circle equal in radius from the new central point to the corner of the square
    // side. In this case, due to symmetry, all circles will have the same radius.
    //

    float c2N = length(p + lROffs.y*e.yx) - lR + ew/2.;
    float c2S = length(p - lROffs.y*e.yx) - lR + ew/2.; // lR 
    float c2E = length(p + (1. + lROffs.x)*e.xy) - lR - ew/2.;
    float c2W = length(p - (1. + lROffs.x)*e.xy) - lR - ew/2.;
    float d = max(c2N, c2S);
    d = smax(d, -min(c2E, c2W), .05);

    // Equivalent to the lines above, just less work for the GPU.
    //float c2NS = length(abs(p) + lROffs.y*e.yx) - lR + ew/2.;
    //float c2EW = length(abs(p) - (1. + lROffs.x)*e.xy) - lR - ew/2.;
    //float d = smax(c2NS, -c2EW, .05);



    // Damn logic. If you switch the points based on checker pattern convexity, you have
    // to rotate the unique border IDs with them. So obvious, but this mistake cost me more time 
    // than I care to admit. Oh, and why I'm at it, I put this line below the unique rotation
    // block, so those went haywire too. :)
    idJoinNSEW = checkerPat<0.? idJoinNSEW.zwxy : idJoinNSEW; 
    sNSEW = checkerPat<0.? sNSEW.zwxy : sNSEW; 
    sR = checkerPat>0.? sR.zwxy : sR; 


    // I wanted to edge out the connector circles 
    if(idJoinNSEW.x<=rnd) sR.x = .1;
    if(idJoinNSEW.y>rnd) sR.y = .1;
    if(idJoinNSEW.z>rnd) sR.z = .1;
    if(idJoinNSEW.w<=rnd) sR.w = .1;    


    // With straight edges, you can simply translate the jigsaw connecter bits along the X and Y borders.
    // However, with curved edges, they need to follow the curves subtented from the main cirecles pivotal
    // point - Don't worry, I groaned about it too. :D 

    // Clamped to stop the circles from overlapping the edges.
    vec4 rAng = clamp((idJoinNSEW - .5), - .35, .35)*3.14159/20.; 
    mat2 rotN = rot2(rAng.x);
    mat2 rotS = rot2(rAng.y);
    mat2 rotE = rot2(rAng.z);
    mat2 rotW = rot2(rAng.w);


    const float sf = .05; // Smoothing factor. The effect is subtle, but rounds the pieces a little.

    // The connector circles run along the out curved edges of the jigsaw pieces. They're rotionally 
    // offset by a unique amount (based on unique border ID).
    float cN = length(rotN*(p + lROffs.y*e.yx) - (lR - ew - .0 - sNSEW.x*sR.x)*e.yx) - cw;
    float cS = length(rotS*(p - lROffs.y*e.yx) + (lR - ew - .0 + sNSEW.y*sR.y)*e.yx) - cw;
    float cE = length(rotE*(p -  (1. + lROffs.x)*e.xy) + (lR - ew - .0 + sNSEW.z*sR.z)*e.xy) - cw;
    float cW = length(rotW*(p +  (1. + lROffs.x)*e.xy) - (lR - ew - .0 - sNSEW.w*sR.w)*e.xy) - cw;


    // As explained above, test the unique random border number against a threshold
    // and either smoothly add or take away the connector circles. Make sure to do the exact
    // opposite with the border directly opposite.
    d = (idJoinNSEW.x>rnd)? smax(d, -(cN - ew), sf) : smin(d, cN, sf);
    d = (idJoinNSEW.y<=rnd)? smax(d, -(cS - ew), sf) : smin(d, cS, sf);
    d = (idJoinNSEW.z>rnd)? smax(d, -(cE - ew), sf) : smin(d, cE, sf);
    d = (idJoinNSEW.w<=rnd)? smax(d, -(cW - ew), sf) : smin(d, cW, sf);

    // Return the 2D distance field value.
    return d;
}



// "jigDist" contains four 2D jigsaw values, so we find the height for each 
// of the pieces and return the minimum 3D and 2D distance. By the way, I 
// left it in 2x2 loop form for informative purposes. I could easily have
// unrolled it.
//
vec2 jigsaw(vec3 p3, vec4 jigDist){

    const float jSc = 4.; // I had to tweak the scale to get things right.
    vec2 p = p3.xy*jSc;    
    const vec2 sc = vec2(2, 2);

    // The 3D and 2D distance values.
    float d = 1e5, d2 = 1e5;

    // The height of each object.
    float cellHeight = 0.;

    // Iterating through four repeat grids of objects - each spaced out to skip the
    // object in between. This way the individual grid objects will easily fit in
    // their cells.
    // 
    // G1 G2 G1 G2 G1 G2
    // G3 G4 G3 G4 G3 G4
    // G1 G2 G1 G2 G1 G2
    // G3 G4 G3 G4 G3 G4 // Etc.
    //
    for(int j = 0; j<=1; j++){
        for(int i = 0; i<=1; i++){


            vec2 ip = floor((p - vec2(i, j))/sc)*sc;

            // The main calculation. Normally expensive, but it's been precalculated
            // so is just being read out.
            //float c = jigDist[j*2 + i]; 
            //float c = jigsawPiece(mod(p + vec2(i, j), sc) - sc/2., ip + vec2(i, j));

            // Swizzling to avoid the switch statement generated by "dynamic vector indexing".
            // The following two lines are equivalent to the indexed line above.
            // Thanks to Cyberjax for this suggestion. It helps the compiler out a bit.
            float c = jigDist.x;
            jigDist.xyzw = jigDist.yzwx;

            // Record the minimum object's cell ID.
            if(c<d2) {
                cellID = ip + vec2(i, j);
            }

            // Minimum 2D field value.
            d2 = min(d2, c);

            // The height of the object.
            cellHeight = getCellHeight(ip + vec2(i, j));

            // Minimum 3D field value.
            d = min(d, smax(c/jSc, abs(p3.z - cellHeight - .0) - .5, .0115));



        }
    }

    // Return the 3D and 2D field values.
    return vec2(d, d2);

}

// The 2D jigsaw pattern value. Called via "Buf A" once per frame. See the function above
// for an explanation. By the way, if you're only interested in a 2D jigsaw value, this
// function will suffice.
vec4 jigsaw4(vec3 p3){

    const float jSc = 4.;
    vec2 p = p3.xy*jSc; // I had to tweak the scale to get things right.
    const vec2 sc = vec2(2, 2);

    vec4 d4 = vec4(0);

    for(int j = 0; j<=1; j++){
        for(int i = 0; i<=1; i++){

            vec2 ip = floor((p - vec2(i, j))/sc)*sc;
            vec2 q = mod(p + vec2(i, j), sc) - sc/2.;

            float c = jigsawPiece(q, ip + vec2(i, j));


            // Swizzling to avoid the switch statement generated by "dynamic vector indexing".
            // Thanks to Cyberjax for this suggestion. It helps the compiler out a bit.
            //d4[j*2 + i] = c;
            d4.x = c;
            d4.xyzw = d4.yzwx;

        }
    }

    // Return the four 2D texture values. We'll use them later -- in the raymarching 
    // equation -- to determine the minimum 3D extruded block field value.
    return d4;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // The 2D jigsaw piece function is by far the most expensive, but it's 
    // only called once per frame, then passed to the raymarching function
    // via the "Buf A" texture, which is a huge saving. Without this step,
    // attempting to raymarch this in realtime would stop a lot of systems 
    // in their tracks.
    //
      init_grids();
    vec2 p = fragCoord.xy/iResolution.xy;

    // The camera movement is provided via texture scrolling. We're doing it
    // this way, because it'd be difficult to wrap this particular pattern. 
    // Not impossible, but compilicated. Therefore, the texture area has to 
    // span beyond the canvas borders to keep it withing the camera's field
    // of view. Otherwise, the effect wouldn't work.
    //
    fragColor = jigsaw4(vec3(p*4. - moveXY(iTime), 0));

}

/*

	Jigsaw
	------

	I saw a few jigsaw images on the net and thought to myself that I'd set aside a few minutes
	to code one up. I wasn't aware of it until a few hours later, but it turns out that jigsaw
	patterns aren't as straight forward as you'd think... or I'm not particulary good at coding 
	jigsaw patterns. Either way, an embarrassing amount of time later, and here it is. :D

	Creating a 2D square jigsaw pattern requires a bit of finesse. Putting a wavy pattern together
    is more frustrating, but it can be done -- Constructing dissimilar interlocked asymmetrical 
    objects requires some thought. However, I started to realize that I might be in for more work 
    than I anticipated when I attempted to raymarch the pattern at a decent frame rate. :)

	I really try not to complicate things, but sometimes, it can't be avoided. Anyone familiar
	with rendering objects of varying heights across a repeat grid will know that rendering them 
    adjacent to one another isn't generally possible due to cell overlap artifacts. The only way 
	around it is to render every second grid cell across two dimensions (ensuring no object overlap), 
	then render three other grid combinations to fill in the spaces -- The return value being the 
	minimum distance of the four individual grid objects. Essentially, this means everything needs 
	to be rendered four times over, so even a mildy complicated object -- like a jigsaw piece -- 
	won't be feasible in real time... unless you can find a way to store calculations in a buffer.

	So, that's what I've done here. Luckily, the jigsaw pieces are extruded, so the 2D distance 
	field calculations can be precalculated and stored in a 2D texture, then read back. Even better, 
	all four calculations can be stored in each of the texture channels, which means just one call 
	in the distance field function. After that, there's still some extruded object construction
    and minimums to determine, but it involves significantly less calculation. In fact, my laptop
	can run this in fullscreen quite easily.

	I decided to jitter the shadows, ever so slightly. It definitely improves banding issues on 
	long shadow casts, but adds a slight amount of unecessary jitter to shadows that are in close
	proximity to the object. Overall, however, I felt it improved things significantly.

	In regard to the lighting and coloring, the scene is supposed to have a cardboard kind of 
	consistency... Not sure if it does entirely, but it turned out roughly the way I wanted.


    Interlocking Asymmetrical Examples:

	// Awesome example: This involves repetition of a single object and is achieved in realtime 
	// by reading the precalculated contour points from an array. Ultra also has a smooth
	// version of this.
	Shaded Horses - Ultraviolet
	https://www.shadertoy.com/view/lsXfDf

	// As above, it involves a single object, but is calculated on the fly.
	escherized tiling 2 (WIP) - FabriceNeyret2
	https://www.shadertoy.com/view/lsdBR7

*/


#define FAR 10.

// Gives a sheeny appearance. Intereting, but I prefer the flat look.
//#define FAKE_ENVIROMENT_REFLECTIONS

// Debug color overide. If you look in the "Common" tab, there's also a "FLAT_PATTERN" 
// define that will level the height out, effectively putting the pieces on a flat plane.
//#define GREY

// There's a slight perspective tilt on the camera, but this has a bit more -- to keep Dr2
// happy and give it less of a scrolling texture look. :)
//#define CAMERA_TWO



/*
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: https://developer.nvidia.com/gpugems/GPUGems3/gpugems3_ch01.html
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n ){

    n = max(abs(n), 0.001); //max(abs(n) - .2, 0.001); // etc.
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;

    // Textures are stored in sRGB (I think), so you have to convert them to linear space 
    // (squaring is a rough approximation) prior to working with them... or something like that. :)
    // Once the final color value is gamma corrected, you should see correct looking colors.
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);

}
*/


// Global 2D surface value. It's poor programming practice putting it here. I'll 
// tidy this up later.
//float surf2D;


// Raymarching a textured XY-plane, with a bit of distortion thrown in.
float map(vec3 p){

    // Sampling the 2D jigsaw pattern value from the texture, then passing it
    // into a relatively cheap 3D extrusion function to give the final distance
    // value.
    vec4 jigDist = texture(iChannel0, p.xy/4. + .5);
    vec2 jig = jigsaw(p - vec3(moveXY(iTime), 0), jigDist);

    //surf2D = jig.y; // Just the 2D value. Used for some cheap edging.

    return jig.x*.866; // 3D extruded distance field value.


}


// Standard raymarching function.
float trace(in vec3 ro, in vec3 rd){

    float t = 0., d;

    for(int i = 0; i<80; i++){

        d = map(ro + rd*t);
        if(abs(d)<.001 || t>FAR) break; // Alternative: 0.001*max(t*.25, 1.)
        t += d;
    }

    return min(t, FAR);

}

// Cheap shadows are the bain of my raymarching existence, since trying to alleviate artifacts is an excercise in
// futility. In fact, I'd almost say, shadowing - in a setting like this - with limited  iterations is impossible... 
// However, I'd be very grateful if someone could prove me wrong. :)
float softShadow(vec3 ro, vec3 lp, float k, float t){

    // More would be nicer. More is always nicer, but not really affordable.
    const int maxIterationsShad = 24; 

    vec3 rd = lp - ro; // Unnormalized direction ray.

    float shade = 1.;
    float dist = .001;  // Coincides with the hit condition in the "trace" function.  
    float end = max(length(rd), .0001);
    //float stepDist = end/float(maxIterationsShad);
    rd /= end;

    dist += hash31(ro + rd)*.007;

    // Max shadow iterations - More iterations make nicer shadows, but slow things down. Obviously, the lowest 
    // number to give a decent shadow is the best one to choose. 
    for (int i=0; i<maxIterationsShad; i++){

        float h = map(ro + rd*dist); //  map(ro + rd*dist + hash31(ro + rd)*dist*.03);
        //shade = min(shade, k*h/dist);
        shade = min(shade, smoothstep(0.0, 1.0, k*h/dist)); // Subtle difference. Thanks to IQ for this tidbit.
        // So many options here, and none are perfect: dist += min(h, .2), dist += clamp(h, .01, stepDist), etc.
        dist += clamp(h, .01, .25); 

        // Early exits from accumulative distance function calls tend to be a good thing.
        if (h<0. || dist > end) break; 
    }

    // I've added a constant to the final shade value, which lightens the shadow a bit. It's a preference thing. 
    // Really dark shadows look too brutal to me. Sometimes, I'll add AO also just for kicks. :)
    return min(max(shade, 0.) + .05, 1.); 
}


// An AO routine, tweaked to suit this particular example, and based on IQ's original.
float calcAO(in vec3 p, in vec3 n){

	float sca = 5., occ = 0.;
    for( int i = 0; i<5; i++ ){

        float hr = float(i + 1)*.1/5.; 
        float d = map(p + n*hr);
        occ += (hr - d)*sca;
        sca *= .7;
    }

    return clamp(1. - occ, 0., 1.);
}


// Standard normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 normal(in vec3 p) {
	const vec2 e = vec2(.001, 0);
	return normalize(vec3(map(p + e.xyy) - map(p - e.xyy), map(p + e.yxy) - map(p - e.yxy),	map(p + e.yyx) - map(p - e.yyx)));
}

