#define PI    3.141592653
const float[6] diagram = float[6](3.0, 2.0, 2.0, 3.0, 2.0, 5.0);

// active mirrors, 1 for active, 0 for non-active. This determines the position
// of the initial vertex hence the truncation type of the honeycomb.
const vec4 active_mirrors = vec4(1.0, 0.0, 0.0, 0.0);

// normal vectors of the four mirrors
mat4 M;

// the initial vertex
vec4 v0;

// size of the vertex and segments
const float vradius = 0.035;
const float sradius = 0.01;

float cvr = cos(vradius), svr = sin(vradius);
float csr = cos(sradius), ssr = sin(sradius);

// reflection about a plane with normal `n`
float ref(inout vec4 p, in vec4 n)
{
    float k = min(0., dot(p, n));
    p -= 2. * k * n;
    return k;
}



float knightyDD(float ca, float sa, float r)
{
    float x = 1. - r * r;
    float y = 2. - x;
    return r - (2. * r * ca - x * sa) / (x * ca + 2. * r * sa + y);
}


void init()
{
    // Cartan matrix of the Coxeter group:
    // ( -1  c01  c02  c03)
    // (c01   -1  c12  c13)
    // (c02  c12   -1  c23)
    // (c03  c13  c23   -1)
    // cij = -cos(PI / (Coxeter label bewteen i-th and j-th mirror))
    float c01 = -cos(PI / diagram[0]);
    float c02 = -cos(PI / diagram[1]);
    float c03 = -cos(PI / diagram[2]);
    float c12 = -cos(PI / diagram[3]);
    float c13 = -cos(PI / diagram[4]);
    float c23 = -cos(PI / diagram[5]);

    vec4 A, B, C, D;
    // find the reflection mirrors A, B, C, D.
    // A can be always chosen as x-axis
    A = vec4(1., 0., 0., 0.);
    B = vec4(c01, sqrt(1. - c01*c01), 0., 0.);
    C = vec4(c02, 0., 0., 0.);
    C.y = (c12 - C.x * B.x) / B.y;
    C.z = sqrt(abs(1. - dot(C.xy, C.xy))); // avoid rounding error in paracompact case
    D = vec4(c03, 0., 0., 0.);
    D.y = (c13 - D.x * B.x) / B.y;
    D.z = (c23 - dot(D.xy, C.xy) ) / C.z;
    D.w = -sqrt(abs(1. - dot(D.xyz, D.xyz))); // !important: - sign is used here

    M = mat4(A, B, C, D);
    v0 = normalize(active_mirrors * inverse(M));

}

// fold a point p in 4d up to a given maximum of steps, return true if
// we successfully transformed p into the fundamental domain else return false.
bool fold(inout vec4 p)
{
    float k;
    for(int i=0; i<20; i++)
    {
        k = 0.;
        p.x = abs(p.x);
        k += ref(p, M[1]);
        k += ref(p, M[2]);
        k += ref(p, M[3]);
        // break as soon as we find it's already in the fundamental domain
        if(k == 0.) return true;
    }
    return false;
}

// if distance between p and q is a, C is a circle with radius VR centered at q,
// then the distance from p to C is a - VR, hence
// cosh(a - VR) = cosh(a)cosh(VR) - sinh(a)sinh(VR)
// sinh(a - VR) = sinh(a)cosh(VR) - cosh(a)sinh(VR)
float dVertex(vec4 p, float r)
{
    float ca = dot(p, v0);
    float sa = 0.5 * length(p-v0) * length(p+v0);
    return knightyDD(ca * cvr + sa * svr, sa * cvr - ca * svr, r);
}
float dSegment(vec4 p, vec4 n, float r)
{
    float pn = dot(p, n);
    float pv = dot(p, v0);
    float nv = dot(n, v0);

    float a = pv - pn*nv;
    float b = pn - pv*nv;
    vec4 pj = normalize(min(b, 0.) * n + a * v0);
    float ca = dot(p, pj);
    float sa = 0.5 * sqrt(1.-ca*ca);
    return knightyDD(ca * csr + sa * ssr, sa * csr - ca * ssr, r);
}

float dSegments(vec4 p, float r, inout float index)
{
    float d = 1.0;
    for(int i=0; i<4; i++)
    {
        float dseg = dSegment(p, M[i], r);
        if (dseg < d)
        {
            d = dseg;
            index = float(i);
        }
    }
    return d;
}

/*

Winding number theorem:

A man is walking his dog along closed paths represented by f(z) and g(z),
respectively. If the man holds the leash tight enough to prevent the dog
from touching the tree throughout their walk, they will circle the tree the
same number of times when they return to their initial positions.

Rouch's theorem:

When f and g are analytic functions in a region that encloses the unit circle S
and |f(z)| > |f(z) - g(z)| whenever z in S (here |f-g| is the length of the leash),
then f(S) and g(S) will circle the origin the same number of times, and this number
equals the number of their zeros inside S.
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - iResolution.xy*.5) / iResolution.y;

    vec2 p = uv * 9.;
    p -= vec2(1.5, .5); // adjust the postion of the curves
    vec2 O = vec2(0.5);

    // Smoothing factor.
    float sf = 8./iResolution.y;

    // background color
    vec3 col = vec3(1, .92, .75);
    float hatch = doHatch(uv, iResolution.y);
    col *= hatch*.2 + .8;

    vec2 e = vec2(.015, .03);
    float le = length(e);

    // curve width
    float lw = .02;

    float dc1 = dcurve(p, 0);
    float dc2 = dcurve(p, 1);

    float tA = mod(iTime*.2, 2.*PI);
    float tB = max(0., tA + sin(iTime) * .1 - .1);
    vec2 A0 = map1(0.), B0 = map2(0.);

    vec2 A = map1(tA);
    vec2 B = map2(tB);
    float dA = length(p - A) ;
    float dB = length(p - B) ;

    // dash grid lines
    vec2 p1 = uv * 6.;
    p1 -= floor(p1) + 0.5;
    float bord = max(abs(p1.x), abs(p1.y))-0.49;
    vec2 q1 = abs(mod(p1, 1./8.) - .5/8.);
    float lines = (min(q1.x, q1.y) - .5/8./3.);
    bord = min(bord, lines);
    bord = step(0., bord);

    float dlink = dSegment(p, A, B);
    // noisy background pattern
    col *= fbm(p*48.)*.4 + .6;
    // draw the grid lines
    col = mix(col, vec3(0), bord*.8);

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*16., dc2))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*8., dc2 - lw*2.4));
    col = mix(col, colorB, (1. - smoothstep(0., sf*4., dc2 - lw*1.6))*.8);

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*16., dc1))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*8., dc1 - lw*2.4));
    col = mix(col, colorA, 1. - smoothstep(0., sf*4., dc1 - lw*1.6));

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*.7, dlink - 0.005))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*.7, dlink - .008));
    col = mix(col, vec3(1, 1, .3), 1. - smoothstep(0., sf*.7, dlink));

    lw *= 12.;

    dA -= lw;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dA - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dA));
    dA += .12;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA - .03));
	col = mix(col, vec3(1, .8, .6), 1. - smoothstep(0., sf, dA)); 
    dA += .08;
	col = mix(col, vec3(0), (1. - smoothstep(0., sf, dA))); 

    dB -= lw;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dB - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB - 0.04));
	col = mix(col, colorB, 1. - smoothstep(0., sf, dB));
    dB += .12;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB - .03));
	col = mix(col, vec3(1, .8, .6), 1. - smoothstep(0., sf, dB)); 
    dB += .08;
	col = mix(col, vec3(0), (1. - smoothstep(0., sf, dB)));

    float dA0 = length(p - A0);
    dA0 -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dA0 - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA0 - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dA0));
    dA0 += .06;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA0));

    float dB0 = length(p - B0);
    dB0 -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dB0 - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB0 - 0.04));
	col = mix(col, colorB, 1. - smoothstep(0., sf, dB0));
    dB0 += .06;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB0));

    vec2 q = p - O;
    float ci = length(q) - .7;
    float sh = max(.75 - ci*4., 0.);
    col = mix(col, vec3(0), (ss(sf*6., ci - .04))*.5);
    col = mix(col, vec3(0), ss(sf, ci));
    col = mix(col, vec3(1, .7, .4)*(ci + sh*sh*.1 + .5), ss(sf, ci + .03));
    col = mix(col, col*1.6, ss(sf*4., ci + .15));
   	col = mix(col, vec3(0), ss(sf, abs(ci + .1) - .01));   
    col = mix(col, vec3(0), (ss(sf, length(q) - .18))*.5);
    col = mix(col, vec3(0), ss(sf, abs(length(q) - .12) - .01));
    col = mix(col, vec3(0), ss(sf, length(q) - .05));

    A -= O;
    B -= O;
    vec2 qA = rot2(atan(A.y, A.x-.5) -PI/2.) * q;
    vec2 qB = rot2(atan(B.y, B.x)-PI/2.) * q;
    float indA = distLine(qA - vec2(0, -.005), qA - vec2(0, .46)) - .01;
    float indB = distLine(qB - vec2(0, -.005), qB - vec2(0, .3)) - .01;

    const float rad = .45;
    const float aNum = 12.;
    q = rot2(3.14159/aNum)*q;
    float a = atan(q.y, q.x);
    float ia = floor(a/6.283*aNum) + .5; // .5 to center cell.
    ia = ia*6.283/aNum;
    q = rot2(ia)*q;
    q.x -= rad;

    // Markings.
    float mark = sBox(q, vec2(.04, .022), 0.);
    col = mix(col, vec3(.5), ss(sf, mark - .015));
    col = mix(col, vec3(0), ss(sf, mark));

    // Indicator.        
    col = mix(col, vec3(0), ss(sf, indA - .025));
    col = mix(col, colorA, ss(sf, indA));
    col = mix(col, vec3(0), ss(sf, indB - .025));
    col = mix(col, colorB, ss(sf, indB));

    p -= vec2(-7.5, -3.);
    p *= .8;
    float ds1 = min(abs(length(p) - 1.), sqrt(dSegment(p, vec2(0), vec2(1, 0))));
    col = mix(col, vec3(0.2), ss(sf, ds1 - 0.025));
    float dr = distRoots(p);
    col = mix(col, vec3(0.5, 0, 0), ss(sf, dr - .05));
    col = mix(col, vec3(0.2), ss(sf, length(p - vec2(1, 0)) - .1));

    vec2 P = vec2(cos(tA), sin(tA));
    float dP = length(p - P);

    dP -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dP - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dP - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dP));

    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    col *= pow(16.*(1. - uv.x)*(1. - uv.y)*uv.x*uv.y, 1./16.)*1.05;

    // Output to screen
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

#define NUM_SEGMENTS  100
#define PI            3.141592653
#define ss(a, b)      (1. - smoothstep(0., a, b))
#define colorA   vec3(1, 0.2, 0.2)
#define colorB   vec3(0.3, 1., .6)


mat2 rot2(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

// complex multiplication
vec2 cmul(vec2 p, vec2 q) {
    return vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x);
}

// complex division
vec2 cdiv(vec2 z, vec2 w) {
    return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

vec2 transform(vec2 z, vec2 a) {
    return cdiv(z - a, vec2(1, 0) - cmul(vec2(a.x, -a.y), z));
}

float n2D(vec2 p) {
    const vec2 s = vec2(1, 113);
    vec2 ip = floor(p); p -= ip;
    vec4 h = vec4(0., s.x, s.y, s.x + s.y) + dot(ip, s);
    p = p*p*(3. - 2.*p);
    h = fract(sin(h)*43758.5453);
    h.xy = mix(h.xy, h.zw, p.y);
    return mix(h.x, h.y, p.x);
}

float fbm(vec2 p) {
    return n2D(p)*.533 + n2D(p*2.)*.267 + n2D(p*4.)*.133 + n2D(p*8.)*.067; }

float distLine(vec2 a, vec2 b) {
    b = a - b;
    float h = clamp(dot(a, b)/dot(b, b), 0., 1.);
    return length(a - b*h);
}

float sBox(vec2 p, vec2 b, float r) {
    vec2 d = abs(p) - b + r;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - r;
}

float dot2(in vec2 v) {
    return dot(v, v);
}

float dSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return dot2(pa - ba*clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0));
}

vec2 map1(float t) {
    const float s = 2.;
    vec2 p = vec2(cos(t), sin(t));
    vec2 z = transform(p, vec2(.5, .7));
    z = cmul(z, transform(p, vec2(-.6, 0.5)));
    z = cmul(z, transform(p, vec2(-0.4)));
    z = cmul(z, p - vec2(s, s));
    return z;
}

float distRoots(vec2 p) {
    float d = length(p - vec2(.5, .7));
    d = min(d, length(p - vec2(-0.6, 0.5)));
    d = min(d, length(p - vec2(-0.4)));
    return d;
}

vec2 map2(float t) {
    return map1(t) + vec2(cos(t) * 1.6, sin(t) * 1.2);
}

float dcurve(vec2 p, int index) {
    float h = 0.05;
    float t = 0.0;

    vec2  a = index == 0 ? map1(t) : map2(t);
    float d = dot2(p - a);

    for(int i = 0; i < NUM_SEGMENTS; i++) {
        vec2  b = index == 0 ? map1(t) : map2(t);
        d = min(d, dSegment(p, a, b));
        t += clamp(0.01*length(a-p)/length(a-b), 0.01, 0.15);
        a = b;
    }
    return sqrt(d);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453);
}

float doHatch(vec2 p, float res) {
    p *= res/16.;
    float hatch;
    float hRnd = hash21(floor(p*6.) + .73);
    if(hRnd>.66) hatch = hRnd;
    else if (hRnd > 0.33) hatch = clamp(sin((p.x - p.y)*3.14159*200.)*2. + .5, 0., 1.);
    else hatch = clamp(sin((p.x + p.y)*3.14159*200.)*2. + .5, 0., 1.);
    return hatch;
}

/*

    Simple Dot Animation
    --------------------

    I love those cute geometric keyframe-based animations that pop up all 
    over the internet, often in gif form. They exist on Shadertoy too, and
    some are amazing, but they're not what I'd call common.

    I guess one of the reasons is that pixelshaders can be restrictive,
    since there's generally no random pixel access and calculations occur 
    on a per-pixel basis. Basically pixelshaders are not really designed for 
    motion tweening. However, it's all pretty doable. This particular 
    animation didn't require too much effort at all. There's also nothing
    original about it. I've seen similar ones all over the place.

    I remember back in the day when democoders finally started giving people 
    a look at the code magic behind the productions and I was amazed at how 
    simple the key framing component was: Divide time up into time blocks,
    each with their own start and end node markers. Determine which time  
    block you're in, set some start time entities (like position, color, 
    orientation, etc.), set the corresponding end time entities, interpolate 
    all entities over the block's time period, then proceed to the next time 
    block.

    Anyway, for this particular animation sequence, I'm breaking time into 
    three blocks, then interpolating the object position between the first 
    two, then position and rotation on the last. The process is repeated ad 
    infinitum, and is all pretty straight forward. The details are below.

    The animation rendering itself was put together pretty quickly. This is
    a 2D example, so speed wasn't really a consideration, but I did it in a
    way that would allow me to extrude it at a later date. Therefore, there
    are more than likely cleverer ways to render this, which I'll leave to 
    the code golfing crowd. :)



    Other examples:

    // Animated squares. 104 makes a lot of cool shaders. I've only 
    // skimmed the code, but I'm pretty sure this was created using 
    // similar principles.
    90's Mirrored Squares - 104
    https://www.shadertoy.com/view/3lBSzW


    // You can't mention keyframes without referencing this one. 
    // Clever on so many levels.
    [SH18] Human Document - reinder
    https://www.shadertoy.com/view/XtcyW4

*/

// Showing the background grid pattern can spoil the illusion, but it
// helps show how the animation is made.
//#define GRID


// Total running time, tmID, and time segment.
float tmTotal, tmID, tmSeg;

// 2x2 matrix rotation. Note the absence of "cos." It's there, but in disguise, and comes courtesy
// of Fabrice Neyret's "ouside the box" thinking. :)
mat2 rot2( float a ){ vec2 v = sin(vec2(1.570796, 0) + a);	return mat2(v, -v.y, v.x); }


// Fabrice's fork of "Integer Hash - III" by IQ: https://shadertoy.com/view/4tXyWN
float hash21(vec2 f){

    //f = mod(f, 4.); // The would work with wrapped elements too.

    // The first line relates to ensuring that icosahedron vertex identification
    // points snap to the exact same position in order to avoid hash inaccuracies.
    uvec2 p = floatBitsToUint(f);
    p = 1664525U*(p>>1U^p.yx);
    return float(1103515245U*(p.x^(p.y>>3U)))/float(0xffffffffU);
}


// Animated dots.
float dots(vec2 p, vec2 ip, vec2 sc){


    // Vertices: Clockwise from the bottom left.
    mat4x2 v = mat4x2(vec2(-1), vec2(-1, 1), vec2(1), vec2(1, -1));
    // Edges.
    mat4x2 e = mat4x2(vec2(-1, 0), vec2(0, 1), vec2(1, 0), vec2(0, -1));

     // Fractional time segment: Same as fract(tmTotal).
    float fTm = tmTotal - tmID; // Range: [0. 1].

    // Using smoothstep to smoothly interpolate the time period between
    // zero and one. You could also use any number of easing functions, 
    // but I'm trying to keep things simple. The following roughly
    // emulates a fast-in-the-middle motion... Not the technical term. :D
    fTm = smoothstep(.25, .75, fTm);


    // Object position and direction.
    vec2 pos = vec2(0), dir;

    // Object (dot) size. 
    float sz = .2*min(sc.x, sc.y);

    // The resultant field distance.
    float d;


    if(tmSeg<.001){

        // Verticle direction.
        dir = vec2(0, 1);


        vec2 di;
        // Left wall semi-circle moving up from v[0].
        di.x = length(p - (v[0] + dir*fTm)*sc/2.) - sz;
        di.x = max(di.x, -(p.x + sc.x/2.));

        // Right wall semi-circle moving down from v[2].
        di.y = length(p - (v[2] - dir*fTm)*sc/2.) - sz;
        di.y = max(di.y, (p.x - sc.x/2.));


        d = min(di.x, di.y);


    }
    else if(tmSeg<1.001){

        // Horizontal direction.
        dir = vec2(1, 0);

        // Splitting a semi circle running between 
        // the left and right edge midpoints and
        // moving them in opposite directions.

        vec2 di;
        // Moving left from e[2].
        di.x = length(p - (e[2] - dir*fTm)*sc/2.) - sz;
        di.x = max(di.x, p.y);

        // Moving right from e[0].
        di.y = length(p - (e[0] + dir*fTm)*sc/2.) - sz;
        di.y = max(di.y, -p.y);


        d = min(di.x, di.y);


    }
    else {

        // Instead of rendering four quarter circles in the center then moving
        // them to the corners, we're saving some rendering (kind of), but splitting
        // the cell into four smaller cells, then rendering diagonally from the
        // appropriate vertex to the opposite diagonal one.


         // We've effectively split each cell into fours, so based on the cell
         // ID, we need to determine which clockwise quadrant we're in.
         vec2 mIP = mod(ip, 2.);
         int j = mIP.x==0.? mIP.y==0.? 0 : 1 : mIP.y==0.? 3 : 2;

         // Starting vertex and ending vertex.
         pos = mix(v[(j + 2)&3], v[j], fTm); 
         // The diagonal direction vector.
         dir = (v[(j + 2)&3] - v[j]);

         // Interpolating a half spin over time.
         mat2 m2 = rot2(mix(0., 3.14159, fTm));

         // Moving a square (the same size as the dot) to the dot's central
         // point, rotating it about that point according to the rotational 
         // factor, then moving the edges to the dot center to create a rotating
         // quarter circle... Yeah, transformations can get a little confusing,
         // but they're not hard to learn, and you get better with practice.
         vec2 q = abs(m2*(p - pos*sc/2.) + sign(dir)*sz) - sz;
         float bound = max(q.x, q.y); // Bounding square.

         // The object position itself.
         float di = length(p - pos*sc/2.) - sz*2.;
         di = max(di, bound); // CSG with the bound to form a quarter circle.
         d = di; // Setting as the distance.

    }



    return d;
}

void mainImage( out vec4 o, in vec2 p){

    // Coordinates: They're already in aspect correct form, so a 
    // simple translation and scaling is all that is necessary.
    p = (p - iResolution.xy*.5)/iResolution.y; 

    vec2 op = p;

    // Last minute subtle plane warping.
    p *= .95 + length(p)*.1;
    // Screen rotation. I liked this angle, but you could choose another.
    p = rot2(3.14159/6.)*(p - vec2(2, 1)*iTime/60.);



    // Obviously, timing is important in keyframing. This breaks time into
    // three equal segments lasting 1.5 seconds each.
    tmTotal = iTime/1.5; 
    tmID = floor(tmTotal);
    tmSeg = mod(tmID, 3.);

    // Grid scale: I've set it to squares, but rectangles work too. 
    vec2 sc0 = vec2(1)/6.;
    vec2 sc = tmSeg<1.01? sc0 : sc0/2.;


    // A gradient background.
    vec3 c = mix(vec3(1, .7, .3), vec3(1, .8, .2), op.y + .5);
    #ifdef GRID 
    float rnd = hash21(floor(p/sc0));
    vec3 bCol = .5 + .45*cos(6.2831*rnd - op.y/2. + vec3(0, 1, 2));
    float ch = mod(dot(floor(p/sc0*2.), vec2(1)), 2.)<.5? -.1 : .1;
    c = mix(c, max(bCol + ch, 0.), .5);
    #endif 


    // Grid distance and shadow.
    float d = 1e5, dSh = 1e5;

    // Rendering the grid: Some of the objects move slightly outside the
    // cell bounds and into the neighboring cells, which means you need
    // to render the neighbors also. The following is a standard way to 
    // do that.
    for(int i = 0; i<4; i++){

        // Neighbor offset.
        vec2 offs = vec2(i&1, i/2) - .5;

        // Grid partitioning -- ID and local coordinates.
        vec2 q = p;
        vec2 iq = floor(q/sc + offs);
        q -= (iq + .5)*sc;

        // The dot field and shadow field.
        d = min(d, dots(q, iq, sc));
        dSh = min(dSh, dots(q - rot2(3.14159/6.)*vec2(-1)*.015, iq, sc));
    }

    // Smoothing factor.
    float sf = 1.5/iResolution.y;


    // Shadow, edge and face color.
    vec3 svC = mix(c*.2, vec3(.14), .75);
    c = mix(c, vec3(0), (1. - smoothstep(0., sf*2., dSh - .007))*.5);
    c = mix(c, vec3(0), 1. - smoothstep(0., sf, d - .007));
    c = mix(c, vec3(.14)*0. + svC, 1. - smoothstep(0., sf, d));

    // Rough gamma correction.
    o = vec4(sqrt(max(c, 0.)), 1);
}

