void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float fov = PI/3.0;
  vec2 xy = (fragCoord + 0.5) - 0.5*iResolution.xy;
  float z = -iResolution.y/(2.0*tan(0.5*fov));
  vec3 dir = normalize(transform(vec3(xy,z)));
  float camera = 18.0;
  camera *= 0.1*float(10+keycount(KEY_DOWN)-keycount(KEY_UP));
  vec3 origin = transform(vec3(0,0,camera));
  vec3 color = cast_ray(origin, dir);
  float maxc = max(color[0], max(color[1], color[2]));
  if (maxc > 1.0) color = color/maxc;
  color = pow(color,vec3(0.4545));
  fragColor = vec4(color,1);
}

