vec4 orbitTrap;

struct Ball {
    vec4 data;
    bool isplane;
    bool invert;
};

const Ball[5] balls = Ball[5] (
    Ball(vec4(0.12132034, 0.50730594, 2.3483438, 1.014611872354576), false, false),
    Ball(vec4(0, 1.73205081, 0, 2.449489742783177), false, true),
    Ball(vec4(0.8660254037844386,  -0.5, 0.0, 0.8660254037844386),  true, false),
    Ball(vec4(-0.8660254037844386, -0.5, 0.0, 0.8660254037844386), true, false),
    Ball(vec4(0, 1, 0, 0), true, false)
);


float distanceToBall(vec3 p, Ball B) {
    if (B.isplane)
        return dot(vec4(p, 1), B.data);
    else
        return length(p - B.data.xyz) - B.data.w;
}

bool try_reflect(inout vec3 p,
                 in Ball B,
                 inout float scale,
                 inout vec4 orb) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), B.data);
        if (k >= 0.)
            return true;
        p -= 2.0 * k  * B.data.xyz;
        return false;
    }
    else {
        vec3 cen = B.data.xyz;
        float r = B.data.w;
        vec3 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1.0 && B.invert) || (k > 1. && !B.invert) )
            return true;

        orb = min(orb, vec4(abs(p), d2));
        scale *= k;
        p = k * q + cen;
        return false;
    }
}

float map(inout vec3 p, inout float scale, inout vec4 orb) {
    orb = vec4(10000.0);
    for (int i = 0; i < Iterations; i++) {
        bool cond = true;
        for (int k = 0; k < 5; k++) {
            cond = cond && try_reflect(p, balls[k], scale, orb);
        }
        if (cond)
            break;
    }
    return abs(p.z);
}


float DE(vec3 p) {
    float DEfactor = 1.;
    float d = map(p, DEfactor, orbitTrap);
    return 0.25 * d / DEfactor;
}


vec3 getNormal(vec3 pos, float normalDistance) {
	normalDistance = max(normalDistance * 0.5, 1e-5);
	vec3 e = vec3(0.0, normalDistance, 0.);
	vec3 n = vec3(
        DE(pos + e.yxx) - DE(pos - e.yxx),
		DE(pos + e.xyx) - DE(pos - e.xyx),
		DE(pos + e.xxy) - DE(pos - e.xxy));

    n = normalize(n);
	return n == n ? n : vec3(0.0);
}


