float timemachine(vec3 p)
{
    p=swing(p+vec3(0,0,0));p.y+=1.15;

    machine_id=BASE;

    float d0=box(p+vec3(0.,0.02,0.), vec3(1.5,0.06,1.8));

    main_control(p,d0);
    lamp(p,d0);
    handles(p,d0);
    sofa(p,d0);

    p.x=abs(p.x)-1.17;
    float d1=line(p+vec3(0.,-0.1,1.45),vec3(0),vec3(0.,0.,1.5),0.2);
    if(d1<d0) {machine_id=FUEL; d0=d1;}
    return d0;
}


vec2 fold(vec2 p, float a)
{
    p.x=abs(p.x);
    vec2 n = vec2(cos(a),sin(a));
    for(int i = 0; i < 3; ++i)
    {
        p -= 2.*min(0.,dot(p,n))*n;
        n = normalize(n-vec2(1.,0.));
    }
    return p;
}

vec3 path(float p)
{
    return vec3(sin(p*0.05)*cos(p*0.025)*18., 0.,0.);
}

/// from Klem's Olympian: https://www.shadertoy.com/view/XltyRf
vec3 tunnel(vec3 rd, float pos, float speed) 
{
    const float max_r = 5.;
    vec3 col;//=vec3(1.);
    for (float r=1.0;r<max_r+float(min(0,iFrame));r+=1.0) 
    {
        // calculate where the ray intersects several fixed radius cylinders
        // using cylindrical coordinates
        // (phi, r, z)
        // phi=arctan(rd.y/rd.x)
        // r=r
        // length(rd.xy) / r = rd.z / z ==> z=rd.z*r/length(rd.xy)
        float phi=atan(rd.x, rd.y);
        float z=rd.z*r/length(rd.xy);
        if(r<1.5)col=vec3(abs(z)*.005);
        // adjust the uv acoording to cylinder size and position
        vec2 uv=vec2(phi*r, pos+z);
        uv.x+=1.717*hash13(vec3(floor(uv),r))*r;

        vec2 cell_center=floor(uv)+0.5;
        cell_center+=hash22(cell_center+vec2(0.,r))-.5;

        vec2 size=vec2(.01);
        size.y+=speed/r;
        size.y/=sin(atan(r/abs(z)));

        //float d=(length((uv-cell_center)/size)-1.)*size.y;
        //col+=vec3(1,.9,.6)*smoothstep(0.105,-0.105,d)/(.5*r*r+.3*z*z);


        float redshift=0.01+speed/r;
        cell_center.y-=redshift;
        for (int i=0; i<3+min(0,iFrame); i++) 
        {
            cell_center.y+=redshift;

            // draw stars
            vec2 p=uv-cell_center;
            float d=(length(p/size)-1.0)*size.y;
            float dist=(r*r+z*z);
            col[i]+=smoothstep(0.02, -0.02, d)/dist;
        }

    }
    return 3.*col;
}


float get_ao(vec3 p, vec3 n)
{
    float r=0.0, w=1.0, d;
    for(float i=1.; i<5.0+1.1+float(min(0,iFrame)); i++)
    {
        d=i/5.0;
        r+=w*(d-min(doraemon(p+n*d),timemachine(p+n*d)));
        w*=0.5;
    }
    return 1.0-clamp(r,0.0,1.0);
}



vec3 material_doraemon(vec3 rd, vec3 pos, vec3 nor)
{
    vec3 col=vec3(0);
    float d; 
    pos=swing(pos);

    if(obj_id==HEAD)
    {
        col=vec3(0.,0.5,1.0); 
        if(pos.z<0.&&length(vec2(pos.x*0.9,pos.y+0.05))<0.38)col=vec3(0.9);

        // eyes
        if(pos.z<0.)
        {
            vec2 p=vec2(abs(pos.x)-0.1,pos.y*.7-0.2);
            float r=length(p.xy);
            col*=pow(1.-smoothstep(0.08,0.09,r)*smoothstep(0.1,0.09,r),1.);
            if(r<0.085) col=vec3(1.);
            p=vec2(abs(pos.x)-0.06, pos.y-0.26);
            r=length(p);
            p=vec2(abs(pos.x)-0.06, pos.y-0.26);
            float r1=length(p);
            col*=pow(1.-smoothstep(0.0,0.025,r)*smoothstep(0.03,0.025,r),7.0);

            // beards
            if(pos.y>0.&&pos.y<0.11)
            col*=smoothstep(0.,0.01,abs(pos.x));
            col*=smoothstep(0.,0.01, line(vec2(abs(pos.x)-0.16,pos.y), vec2(0.,0.14),vec2(0.14,0.18),0.001));
            col*=smoothstep(0.,0.01, line(vec2(abs(pos.x)-0.16,pos.y+0.04), vec2(0.,0.14),vec2(0.15,0.15),0.001));
            col*=smoothstep(0.,0.01, line(vec2(abs(pos.x)-0.16,pos.y+0.08), vec2(0.,0.14),vec2(0.15,0.13),0.001));
        }
    }
    else if(obj_id==BODY)
    {
        col=vec3(0.,0.5,1.0); 
        if(pos.z<0.&&length(vec2(pos.x,pos.y+.6))<.22)col=vec3(0.9);
    }
    else if(obj_id==NOSE)
        col=vec3(.4,0.,0.);
    else if(obj_id==BELL)
        col=vec3(1.2,0.7,0.);
    else if(obj_id==HAND)
        col=vec3(.9);
    else if(obj_id==BASE)
        col=vec3(0.05,0.1,0.2);
    else if(obj_id==CONTROL)
        col=vec3(.9,.7,.5);
    else if(obj_id==CONTROL_FRONT)
    {
        col=vec3(.9,.7,.5);
        // pos.y -1.x -> -0.7
        col=mix(vec3(1.,0.2,0.),col,pow(smoothstep(0.,0.04, line(vec2(pos.x,pos.y), vec2(-0.5,-0.8),vec2(0.5,-0.8),0.001)),40.));
        col=mix(vec3(1.,0.2,0.),col,pow(smoothstep(0.,0.04, line(vec2(pos.x,pos.y), vec2(-0.5,-0.9),vec2(0.5,-0.9),0.001)),40.));
        col=mix(vec3(1.,0.2,0.),col,pow(smoothstep(0.,0.04, line(vec2(pos.x,pos.y), vec2(-0.5,-1.),vec2(0.5,-1.),0.001)),40.));

    }
    else if(obj_id==HANDLE1)
        col=vec3(0.,0.1,0.3);
    else if(obj_id==FUEL)
        col=vec3(0.01,0.04,0.1);
    else if(obj_id==HANDLE2||obj_id==SOFA)
        col=vec3(0.1,0.,0.04);
    else if(obj_id==LIGHT)
    {
        // pixel light
        vec2 frp=abs(fract(pos.xz*10.));
        frp=pow(frp, vec2(4.));
        float edge=max(0.,1.-(frp.x+frp.y));
        vec2 flp=floor(pos.xz*10.);
        float k=dot(sin(flp+cos(flp.yx*2.+iTime*2.)),vec2(.5));
        col=nor.y<0.? 10.*edge*vec3(pow(k,.7)*2., 4.*pow(k, 1.5), pow(k,2.)) : vec3(.9,.7,.5);
    }
    return col;
}

vec3 get_doraemon_normal(vec3 p)
{
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*doraemon(p+0.001*e);
    }
    return normalize(n);
}

vec3 get_timemachine_normal(vec3 p)
{
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*timemachine(p+0.001*e);
    }
    return normalize(n);
}

vec3 lighting_doraemon(vec3 rd, vec3 pos, float ps,float hitinfo, float t)
{
    vec3 l1dir=normalize(vec3(1.0,2.,-1.));
    vec3 l1col=vec3(1.,0.8,0.8);

    vec3 e=vec3(0.5*ps,0.0,0.0); 
    vec3 nor;

    if(hitinfo<.9)
        nor=get_doraemon_normal(pos);
    else
        nor=get_timemachine_normal(pos);

    if(timemachine(pos)<doraemon(pos))obj_id=machine_id;
    if(t>FAR&&obj_id==LIGHT)obj_id=CONTROL; // avoid weird artifacts, should find a bettwe way

    vec3 mate=material_doraemon(rd,pos,nor);
    float ao=get_ao(pos,nor);
    float dif=max(0.0,dot(nor,l1dir));
    float bac=max(0.0,dot(nor,-l1dir));
    float spe=max(0.0, pow(clamp(dot(l1dir, reflect(rd, nor)), 0.0, 1.0), 32.0));

    vec3 lin=6.0*dif*l1col*ao;
    lin+=1.*bac*l1col;
    lin+=3.*spe*vec3(1.);
    return lin*0.2*mate;
}

float pixel_size;

vec4 intersect_doraemon(vec3 ro, vec3 rd, out vec3 hitinfo)
{
    hitinfo=vec3(0.,0.,1.);
    float d_first=100.0, t_first=0.0;
    float old_d=1000.0;
    float d_max=1000.0, t_max=0.0;
    float t=1.0;
    float d=100.0;
    float hitwho=0.,old_hitwho=0.;

    for(int i=0; i<64+min(0,iFrame); ++i) 
    {
        hitwho=0.;
        // splitting them is just for not crashing my windows laptop....
        d=doraemon(ro+rd*t);
        float d1=timemachine(ro+rd*t);
        if(d1<d){hitwho=1.;d=d1;}


        if(d_first == 100.0)  // the first edge
        {
            hitinfo.x=hitwho;
            if(d>old_d) 
            {
                if(old_d<pixel_size * (t-old_d))
                {
                    d_first=old_d;
                    t_first=t-old_d;
                    hitinfo.x=old_hitwho;
                }
            }
            old_d=d;
            old_hitwho=hitwho;
        }
        if(d<d_max) // save the max occluder
        { 
            t_max=t; 
            d_max=d;
            hitinfo.y=hitwho;
        }  

        if(d<0.00001 || t>FAR)
            break;
        t += d;
        hitinfo.z=t;
    }
    return vec4(t_max, d_max, t_first, d_first);
}

float bounding_sphere(in vec3 ro, in vec3 rd, in vec4 sph)
{
    vec3 p=sph.xyz;
    p=inverse_swing(p);
    float t=-1.0;
    vec3  ce=ro-p;
    float b=dot(rd, ce);
    float c=dot(ce, ce)-sph.w*sph.w;
    float h=b*b - c;
    if(h>0.0)
    {
        t=-b-sqrt(h);
    }

    return t;
}

vec3 render_doraemon(vec3 ro, vec3 rd, vec3 bg)
{
    float t=bounding_sphere(ro,rd,vec4(0.,-0.35,0.,2.5));
    if(t<=0. || t>1000.)
        return bg;

    // first hit, max hit, t
    vec3 hitinfo;
    vec4 res=intersect_doraemon(ro,rd,hitinfo);

    float d_max, t_max, d_first, t_first;
    t_max=res.x;
    d_max=res.y;
    t_first=res.z;
    d_first=res.w;
    vec3 nor,pos;

    vec3 col=bg;

    if(d_max < pixel_size*t_max) 
    {
        pos=ro+rd*t_max;
        col=mix(lighting_doraemon(rd, pos, pixel_size*t_max,hitinfo.y, hitinfo.z), col, 
                  clamp(d_max/(pixel_size * t_max), 0.0, 1.0));
    }
    float ratio=0.0;

    if(d_first==100.0 || t_max==t_first)
    {
        t_first=t_max;
        d_first=d_max;
        ratio=0.5;
    }

    pos=ro+rd*t_first;
    col=mix(lighting_doraemon(rd, pos, pixel_size*t_first,hitinfo.x, hitinfo.z),
              col, clamp(ratio+d_first/(pixel_size*t_first), 0.0, 1.0));

    return col;
}


#define CITY_MENGER 1
#define CITY_ROAD 2

#define VOXEL_GAP 0.1

const mat3 ma=mat3(0.6,0.,0.8,
                   0.,1.,0.,
                   -0.8,0.,0.6);

vec4 city(vec3 p)
{
    p.y-=0.3;
    obj_id=CITY_MENGER;
    vec2 flp=floor(p.xz);
    vec2 frp=fract(p.xz);

    frp-=0.5;

    vec2 rand;
    rand=hash22(flp);
    float height=0.4+rand.x*rand.x*1.7;
    float d0=box(vec3(frp.x,p.y,frp.y),vec3(0.1,height,.4));   

    vec4 res=vec4(d0, 1.0, 0.0, 0.0);

    // menger spone from iq
    // larger value gives higher density of rooms
    // I like 1.1, 1.2, 1.7
    float s=1.+.2*rand.y*(1.-step(1.6,height)); 
    vec3 q=p;
    for(int m=0; m<4+min(0,iFrame); m++)
    {      
        p.y+=rand.y;
        vec3 a=mod(p*s, 2.0)-1.0;
        s*=3.;
        vec3 r=abs(1. - 3.0*abs(a));
        float da=max(r.x,r.y);
        float db=r.y;//max(r.y,r.z);
        float dc=max(r.z,r.x);
        float c=(min(da,min(db,dc))-1.)/s;

        if(c>d0)
        {
          d0=c;
          res=vec4(d0, min(res.y,.2*da*db*dc), (1.0+float(m))/4.0, 0. );
        }
    }

    if(q.y<res.x){obj_id=CITY_ROAD;res.x=q.y;}
    return res;
}


vec3 get_city_normal(vec3 p)
{
    // inspired by tdhooper and klems - a way to prevent the compiler from inlining map() 4 times
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*city(p+0.001*e).x;
    }
    return normalize(n);
}

#define CITY_ITER 250
#define CITY_FAR 50.

vec4 intersect_city_voxel(vec3 ro, vec3 rd)
{
    vec4 h=vec4(100.),res=vec4(-1);
    float t = 0.05;
    vec3 p=vec3(0.0);

    for (int i=0; i<CITY_ITER+min(0,iFrame); i++)
    {
        if(h.x<0.0001+0.000125*t||t>CITY_FAR)
        {
            continue;
        }
        p=ro+rd*t;

        h=city(p);

        // 2d voxel marching the city blocks, as the boundaries are not continues

        float dx=-fract(p.x);
        if (rd.x>0.) 
            dx=fract(-p.x);

        float dz=-fract(p.z);
        if (rd.z>0.)
            dz=fract(-p.z);

        float nearest=min(fract(dx/rd.x), fract(dz/rd.z))+VOXEL_GAP;
        nearest=max(VOXEL_GAP, nearest);

        t+= min(h.x, nearest); 
        res=vec4(t,h.yzw);

    }
    return res;
}

vec3 material_city(vec3 p, float night)
{
    vec3 col=vec3(0.4,0.6,1.0)*0.15;
    vec4 res=city(p);
    if(obj_id==CITY_MENGER)
    {
        res.z=1.-res.z;
        col=(1.2*(1.-night)+0.8)* vec3(.5+res.z*res.z,.3+pow(res.z, 3.), res.z*res.z*0.9);
        //col=vec3(res.z);  

        if(night>0.5)
        {
            if(res.z<.25)
                col=50.*vec3(1.2,0.3,0.);
            else if(res.z<0.75)
                col*=6.;
        }

    }
    else
    {
        p.z-=iTime*2.;
        col+=(1.-smoothstep(0.01,0.025,abs(abs(p.x)-.25)))*vec3(1);
        col=mix(col,vec3(1.2,0.7,0.),floor(fract(p.z)+.5)*(1.-smoothstep(0.01,0.02,abs(p.x)-0.001)));//*vec3(1);
        // fake shadow
        col*=(0.06+smoothstep(0.4,0.6,abs(abs(p.x+0.13)-0.7))); 
        col*=(0.06+smoothstep(0.4,0.6,-p.x+1.47))*vec3(1); 
    }
    return col;
}

vec3 city_bg(vec3 ro, vec3 rd, float night)
{
    const vec3 moon_col=vec3(0.8,1.,1.);
    const vec3 moon_dir=vec3(0,0,-1.);    
    vec3 col;

    col=vec3(1.)-moon_col*smoothstep(-.1,0.,rd.y)*smoothstep(0.33,0.37,pow(max(dot(moon_dir, rd), 0.0), 32.0));

    if(night>0.)
        col=vec3(0.)+vec3(0.3,0.,0.)*smoothstep(-.1,0.,rd.y)*smoothstep(0.33,0.37,pow(max(dot(moon_dir, rd), 0.0), 32.0));

    return col;
}


float curve(in vec3 p, in float w)
{
    vec2 e=vec2(-1., 1.)*w;

    float t1=city(p+e.yxx).x, t2=city(p+e.xxy).x;
    float t3=city(p+e.xyx).x, t4=city(p+e.yyy).x;

    return 0.0125/(w*w)*(t1+t2+t3+t4-4.*city(p).x);
}

vec4 render_city(vec3 ro, vec3 rd, float night)
{
    vec4 res=intersect_city_voxel(ro,rd);
    vec3 col=city_bg(ro,rd,night);

    if(res.x<CITY_FAR)
    {
       // if(city(ro+res.x*rd).x>0.005/res.x)
         //   res.x=res.w;
        vec3 pos=ro+res.x*rd;
        vec3 nor=get_city_normal(pos);
        vec3 l1dir=normalize(vec3(1.0,2.,-1.));
        vec3 l1col=vec3(1.2,0.8,0.5);

        float ao=res.y*res.y;
        if(obj_id==CITY_ROAD)
            ao=1.;

        vec3 mate=material_city(pos,night);
        float dif=max(0.0,dot(nor,l1dir));
        float bac=max(0.0,dot(nor,-l1dir));
        float sky=0.5+0.5*nor.y;
        float spe=pow(max(dot(reflect(-l1dir, nor), -rd), 0.0), 16.0);
        float crv=clamp(1.-abs(curve(pos,0.0015)),0.,1.);

        vec3 lin=4.0*(1.-night)*dif*l1col*ao;
        lin+=3.*sky*vec3(0.1,0.2,0.5)*ao;
        lin+=1.*bac*l1col*ao;
        lin+=1.*spe*vec3(1.);
        col=lin*crv*0.2*mate;

        vec3 skycol=vec3(1.-night);
        if(rd.z<0.)
            col=mix(col,vec3(.15,0,0),1.-exp(-0.005*res.x*res.x));
        else col=mix(col,skycol,1.-exp(-0.001*res.x*res.x));
       //col=vec3(lin);
    }
    return vec4(col,res.x);
}


vec3 tonemap(vec3 x) 
{
    const float a=2.51;
    const float b=0.03;
    const float c=2.43;
    const float d=0.59;
    const float e=0.14;
    return (x * (a * x + b)) / (x * (c * x + d) + e);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q=fragCoord/iResolution.xy;
    vec2 p=q*2.-1.;
    p.x*=iResolution.x/iResolution.y;
    pixel_size=1.0/(iResolution.y);

     // debugging camera
    //float x_rot=-iMouse.x/iResolution.x*PI*2.0;
    //float y_rot=iMouse.y/iResolution.y*3.14*0.5 + PI/2.0;
    //vec3 ro=vec3(0.,0.9,-2.*iTime)+vec3(cos(y_rot)*cos(x_rot),0.,cos(y_rot)*sin(x_rot))*10.;
    //vec3 ta=vec3(0.,0.,-2.*iTime);

    float time=mod(iTime,40.);
    vec3 ro,ro2;
    if(time < 20.)
    {
        ro=vec3(0.,.9,-9.);
        ro2=ro;

        if(time>10.)
        {
            ro.xz*=rot(-(time-10.)*0.07);
        }
    }
    else
    {
        ro=vec3(0.,.9,9.);
        ro2=ro;
    }

    ro.z-=2.*iTime;
    ro2.z-=2.*iTime;
    vec3 ta=vec3(0.,0.,-2.*iTime);


    vec3 f=normalize(ta-ro);
    vec3 r=normalize(cross(f,vec3(0.,1.,0.)));
    vec3 u=normalize(cross(r,f));

    vec3 rd=normalize(r*p.x + u*p.y + f*2.3);

    vec3 timetunnel=tunnel(rd,-iTime*8.,.05);
    vec3 menger=render_city(ro2,rd, 1.0-step(0.0,sin(0.05*(iTime+40.-14.)))).xyz;

    vec3 bg=mix(timetunnel,menger,1.-smoothstep(0.,1.,sin(0.1*(iTime+40.))));
    vec3 col=render_doraemon(ro,rd,bg);

    // post processing
    col=tonemap(col);
    col=pow(clamp(col,0.,1.0),vec3(0.45));
    col*=0.5+0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
    fragColor=vec4(col,1.);
}

/*
=============================================

Limit set of rank 4 hyperbolic Coxeter groups

                                by Zhao Liang
=============================================

This program shows the limit sets of rank 4 hyperbolic Coxeter groups.

Some math stuff:

Let G be a hyperbolic Coxeter group and x a point inside the hyperbolic
unit ball, the orbit S_x = { gx, g \in G } has accumulation points
(under Euclidean metric) only on the boundary of the space. We call the
accumulation points of S_x the limit set of the group, it can be proved that
this set is independent of the way x is chosen, and it's the smallest
closed subset of the boundary that is invariant under the action of the group.

The Coxeter-Dynkin diagram of a rank 4 Coxeter group of string type has the form

   A --- B --- C --- D
      p     q     r

Here A, B, D can be chosen as ususal Euclidean planes, C is a sphere orthongonal
to the unit ball. This is taken from mla's notation, and as far as I know this
has long been used by users on fractalforums. (fragmentarium)

In this animation these points are colored in "brass metal".

==========
!important
==========

The limit set is a closed set with no interior points, to show them we have
to use an approximate procedure: we simply try to reflect a point p on the
boundary to the fundamental domain up to a maximum steps, once failed then we
think p belongs to the limit set.

**So the number MAX_REFLECTIONS is an important param**, if it's set too high
then little limit set will be shown, or if it's not high enough then
the boundary of the set will look too coarse, so beware of this.

As always, you can do whatever you want to this work.

Update: thanks @mla for helping fix some bugs!

You can try more patterns like
(3, 7, 3), (4, 6, 3), (4, 4, 5), (5, 4, 4), (7, 3, 4), ..., etc.
5, 4, 4) is my favorite!
Set PQR below to see the result.
For large PQRs the limit set will become too small to be visible, you need to adjust
MAX_REFLECTIONS and tweak with the function chooseColor to get appealling results.
*/

#define inf        -1.

const vec3 PQR = vec3(3, 3, 7);

// --------------------------
// some global settings

#define MAX_TRACE_STEPS  100
#define MIN_TRACE_DIST   0.1
#define MAX_TRACE_DIST   100.0
#define PRECISION        0.0001
#define AA               2
#define MAX_REFLECTIONS  500
#define PI               3.141592653

// another pattern
//#define CHECKER1  vec3(0.196078, 0.33, 0.82)
//#define CHECKER2  vec3(0.75, 0.35, 0.196078)

/*
#define CHECKER1  vec3(0.82, 0.196078, 0.33)
#define CHECKER2  vec3(0.196078, 0.35, 0.92)
#define MATERIAL  vec3(0.71, 0.65, 0.26)
#define FUNDCOL   vec3(0., 0.82, .33)
*/

// Shane's color scheme
#define CHECKER1  vec3(0., 0., 0.05)
#define CHECKER2  vec3(0.2)
#define MATERIAL  vec3(10, 0.3, 0.2)
#define FUNDCOL   vec3(.3, 1, 8)


// used to highlight the limit set
#define LighteningFactor 8.
// --------------------------

vec3 A, B, D;
vec4 C;
float orb;

float dihedral(float x) { return x == inf ? 1. : cos(PI / x); }

// minimal distance to the four mirrors
float distABCD(vec3 p)
{
    float dA = abs(dot(p, A));
    float dB = abs(dot(p, B));
    float dD = abs(dot(p, D));
    float dC = abs(length(p - C.xyz) - C.w);
    return min(dA, min(dB, min(dC, dD)));
}

// try to reflect across a plane with normal n and update the counter
bool try_reflect(inout vec3 p, vec3 n, inout int count)
{
    float k = dot(p, n);
    // if we are already inside, do nothing and return true
    if (k >= 0.0)
    	return true;

    p -= 2.0 * k * n;
    count += 1;
    return false;
}

// similar with above, instead this is a sphere inversion
bool try_reflect(inout vec3 p, vec4 sphere, inout int count)
{
    vec3 cen = sphere.xyz;
    float r = sphere.w;
    vec3 q = p - cen;
    float d2 = dot(q, q);
    if (d2 == 0.0)
    	return true;
    float k = (r * r) / d2;
    if (k < 1.0)
    	return true;
    p = k * q + cen;
    count += 1;
    orb *= k;
    return false;
}

// sdf of the unit sphere at origin
float sdSphere(vec3 p, float radius) { return length(p) - 1.0; }

// sdf of the plane y=-1
float sdPlane(vec3 p, float offset) { return p.y + 1.0; }

// inverse stereo-graphic projection, from a point on plane y=-1 to
// the unit ball centered at the origin
vec3 planeToSphere(vec2 p)
{
    float pp = dot(p, p);
    return vec3(2.0 * p, pp - 1.0).xzy / (1.0 + pp);
}

// iteratively reflect a point on the unit sphere into the fundamental cell
// and update the counter along the way
bool iterateSpherePoint(inout vec3 p, inout int count)
{
    bool inA, inB, inC, inD;
    for(int iter=0; iter<MAX_REFLECTIONS; iter++)
    {
        inA = try_reflect(p, A, count);
        inB = try_reflect(p, B, count);
        inC = try_reflect(p, C, count);
        inD = try_reflect(p, D, count);
        p =  normalize(p);  // avoid floating error accumulation
        if (inA && inB && inC && inD)
            return true;
    }
    return false;
}

// colors for fundamental domain, checker pattern and limit set.
vec3 chooseColor(bool found, int count)
{
    vec3 col;
    if (found)
    {
        if (count == 0) return FUNDCOL;
        else if (count >= 300) col = MATERIAL;
        else
            col = (count % 2 == 0) ? CHECKER1 : CHECKER2;

    }
    else
        col = MATERIAL;

    float t =  float(count) / float(MAX_REFLECTIONS);
    col = mix(MATERIAL*LighteningFactor, col, 1. - t * smoothstep(0., 1., log(orb) / 32.));
    return col;
}

// 2d rotation
vec2 rot2d(vec2 p, float a) { return p * cos(a) + vec2(-p.y, p.x) * sin(a); }

vec2 map(vec3 p)
{
    float d1 = sdSphere(p, 1.0);
    float d2 = sdPlane(p, -1.0);
    float id = (d1 < d2) ? 0.: 1.;
    return vec2(min(d1, d2), id);
}

// standard scene normal
vec3 getNormal(vec3 p)
{
    const vec2 e = vec2(0.001, 0.);
    return normalize(
        vec3(
            map(p + e.xyy).x - map(p  - e.xyy).x,
            map(p + e.yxy).x - map(p  - e.yxy).x,
            map(p + e.yyx).x - map(p  - e.yyx).x
            )
        );
}

// get the signed distance to an object and object id
vec2 raymarch(in vec3 ro, in vec3 rd)
{
    float t = MIN_TRACE_DIST;
    vec2 h;
    for(int i=0; i<MAX_TRACE_STEPS; i++)
    {
        h = map(ro + t * rd);
        if (h.x < PRECISION * t)
            return vec2(t, h.y);

        if (t > MAX_TRACE_DIST)
            break;

        t += h.x;
    }
    return vec2(-1.0);
}

float calcOcclusion(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.75;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}


float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k) {
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < 12; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, k * h / t);
        t += clamp(h, 0.01, 0.2);
        if (h < 0.0001 || t > tmax)
            break;
    }
    return clamp(res, 0.0, 1.0);
}


vec3 getColor(vec3 ro, vec3 rd, vec3 pos, vec3 nor, vec3 lp, vec3 basecol)
{
    vec3 col = vec3(0.0);
    vec3 ld = lp - pos;
    float lDist = max(length(ld), .001);
    ld /= lDist;
    float ao = calcOcclusion(pos, nor);
    float sh = softShadow(pos+0.001*nor, ld, 0.02, lDist, 32.);
    float diff = clamp(dot(nor, ld), 0., 1.);
    float atten = 2. / (1. + lDist * lDist * .01);

    float spec = pow(max( dot( reflect(-ld, nor), -rd ), 0.0 ), 32.);
    float fres = clamp(1.0 + dot(rd, nor), 0.0, 1.0);

    col += basecol * diff;
    col += basecol * vec3(1., 0.8, 0.3) * spec * 4.;
    col += basecol * vec3(0.8) * fres * fres * 2.;
    col *= ao * atten * sh;
    col += basecol * clamp(0.8 + 0.2 * nor.y, 0., 1.) * 0.5;
    return col;
}

mat3 sphMat(float theta, float phi)
{
    float cx = cos(theta);
    float cy = cos(phi);
    float sx = sin(theta);
    float sy = sin(phi);
    return mat3(cy, -sy * -sx, -sy * cx,
                0,   cx,  sx,
                sy,  cy * -sx, cy * cx);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 finalcol = vec3(0.);
    int count = 0;
    vec2 m = vec2(0.0, 1.0) + iMouse.xy / iResolution.xy;
    float rx = m.y * PI;
    float ry = -m.x * 2. * PI;
    mat3 mouRot = sphMat(rx, ry);

// ---------------------------------
// initialize the mirrors

    float P = PQR.x, Q = PQR.y, R = PQR.z;
    float cp = dihedral(P), sp = sqrt(1. - cp*cp);
    float cq = dihedral(Q);
    float cr = dihedral(R);
    A = vec3(0,  0,   1);
    B = vec3(0, sp, -cp);
    D = vec3(1,  0,   0);

    float r = 1.0 / cr;
    float k = r * cq / sp;
    vec3 cen = vec3(1, k, 0);
    C = vec4(cen, r) / sqrt(dot(cen, cen) - r * r);

// -------------------------------------
// view setttings

    vec3 camera = vec3(3., 3.2, -3.);
    vec3 lp = vec3(0.5, 3.0, -0.8); //light position
    camera.xz = rot2d(camera.xz, iTime*0.3);
    vec3 lookat  = vec3(0., -0.5, 0.);
    vec3 up = vec3(0., 1., 0.);
    vec3 forward = normalize(lookat - camera);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));

// -------------------------------------
// antialiasing loop

    for(int ii=0; ii<AA; ii++)
    {
        for(int jj=0; jj<AA; jj++)
        {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
            vec3 rd = normalize(uv.x * right + uv.y * up + 3.0 * forward);
            orb = 1.0;
            // ---------------------------------
            // hit the scene and get distance, object id

            vec2 res = raymarch(camera, rd);
            float t = res.x;
            float id = res.y;
            vec3 pos = camera + t * rd;

            bool found;
            float edist;
            vec3 col;
            // the sphere is hit
            if (id == 0.)
            {
                vec3 nor = pos;
                vec3 q = pos * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);

                col = getColor(camera, rd, pos, nor, lp, basecol);
            }
            // the plane is hit
            else if (id == 1.)
            {
                vec3 nor = vec3(0., 1., 0.);
                vec3 q = planeToSphere(pos.xz);
                q = q * mouRot;
                found = iterateSpherePoint(q, count);
                edist = distABCD(q);
                vec3 basecol = chooseColor(found, count);
                col = getColor(camera, rd, pos, nor, lp, basecol) * .9;
            }
            // draw the arcs
            col = mix(col, vec3(0.), (1.0 - smoothstep(0., 0.005, edist))*0.85);
            col = mix(col, vec3(0.), 1.0 - exp(-0.01*t*t));
            finalcol += col;
        }
    }
    finalcol /= (float(AA) * float(AA));

// ------------------------------------
// a little post-processing

    finalcol = mix(finalcol, 1. - exp(-finalcol), .35);
    fragColor = vec4(sqrt(max(finalcol, 0.0)), 1.0);
}

/*
    A mimic of shane's work at https://www.shadertoy.com/view/WlBczG.

    Show the basic procedure to draw a 2d hyperbolic Poincare tiling.

    Also can draw paracompact and noncompact tilings (with ideal and hyperideal vertices)

*/
// show the three reflection mirrors
//#define debug_mirrors

// comment out this to see the initial pattern
#define enableMouseInversion

// change to Klein model
//#define KleinModel

// set inf = 1.0 for paracompact tiling
// set inf > 1.0 for noncompact tiling
#define inf       1.0

#define PI		  3.14159265
#define TAU	      6.28318531
#define MAX_ITER  30


// the smaller this value, the larger the black area
#define BlackRegionSize   0.065

// the first entry in PQR must be finite, other two entries can be either finite or infinite
// for example (3, inf 4), (3, 4, inf), etc
const vec3 PQR = vec3(3, 3, 7);


// reflection mirrors
vec2 A, B;
vec3 C;

// two vertices of the fundamental triangle, the 3rd one is the origin
vec2 v0, m0;

// count the total number of reflections
float count;

// compute cos(PI / x) using Vinberg's convention for inf.
float dihedral(float x) { return x == inf ? inf : cos(PI / x); }

mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


void init() {

    float cAB = dihedral(PQR.x);
    float sAB = sqrt(1. - cAB*cAB);

	A = vec2(1, 0);
    B = vec2(-cAB, sAB);

    float cAC = dihedral(PQR.y);
    float cBC = dihedral(PQR.z);

    float k1 = cAC;
    float k2 = (cBC + cAB*cAC) / sAB;
    float r = 1. / sqrt(k1*k1 + k2*k2 - 1.);

    C = vec3(k1, k2, 1.)*r;

    if (r*r >= C.x*C.x)
        v0 = vec2(0., C.y - sqrt(r*r - C.x*C.x));
    else
        v0 = vec2(0, 1);

    vec2 n = vec2(-B.y, B.x);
    float b = dot(C.xy, n);
    float c = dot(C.xy, C.xy) - r*r;
    float k = -1.0;

    if (b*b >= c)
        k = b + sqrt(b*b-c);

    m0 = k*n;
}


bool try_reflect(inout vec2 p, vec2 mirror, inout float count) {
    float k = dot(p, mirror);
    if (k >= 0.)
        return true;
    p -= 2. * k  * mirror;
    count += 1.;
    return false;
}


bool try_reflect(inout vec2 p, vec3 sphere, inout float count) {
    vec2 cen = sphere.xy;
    float r = sphere.z;
    float d = length(p - cen) - r;
    if (d >= 0.)
        return true;
    p -= cen;
    p *= r * r/ dot(p, p);
    p += cen;
    count += 1.;
    return false;
}


void fold(inout vec2 p, inout float count) {
    count = 0.;
    for (int k = 0; k < MAX_ITER; k++) {
        try_reflect(p, A, count);
        try_reflect(p, B, count);
        try_reflect(p, C, count);
    }
}


float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}


float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}


vec2 mouseInversion(vec2 p) {
    vec2 m = vec2((2.*iMouse.xy - iResolution.xy)/iResolution.y);
    if(length(m) < 1e-3) m += 1e-3; 
    if(abs(m.x)>.98*.7071 || abs(m.y)>.98*.7071) m *= .98;

    float k = 1./dot(m, m);
    vec2 invCtr = k*m;
    float t = (k - 1.)/dot(p -invCtr, p - invCtr);
    p = t*p + (1. - t)*invCtr;
    p.x = -p.x;
    return p;    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    uv *= 1.05;
    vec2 p = uv;

#ifdef enableMouseInversion
    p = mouseInversion(p);
    p = rot2(iTime/16.)*p;
#endif

    init();   

    if(length(p)> 1.)
        p /= dot(p, p);

#ifdef KleinModel
    p = p / (1. + sqrt(1. - dot(p, p)));
#endif

    fold(p, count);

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, vec2(0), v0, .007));    
    ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, length(p-C.xy) - C.z - 0.007);

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float ssf = (2. - smoothstep(0., .25, abs(length(uv) - 1.) - .25));
    float sf = 2./iResolution.y*ssf;

    vec3 oCol = .55 + .45*cos(count*TAU / 8. + vec3(0, 1, 2));
    float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);

    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));
    col = mix(col, vec3(1, .8, .3), 1. - smoothstep(0., sf, pnt + .02));

    vec3 bg = vec3(1, .2, .4);
    bg *= .7*(mix(col, vec3(1)*dot(col, vec3(.299, .587, .114)), .5)*.5 + .5);
    pat = smoothstep(0., .25, abs(fract((uv.x - uv.y)*43. - .25) - .5)*2. -.5);
    bg *= max(1. - length(uv)*.5, 0.)*(pat*.2 + .9);

    float cir = length(uv);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*10., abs(cir - 1.) - .05))*.7);
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*2., abs(cir - 1.) - .05)));
    col = mix(col, vec3(.9) + bg, (1. - smoothstep(0., sf, abs(cir - 1.) - .03)));
    col = mix(col, col*max(1. - length(uv)*.5, 0.), (1. - smoothstep(0., sf, -cir + 1.05)));
    col = mix(col, bg, (1. - smoothstep(0., sf, -cir + 1.05)));

    col = mix(col, vec3(0), (1. - smoothstep(0., sf, abs(cir - 1.035) - .03))*.8);
    col = mix(col, 1. - exp(-col), .35);

#ifdef debug_mirrors
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, A)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.01, abs(dot(uv, B)) - 0.01));
    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0., 0.02, abs(length(uv-C.xy) - C.z))-0.01);
#endif

    fragColor = vec4(sqrt(max(col, 0.)), 1.);
}

#define LATTICE   1

// Barycentric coordinates of the initial vertex v0 in the fundamental tetrahedron.
// This determines the truncation type of the honeycomb.
const vec4 T = vec4(1, 1, 1, 0);

// use another color style
#define GOLD_STYLE

// initial vertex
vec3 v0;

// M is the four reflection mirrors of the fundamental tetrahedron,
// M_inv is the inverse of M.
mat4 M, M_inv;

// The four vertices of the fundamental tetrahedron and the four mirror images of v0 about
// the four mirrors
mat4x3 V, E;

const float tbsize = 0.055;

// object id
vec3 objIDs;

float glow;

vec3 do_reflect(vec3 p, vec4 n) {
    vec4 q = vec4(p, 1.);
    return p - 2. * dot(q, n) * n.xyz;
}

vec3 rA(vec3 p) { return do_reflect(p, M[0]); }
vec3 rB(vec3 p) { return do_reflect(p, M[1]); }
vec3 rC(vec3 p) { return do_reflect(p, M[2]); }
vec3 rD(vec3 p) { return do_reflect(p, M[3]); }


void init() {

    const float s = 0.70710678;    // sqrt(2) / 2

#if LATTICE == 0
    // affine A3 lattice
    //
    // A     C
    //  o---o
    //  |   |
    //  o---o
    // D     B
    M = mat4(vec4(-s, -s, 0, 2.*s),
             vec4(s, -s, 0, 0),
             vec4(0, s, -s, 0),
             vec4(0, s, s, 0));

    V = mat4x3(vec3(0, 0, 0),
               vec3(2, 0, 0),
               vec3(1, 1, -1),
               vec3(1, 1, 1));

#elif LATTICE == 1
    // affine B3 lattice
    //          C
    //         o
    //     4  /
    // A o---o B
    //        \
    //         o
    //          D
    M = mat4(vec4(0, 0, 1, 0),
             vec4(0, s, -s, 0),
             vec4(s, -s, 0, 0),
             vec4(-s, -s, 0, 2.*s));

    V = mat4x3(vec3(1, 1, 1),
               vec3(1, 1, 0),
               vec3(2, 0, 0),
               vec3(0, 0, 0));

// affine C3 lattice
#else
    //   4       4
    // o---o---o---o
    // A   B   C   D
    M = mat4(vec4(0, 0, 1, 0),
             vec4(0, s, -s, 0),
             vec4(s, -s, 0, 0),
             vec4(-1, 0, 0, 1));

    V = mat4x3(vec3(0, 0, 0),
               vec3(1, 0, 0),
               vec3(1, 1, 0),
               vec3(1, 1, 1));

#endif

    M_inv = inverse(M);
    vec4 v4 = T * M_inv;
    v4 /= v4.w;
    v0 = v4.xyz;
    for (int i = 0; i < 4; i++) {
        E[i] = v0 - 2. * dot(v4, M[i]) * M[i].xyz;
    }
}


#define FAR     80.
#define PI      3.141592654

float sgn(float x) { return x > 0. ? 1. : -1.; }

// Camera path as a function of time t.
// I think it's unlikely to devise an universal path that flies through all
// honeycombs without hitting the lattice.
vec3 camPath(float t) { return vec3(t, 0, 0); }

// A mirror is active if and only if the initial vertex v0 is not on it,
// or equivalently, the barycentric coordinate with respect to this mirror is non-zero.
bool isActive(int k) { return T[k] != 0.0; }

// try to reflect a point p about mirror with normal `n`, if p and the fundamental domain
// are on the different sides of the mirror
vec4 try_reflect(vec4 p, vec4 n, inout int flips) {
    float k = dot(p, n);
    if (k < 0.) {
        flips += 1;
        p.xyz -= 2. * k * n.xyz;
    }
    return p;
}

vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }


// fold a point p into the fundamental domain.
// we return the final position of p, but also track an intermediate state of p for
// handling dual cases.
vec3 fold(inout vec3 p, inout int flips) {

    vec4 q;

#if LATTICE == 0

    p = mod(p + 2., 4.) - 2.;
    q = vec4(p, 1);
    for (int i = 0; i < 4; i++) {
        for (int j = 0; j < 4; j++) {
            q = try_reflect(q, M[j], flips);
        }
    }

#elif LATTICE == 1

    p = mod(p + 2., 4.) - 2.;
    flips += int(p.x < 0.0) + int(p.y < 0.0) + int(p.z < 0.0);
    p = abs(p);
    q = vec4(p, 1);
    for (int i = 0; i < 2; i++) {
        q = try_reflect(q, M[3], flips);
        q = try_reflect(q, M[2], flips);
        q = try_reflect(q, M[1], flips);
    }

#else

    p = mod(p + 1., 2.) - 1.;
    flips += int(p.x < 0.0) + int(p.y < 0.0) + int(p.z < 0.0);
    p = abs(p);
    q = vec4(p, 1.);
    q = try_reflect(q, M[1], flips);
    q = try_reflect(q, M[2], flips);
    q = try_reflect(q, M[1], flips);

#endif

    return q.xyz;
}


// Shane's tex3D function
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n) {
    n = max(abs(n), 0.001);
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}

// I hacked this function to rotate any edge (a, b) to z-axis
mat3 rotAxis(vec3 n) {
    n = normalize(n);
    vec3 x;
    if (n.x == 0.)
        x = vec3(1, 0, 0);
    else
        x = normalize(vec3(-n.y, n.x, 0.));
    vec3 y = cross(n, x);
    return mat3(x, y, n);
}


float tube(vec2 p, float sc, float rad) {
    return max(max(p.x, p.y), (p.x + p.y)*sc) - rad;
}


// draw an edge with two ends at a, b.
vec3 dSegment(vec3 p, vec3 a, vec3 b) {
    // set local origin at middle point of the edge
    vec3 m = (a + b) / 2.;
    p -= m;
    b -= m;
    a -= m;
    vec3 h = (b - a) / 2.;
    float L = length(h); // length of half the edge

    p = p * rotAxis(h);  // reorient the edge along with z-axis
    p = abs(p);

    // the main tube, currently this has infinite length
    float tb;
    tb = tube(p.xy, 0.75, tbsize);

    float band = 1e5;
    float innerTb = 1e5;

    // add a band of 1/4 length of the edge
    band = max(tb - 0.0075, p.z - L/4.);

    // trick: rotate p to make two smaller tubes
    // remove them from the main tube to make the holes
    float psize = tbsize  / 1.5;
    vec2 peg = vec2(tube(p.xz, .64, psize), tube(p.yz, .64, psize));
    float hole = min(peg.x, peg.y);

    // use planes to cut the band
    float dp1 = max(peg.x, p.y - tbsize - .0095); dp1 = max(dp1, p.y - L / 4.);
    float dp2 = max(peg.y, p.x - tbsize - .0095); dp2 = max(dp2, p.x - L / 4.);
    band = min(band, min(dp1, dp2));

    // make holes on the main tube
    tb = max(tb, -(hole - .015));

    // cut the infinite main tube at the two ends
    tb = max(tb, p.z - L);

    // make holes on the band
    band = max(band, -(hole + .0125));

    // use sphere to create a fake inner tube
    innerTb = length(p) - tbsize;

    return vec3(tb, band, innerTb);
}

// This function is only used for handling dual of A3 honeycombs.
// verts[4] are four non-adjacent vertices of a cube, so each face of the cube
/// contains exactly one pair of vertices in verts[4].
// For p a point in the cube, we find this pair of vertices so that p is nearest to
// the face contains this pair among all six faces.
void findVertexPair(vec3 p, vec3 verts[4], out int i1, out int j1) {
    float d = 1e5;
    float d1;
    for (int i = 0; i < 4; i++) {
        for (int j = i+1; j < 4; j++) {
            // (verts[i] + verts[j]) / 2. is the center of the face since they form a diagonal pair.
            d1 = length(p - (verts[i] + verts[j]) / 2.);
            if (d1 < d) {
                i1 = i;
                j1 = j;
                d = d1;
            }
        }
    }
}


float map(vec3 p) {
    vec3 dedge = vec3(1e5);
    vec3 p0 = p;
    int flips = 0;
    p = fold(p0, flips);

// For the usual uniform honeycomb just render the edges, that's safe since they lie inside
// the fundamental tetrahedron.
#ifndef DUAL

    for (int i = 0; i < 4; i++) {
         dedge = min(dedge, dSegment(p, v0, E[i]));
    }

#else
    /*
    Render the dual honeycomb.

    In the dual honeycomb two cells are adjacent if and only if they share a common face.

    For two mirrors A, B they generate a non-degenerate face if and only if either
    of below holds:

        1. A, B are perpendicular and they are both active
        2. A, B are not perpendicular and at least one of them is active

    For such a face we further check if {A, B, C} and {A, B, D} both generate non-degenerate cells.

    1. If {A, B, C} and {A, B, D} are both non-degenerate, (C, D) form an edge in the dual honeycomb.
    2. If {A, B, D} degenerates to a face, (D, 2*C-D) form an edge in the dual honeycomb.
    3. If both {A, B, C} and {A, B, D} are degenerate, no edge between C and D.
    */

    // The C3 honeycomb is the easiest, handle this case first.
    //   4       4
    // o---o---o---o
    // A   B   C   D
    #if LATTICE == 2
        // if either A or B is active, then {A, B} generate a face and {A, B, C} is a
        // non-degenerate cell. {A, B, D} is non-degenerate iff D is active.
        // Other procedures below are all similar.
        if (isActive(0) || isActive(1)) {
            if (isActive(3))
                dedge = min(dedge, dSegment(p, V[2], V[3]));
            else
                dedge = min(dedge, dSegment(p, V[3], 2.*V[2]-V[3]));
        }
        if (isActive(0) && isActive(2)) dedge = min(dedge, dSegment(p, V[1], V[3]));
        if (isActive(1) || isActive(2)) dedge = min(dedge, dSegment(p0, V[0], V[3]));
        if (isActive(0) && isActive(3)) dedge = min(dedge, dSegment(p, V[1], V[2]));
        if (isActive(1) && isActive(3)) dedge = min(dedge, dSegment(p, V[0], V[2]));
        if (isActive(2) || isActive(3)) {
            if (isActive(0))
                dedge = min(dedge, dSegment(p, V[1], V[0]));
            else
                dedge = min(dedge, dSegment(p, V[0], 2.*V[1]-V[0]));
        }

    #elif LATTICE == 1

        if (isActive(0) || isActive(1)) dedge = min(dedge, dSegment(p, V[2], V[3]));
        if (isActive(0) && isActive(2)) {
            if (isActive(3))
                dedge = min(dedge, dSegment(p, V[1], V[3]));
            else
                dedge = min(dedge, dSegment(p, V[3], 2.*V[1]-V[3]));
        }
        // trick: the edge (V[0], V[3]) does not lie inside the fundamental domain hence octahedral
        // shapes won't work. we render its 4 copies in the cube [0, 0, 0] x [2, 2, 2].
        if (isActive(1) || isActive(2)) {
            dedge = min(dedge, dSegment(p0, V[0], V[3]));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 2, 0)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 0, 2)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 2, 2)));
        }

        if (isActive(0) && isActive(3)) {
            if (isActive(2))
                dedge = min(dedge, dSegment(p, V[1], V[2]));
            else
                dedge = min(dedge, dSegment(p, V[2], 2.*V[1]-V[2]));
        }
        if (isActive(1) || isActive(3)) {
            dedge = min(dedge, dSegment(p0, V[0], V[2]));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 2, 0)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 0, 2)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 2, 2)));
        }
        if (isActive(2) && isActive(3)) {
            if (isActive(1))
                dedge = min(dedge, dSegment(p, V[1], V[0]));
            else
                dedge = min(dedge, dSegment(p, V[0], 2.*V[1]-V[0]));
        } 

    #else
        // The duals of A3 honeycombs are the most difficult. Recall p0 is now in [-2, -2, -2] x [2, 2, 2].
        // We use a completely different way to render them. The idea is, vertices of the A3 regular honeycombs
        // are integer lattices points (i, j, k) with:
        // i + j + k = 0 mod 4 for V0
        // i + j + k = 2 mod 4 for V1
        // i + j + k = 1 mod 4 for V2
        // i + j + k = 3 mod 4 for V3

        // find the center of the 2x2x2 cube that p belongs to
        vec3 C = vec3(sgn(p0.x), sgn(p0.y), sgn(p0.z));

        float k = mod(C.x + C.y + C.z, 4.);
        // if k = 1 mod 4, then the center of this cube is congruent to V[2] of the fundamental tetrahedron.
        // else k = 3 mod 4 and the center is congruent to V[3].
        int type = (k == 1.) ? 2 : 3;

        vec3 V0[4];  // holds the vertices of this 2x2x2 cube that are congruent to V[0]
        vec3 V1[4];  // holds the vertices of this 2x2x2 cube that are congruent to V[1]

        if (type == 2) {
            V0[0] = C + vec3(1, 1, 1);
            V0[1] = C + vec3(-1, -1, 1);
            V0[2] = C + vec3(1, -1, -1);
            V0[3] = C + vec3(-1, 1, -1);
            V1[0] = C + vec3(1, -1, 1);
            V1[1] = C + vec3(-1, 1, 1);
            V1[2] = C + vec3(1, 1, -1);
            V1[3] = C + vec3(-1, -1, -1);
        }
        else {  // switch the role of V0, V1 above
            V1[0] = C + vec3(1, 1, 1);
            V1[1] = C + vec3(-1, -1, 1);
            V1[2] = C + vec3(1, -1, -1);
            V1[3] = C + vec3(-1, 1, -1);
            V0[0] = C + vec3(1, -1, 1);
            V0[1] = C + vec3(-1, 1, 1);
            V0[2] = C + vec3(1, 1, -1);
            V0[3] = C + vec3(-1, -1, -1);
        }

        int r0, s0, r1, s1;
        findVertexPair(p0, V0, r0, s0);
        findVertexPair(p0, V1, r1, s1);
        vec3 P0 = V0[r0] + V0[s0] - C;
        vec3 P1 = V1[r1] + V1[s1] - C;

        if (isActive(2) && isActive(3)) dedge = min(dedge, dSegment(p, V[0], V[1]));
        if (isActive(1) || isActive(3)) {
            if (type == 2) {
                dedge = min(dedge, dSegment(p0, C, V0[r0]));
                dedge = min(dedge, dSegment(p0, C, V0[s0]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P0, V0[r0]));
                dedge = min(dedge, dSegment(p0, P0, V0[s0]));
            }
        }
        if (isActive(1) || isActive(2)) {
            if (type == 3) {
                dedge = min(dedge, dSegment(p0, C, V0[r0]));
                dedge = min(dedge, dSegment(p0, C, V0[s0]));            
            }
            else {
                dedge = min(dedge, dSegment(p0, P0, V0[r0]));
                dedge = min(dedge, dSegment(p0, P0, V0[s0]));
            }
        }
        if (isActive(0) || isActive(3)) {
            if (type == 2) {
                dedge = min(dedge, dSegment(p0, C, V1[r1]));
                dedge = min(dedge, dSegment(p0, C, V1[s1]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P1, V1[r1]));
                dedge = min(dedge, dSegment(p0, P1, V1[s1]));
            }
        }
        if (isActive(0) || isActive(2)) {
            if (type == 3) {
                dedge = min(dedge, dSegment(p0, C, V1[r1]));
                dedge = min(dedge, dSegment(p0, C, V1[s1]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P1, V1[r1]));
                dedge = min(dedge, dSegment(p0, P1, V1[s1]));
            }
        }
        if (isActive(0) && isActive(1)) dedge = min(dedge, dSegment(p, V[2], V[3]));

    #endif
#endif

    // store the object ids.
    objIDs = dedge;
    return min(dedge.x, min(dedge.y, dedge.z));
}


// This reduces the compiling time to about 7x times faster
vec3 calcNormal(vec3 p, inout float edge, float t) {
    float eps = 1./mix(400., iResolution.y, .5)*(1. + t*.5);
    float d = map(p);
    vec3 e = vec3(eps, 0, 0);
    vec3 da = vec3(-2.*d);
    for(int i = min(iFrame, 0); i < 3; i++ ) {
        for(int j = min(iFrame, 0); j < 2; j++)
            da[i] += map(p + e * float(1 - 2*j));
        e = e.zxy;
    }
    da = abs(da);

    edge = da.x + da.y + da.z;
    edge = smoothstep(0., 1., sqrt(edge/e.x*2.));

    vec3 n = vec3(0.0);
    for(int i=min(iFrame, 0); i<4; i++) {
        vec3 e = .57735*(2.*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1)) - 1.);
        n += e*map(p + .001*e);
    }
    return normalize(n);
}


float softShadow(vec3 ro, vec3 lp, float tmin, float tmax, float k) {
    const int maxShadeIterations = 32;
    vec3 rd = lp - ro;
    rd /= max(length(rd), 1e-4);
    float shade = 1.0;
    float t = tmin;
    for (int i = 0; i < maxShadeIterations; i++) {
        float h = map(ro + rd * t);
        t += clamp(h, 0.01, 0.2);
        shade = min(shade, smoothstep(0., 1., k * h / t));
        if (abs(h) < 1e-4 || t > tmax)
            break;
    }
    return min(max(shade, 0.) + .1, 1.);
}

float calcAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n);
        occ += (h - d) * sca;
        sca *= 0.7;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}


float trace(in vec3 ro, in vec3 rd) {
    glow = 0.;
    float ah;
    const float precis = 1e-3;
    float t = 0.0;
    for (int i = 0; i < 128; i++) {
        float h = map(ro + rd * t);
        ah = abs(h);
        glow += 1./(1. + ah*ah*8.);
        if (ah < (t * 0.125 + 1.) * precis || t > FAR)
            break;
        t += h;
    }
    return min(t, FAR);
}


vec3 transform(in vec3 p) {
    if (iMouse.x > 0.0) {
        float phi =  2.0*iMouse.x / iResolution.x * PI - PI;
        float theta = iMouse.y / iResolution.y * PI - 0.5*PI;
        p.xz = rot2d(p.xz, phi / 6.);
        p.yx = rot2d(p.yx, theta / 4.);

    }
    return p;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    init();

    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 up = vec3(0, 1, 0);
    vec3 ro = camPath(iTime * 1.1);
    vec3 lookat = camPath(iTime * 1.1 + 0.1);

    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, up));
    up = cross(right, forward);
    vec3 rd = normalize(uv.x * right + uv.y * up + forward * 2.);
    ro = transform(ro);
    rd = transform(rd);
    vec3 lp = ro + vec3(-1, 2, -1);
    vec3 col = vec3(0);

    float t = trace(ro, rd);

    float objID = (objIDs.x < objIDs.y && objIDs.x < objIDs.z) ? 0. : (objIDs.y < objIDs.z) ? 1. : 2.;

    if (t < FAR) {
        float ed;
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos, ed, t);

        vec3 oCol;
        #ifndef GOLD_STYLE
        vec3 bCol = mix(vec3(1, .1, .5).zyx,
                        vec3(1, .3, .1).zyx,
                        dot(sin(pos*8. - cos(pos.yzx*4. + iTime*4.)), vec3(.166)) + .5);
        #else
        vec3 bCol = mix(vec3(1, .5, .1),
                        vec3(1, .1, .2),
                        dot(sin(pos*8. - cos(pos.yzx*4. + iTime*4.)), vec3(.166)) + .5);
        #endif
        if(objID < .5)
            oCol = mix(bCol, vec3(1), .97);

        else if (objID > 1.5)
            oCol = mix(bCol, vec3(1), .05) + bCol*2.;

        else
            oCol = mix(bCol, vec3(1.35), .97)*vec3(1.1, 1, .9);

        vec3 tx = tex3D(iChannel0, pos*2., nor);
        tx = smoothstep(.0, .5, tx)*2.;

        if(objID < 1.5)
            oCol *= tx;
        else
            oCol *= mix(vec3(1), tx, .5);

        float ao = calcAO(pos, nor);
        float sh = softShadow(pos + nor*.002, lp, 0.001, 16., t);

        vec3 ld = lp - pos;
        float dist = max(length(ld), 0.001);
        ld /= dist;

        float atten = 3./(1. + dist*0.05 + dist*dist*0.025);

        float diff = max(dot(ld, nor), 0.);
        if (objID < 1.5)
            diff = pow(diff, 4.)*2.;
        float spec = pow(max(dot( reflect(ld, nor), rd), 0.0 ), 32.0);

        col = oCol*(diff + .25) + mix(bCol.zyx, vec3(1, .7, .3), .5)*spec*4.;

        col += .015/max(abs(.05 - map(pos*1.5 + sin(iTime/6.))), .01)*oCol*mix(bCol, vec3(1, .8, .5), .35);

        // Adding a bit of glow. It was tempting to get my money's worth, but I kept it subtle. :)
        if(objID < 1.5)
            col += bCol*glow*.02;
        else
            col += bCol*glow*1.5;

        // Applying the dark edges, attenuation, shadows and ambient occlusion.
        col *= (1. - ed*.7);
        col *= atten*(sh + ao*.25)*ao;

    }

    float fog = 1./(1. + t*.125 + t*t*.01);
    col = mix(vec3(0), col, fog);//

    uv = fragCoord/iResolution.xy;
    col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .125);
	fragColor = vec4(sqrt(max(col, 0.)), 1);
}

const float sq5 = sqrt(5.0);
const float sq52 = 2.0 / sqrt(5.0);
const float pi = 3.141592654;

const vec3 icosa[6] = vec3[6] (
    vec3(1, 0, 0.5) * sq52,
    vec3(cos(pi / 5.), sin(pi/5.), 0.5) * sq52,
    vec3(cos(2.*pi / 5.), sin(2.*pi/5.), 0.5) * sq52,
    vec3(cos(3.*pi / 5.), sin(3.*pi/5.), 0.5) * sq52,
    vec3(cos(4.*pi / 5.), sin(4.*pi/5.), 0.5) * sq52,
    vec3(0,0,1)
);

const float offsets[6] = float[6] (1./6., 1./6., 1./6., 1./6., 1./6., 1./6.);

#define PI        3.141592654

mat2 rot2d(float a) {
    float ca = cos(a), sa = sin(a);
    return mat2(ca, -sa, sa, ca);
}

vec2 cmul(vec2 z, vec2 w) {
    return vec2(
        z.x * w.x - z.y * w.y,
        z.x * w.y + z.y * w.x
    );
}

vec2 cdiv(vec2 z, vec2 w) {
    return vec2(
        z.x * w.x + z.y * w.y,
        -z.x * w.y + z.y * w.x
    ) / dot(w, w);
}

vec2 cconj(vec2 z) {
    return vec2(z.x, -z.y);
}

struct Mobius {
    vec2 A, B, C, D;
};

float hypot (vec2 z) {
    float x = abs(z.x);
    float y = abs(z.y);
    float t = min(x, y);
    x = max(x, y);
    t = t / x;
    return x * sqrt(1.0 + t * t);
}


float e2h(float r) {
    return 2. * atanh(r);
}

float h2e(float r) {
    return tanh(0.5 * r);
}

Mobius mobInverse(Mobius m) {
    return Mobius(
        m.D, -m.B, -m.C, m.A
    );
}

vec2 applyMobius(Mobius m, vec2 z) {
    return cdiv(cmul(m.A, z) + m.B, cmul(m.C, z) + m.D);
}

Mobius translation(vec2 p) {
    return Mobius(
        vec2(1, 0), -p, -cconj(p), vec2(1, 0)
    );
}

Mobius translation2(vec2 p) {
    return Mobius(
        vec2(1, 0), p, cconj(p), vec2(1, 0)
    );
}

Mobius rotation(float a) {
    return Mobius(
        vec2(cos(a), sin(a)), vec2(0), vec2(0), vec2(1, 0)
    );
}

Mobius composite(Mobius m1, Mobius m2) {
    vec2 A = cmul(m1.A, m2.A) + cmul(m1.B, m2.C);
    vec2 B = cmul(m1.A, m2.B) + cmul(m1.B, m2.D);
    vec2 C = cmul(m1.C, m2.A) + cmul(m1.D, m2.C);
    vec2 D = cmul(m1.C, m2.B) + cmul(m1.D, m2.D);
    return Mobius(A, B, C, D);
}

Mobius composite(Mobius m1, Mobius m2, Mobius m3) {
    return composite(m1, composite(m2, m3));
}

Mobius rotation(vec2 p, float a) {
    Mobius m1 = translation(-p);
    Mobius m2 = rotation(a);
    Mobius m3 = translation(p);
    return composite(m3, m2, m1);
}


vec2 rectGrid(vec2 z, vec2 size) {
    return mod(z + 0.5 * size, size) - 0.5 * size;
}

vec2 polarGrid(vec2 z, vec2 size) {
    float theta = atan(z.y, z.x);
    float r = e2h(hypot(z));
    return rectGrid(vec2(r, theta), size);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 c = vec2(0.2, 0.4);
    Mobius m = translation(c);
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv *= 1.02;
    vec2 p = uv;
    vec3 col = vec3(1);

    if (length(p) < 1.) {

        p = applyMobius(m, p);
        p = rot2d(iTime / 3.) * p;
        vec2 polar = polarGrid(p, vec2(.5, PI/6.));
        float d1 = abs(polar.x);
        float d2 = abs(polar.y);


        float K = hypot(vec2(dFdx(e2h(length(p))), dFdy(e2h(length(p)))));
        d1 /= K * iResolution.y / 2.;



        float L = hypot(vec2(dFdx(atan(p.y, p.x)), dFdy(atan(p.y, p.x))));
        d2 /= L * iResolution.y / 2.;

        float d = hypot(vec2(d1, d2));
        //d /= (1. - pow(dot(p, p), 10.));

        float sf = 2. / iResolution.y;
        col = mix(col, vec3(0), 1. - smoothstep(0.00, sf, d1 - sf));
        col = mix(col, vec3(0), 1. - smoothstep(0.00, sf, d2 - sf));

        col = mix(col, vec3(0), 1. - smoothstep(0.00, sf, d - 7.*sf));
    }

    vec2 q = vec2(0.1, 0);

    q = applyMobius(m, q);

    col = mix(col, vec3(1, 0, 0), 1. - smoothstep(0.00, 0.01, abs(length(p-q)) - 0.03));
    col = mix(col, vec3(0), 1. - smoothstep(0.00, 0.01, abs(length(p) - 1.0)));

    fragColor = vec4(sqrt(max(col, 0.)), 1.0);
}

#define L2(x)           dot(x, x)
#define MAX_ITER        50


bool doHalfPlane = true;

const vec2[] PARAMS = vec2[] (
    vec2(1.8462756, 0.09627581),
    vec2(1.958591, 0.011278),
    vec2(1.857382, 0.076258),
    vec2(1.64213876, 0.76658841),
    vec2(1.658312, 0.5),
    vec2(1.926434053, 0.027381792),
    vec2(2, 0)
);

const int NUM_PARAMS = PARAMS.length();

float wrap(in float x, in float a, in float s) {
    return mod(x - s, a) + s;
}

vec2 transA(in vec2 z, float a, float b, inout float scale) {
    float k = 1. / dot(z, z);
    z *= k;
    scale *= k;
    z.x -= b;
    z.y = a - z.y;
    return z;
}

bool separation(in vec2 z, in float a, in float b) {
    float f = (z.x >= -b/2.0) ? 1.0 : -1.0;
    float K = sign(b) * (2.0*a - 1.95) / 4.3;
    float M = 7.2 - (1.95 - a) * 18.0;
    return z.y >= 0.5*a + K*f*(1.0 - exp(-M*abs(z.x + b * 0.5)));
}

float kleinian(in vec2 z, vec2 pattern, float scale) {
    float a = pattern.x, b = pattern.y;
    float f = sign(b);
    vec2 lz = z + vec2(1), llz = z - vec2(1);
    for (int i = 0; i < MAX_ITER; i++) {
        z.x = z.x + f * b / a * z.y;
        z.x = wrap(z.x, 2.0, -1.0);
        z.x = z.x - f * b / a * z.y;

        if (separation(z, a, b)) {
            z = vec2(-b, a) - z;
        }

        z = transA(z, a, b, scale);

        // If the iterated points enters a 2-cycle, bail out.
        if (dot(z-llz, z-llz) < 1e-6) {
            return abs(z.y)  / scale;
        }

       // If the iterated point gets outside z.y=0 and z.y=a, bail out.
        if (z.y < 0.0)
            return -z.y/scale;
        if (z.y > a)
            return (z.y - a)/scale;

        llz=lz; lz=z;
    }
    return 1e3;
}


vec3 hsv2rgb(vec3 c) {
    const vec4 K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 postprocess(vec3 col, vec2 q) {
    col = pow(clamp(col, 0.0, 1.0), vec3(1.0/2.2)); 
    col = col*0.6 + 0.4*col*col*(3.0 - 2.0*col);
    col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
    col *= 0.5 + 0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.7);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    const float zoom = 3.;

    float time = mod(0.2*iTime, float(NUM_PARAMS));
    int it = int(floor(time));
    float ft = fract(time);
    vec2 pattern = mix(PARAMS[it], PARAMS[(it+1) % NUM_PARAMS], smoothstep(0., 1., ft));
    float scale = 1.2;
    vec2 p = uv * 1.1;

    if (doHalfPlane) {
        p.y += 1.0;
        p *= 0.5*pattern.x;
    }
    else {
        p -= vec2(0, -1);
        float r2 = 2. / dot(p, p);
        p *= r2;
        scale *= r2;
        p += vec2(0, -1);
    }
    float aa = 2. / iResolution.y;

    float d = zoom * kleinian(p, pattern, scale);

    float b = -0.125;
    float t = 20.0;
    const float lh = 1.25;
    const vec3 lp1 = vec3(.5, .5, lh);
    const vec3 lp2 = vec3(-0.5, .5, lh);

    vec3 ro = vec3(0, 0, t);
    vec3 pp = vec3(p, 0);

    vec3 rd = normalize(pp - ro);

    vec3 ld1 = normalize(lp1 - pp);
    vec3 ld2 = normalize(lp2 - pp);

    float bt = -(t-b)/rd.z;

    vec3  bp   = ro + bt*rd;
    vec3  srd1 = normalize(lp1 - bp);
    vec3  srd2 = normalize(lp2 - bp);
    float bl21 = L2(lp1 - bp);
    float bl22 = L2(lp2 - bp);

    float st1 = (0.0-b)/srd1.z;
    float st2 = (0.0-b)/srd2.z;
    vec3  sp1 = bp + srd1*st1;
    vec3  sp2 = bp + srd2*st1;

    float bd = zoom * kleinian(bp.xy, pattern, scale);
    float sd1= zoom * kleinian(sp1.xy, pattern, scale);
    float sd2= zoom * kleinian(sp2.xy, pattern, scale);

    vec3 col = vec3(0.0);
    const float ss = 15.0;

    col       += vec3(1)  * (1.0 - exp(-ss*(max(sd1, 0.0)))) / bl21;
    col       += vec3(.5) * (1.0 - exp(-ss*(max(sd2, 0.0)))) / bl22;
    float l   = length(p);
    float hue = fract(0.75*l-0.3*iTime) + .5;
    float sat = tanh(4.*l);
    vec3 hsv  = vec3(hue, sat, 1.0);
    vec3 bcol = hsv2rgb(hsv);
    col       *= (1.0-tanh(0.25*l))*0.5;
    col       = mix(col, bcol, smoothstep(-aa, aa, -d));  
    col       += 0.5*sqrt(bcol.zxy)*(exp(-(10.0+100.0*tanh(l))*max(d, 0.0)));

    col = postprocess(col, fragCoord/iResolution.xy);
    fragColor = vec4(col,1.0);
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2 // Make it 3 if you have a fast machine
#endif

float map(vec3 p, inout int index) {
    float scale = 3.;
    orb = vec4(1000.0); 
    for (int i = 0; i < MAX_ITER; i++) {
        bool cond = true;
        for (int k = 0; k < 5; k++) {
            cond = try_reflect(p, coclusters[k], scale) && cond;
            orb = min( orb, vec4(abs(p), dot(p, p)));
        }

        if (cond)
            break;
    }
    index = 0;

    float d = p.z;
    for (int j = 1; j < 5; j++) {
        if (clusters[j].isRealBall) {
            float d1 = sdistanceToBall(p, clusters[j]);
            if (d1 < d) { d = d1; index = j; }
        }
    }
    return d / scale;
}



float trace( in vec3 ro, in vec3 rd, inout int index )
{
	float maxd = 30.0;
    float t = 0.01;
    for( int i=0; i<512; i++ )
    {
	    float precis = 0.001 * t;

	    float h = map( ro+rd*t, index );
        if( h<precis||t>maxd ) break;
        t += h;
    }

    if( t>maxd ) t=-1.0;
    return t;
}

vec3 calcNormal( in vec3 pos, in float t )
{
    float precis = 0.001 * t;
    int index;
    vec2 e = vec2(1.0,-1.0)*precis;
    return normalize( e.xyy*map( pos + e.xyy, index) + 
					  e.yyx*map( pos + e.yyx, index ) + 
					  e.yxy*map( pos + e.yxy, index ) + 
                      e.xxx*map( pos + e.xxx, index ) );
}

vec3 render( in vec3 ro, in vec3 rd, in float anim )
{
    // trace	
    vec3 col = vec3(0.0);
    int index = 0;
    float t = trace( ro, rd, index);
    if( t>0.0 )
    {
        vec4 tra = orb;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, t );

        // lighting
        vec3  light1 = vec3(  0.577, 0.577, 0.577 );
        vec3  light2 = vec3( -0.707, 0.000,  0.707 );
        float key = clamp( dot( light1, nor ), 0.0, 1.0 );
        float bac = clamp( 0.2 + 0.8*dot( light2, nor ), 0.0, 1.0 );
        float amb = (0.7+0.3*nor.z);
        float ao = pow( clamp(tra.w*2.0,0.0,1.0), 1.2 );

        vec3 brdf  = 1.0*vec3(0.40,0.40,0.40)*amb*ao;
        brdf += 1.0*vec3(1.00,1.00,1.00)*key*ao;
        brdf += 1.0*vec3(0.40,0.40,0.40)*bac*ao;

        // material		
        vec3 rgb = vec3(1.0);
        rgb = mix( rgb, vec3(1.0,0.80,0.2), clamp(6.0*tra.z,0.0,1.0) );
        rgb = mix( rgb, vec3(1.0,0.55,0.0), pow(clamp(1.0-2.0*tra.z,0.0,1.0),8.0) );

        // color
        col = rgb*brdf*exp(-0.2*t);
    }

    return sqrt(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    init(333, vec3(3, 3, 2), vec3(3, 3, 2), inf, bvec4(1, 0, 0, 0));
    float time = iTime*0.05 + 0.01*iMouse.x;
    float anim = 1.1 + 0.5*smoothstep( -0.3, 0.3, cos(0.1*iTime) );

    vec3 tot = vec3(0.0);
    #if AA>1
    for( int jj=0; jj<AA; jj++ )
    for( int ii=0; ii<AA; ii++ )
    #else
    int ii = 1, jj = 1;
    #endif
    {
        vec2 q = fragCoord.xy+vec2(float(ii),float(jj))/float(AA);
        vec2 p = (2.0*q-iResolution.xy)/iResolution.y;

        // camera
        vec3 ro = vec3( 2.8*cos(0.1+.33*time), 0.4 + 0.30*cos(0.37*time), 1.2+1.2*cos(0.5+0.35*time) );
        vec3 ta = vec3( 1.9*cos(1.2+.41*time), 0.4 + 0.10*cos(0.27*time), 1.5 + 1.*cos(2.0+0.38*time) );
        float roll = 0.2*cos(0.1*time);
        vec3 cw = normalize(ta-ro);
        vec3 cp = vec3(0, 0, 1);
        vec3 cu = normalize(cross(cw,cp));
        vec3 cv = normalize(cross(cu,cw));
        vec3 rd = normalize( p.x*cu + p.y*cv + 2.0*cw );

        tot += render( ro, rd, anim );
    }

    tot = tot/float(AA*AA);

	fragColor = vec4( tot, 1.0 );	

}

#define MAX_ITER         30
#define PI               3.141592653
#define L2(x)            dot(x, x)
#define ZOOM             3.
#define inf              -1.05

vec4 orb; 

// uncomment this to show the vertice sections
//#define DrawVerticalSections

float dihedral(float x) {
    return x == inf ? 1. : cos(PI / x);
}

vec3 dihedral(vec3 v) {
    return vec3(dihedral(v.x), dihedral(v.y), dihedral(v.z));
}


// For spheres n is the center, r is the radius
// For planes n is the normal vector, r is the distance between the plane and the origin
// if invert is true then the inside/outside of the sphere is interchanged (not used in this program)
// if hasRealBall is true then this virtual ball has a real ball correspondes to it
struct Ball {
    bool isplane;
    vec3 n;
    float r;
    bool invert;
    bool isRealBall;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[5] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[5] clusters;


// create a ball. by default it's the z=0 plane with normal points to the negative half
Ball defaultBall() {
    return Ball(false, vec3(0, 0, -1), 0., false, false);
}

// Distance from a point to a ball
float sdistanceToBall(vec3 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        return k;
    }
    else
        return length(p - B.n) - B.r;
}

Ball from_plane(vec3 n, float d) {
    return Ball(true, n, d, false, false);
}

Ball from_sphere(vec3 cen, float r) {
    return Ball(false, cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}

// try to reflect a point p to the positive half space bounded by a ball
// if we are already in the positive half space, do nothing and return true,
// else reflect about the ball and return false
// if B is a sphere we try to reflect p into the interior of B
bool try_reflect(inout vec3 p, Ball B, inout float scale) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * B.n;
        return false;
    }
    else {
        vec3 cen = B.n;
        float r = B.r;
        vec3 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1.0 && B.invert) || (k > 1. && !B.invert) )
            return true;
        p = k * q + cen;
        scale *= k;
        return false;
    }
}

// return distance to the scene, and get the index of the real ball hitted


// solve M*v = b, where v = (x, y, r) is the xy-coordinate of the center of the ball,
// r is the radius of the ball.
Ball solveBall(mat3 M, vec3 b) {
    vec3 p = b * inverse(M);
    return from_sphere(vec3(p.xy, 0.), p.z);
}

// Solve a ball with its center has xy-coordinates P, and is orthogonal to other two Balls B0, B1.
// Here B0 may be either a sphere or a plane, B1 is always a sphere. 
Ball solveBall(vec2 P, Ball B0, Ball B1) {
    if (B0.isplane) {
        float z = B0.r;
        vec3 cen = vec3(P, z);
        float R = sqrt(L2(cen - B1.n) - B1.r*B1.r);
        return from_sphere(cen, R);
    }
    else {
        float r1 = B1.r;
        float r0 = B0.r;
        float z0 = B0.n.z;
        float k0 = L2(P - B0.n.xy);
        float k1 = L2(P - B1.n.xy);
        float z = (r1*r1 - r0*r0 + z0*z0 + k0 - k1) / (2.*z0);
        float R = sqrt(k1 + z*z - r1*r1);
        return from_sphere(vec3(P, z), R);
    }

}

// triangleType: must be one of 236, 244 or 333
// dihedralAngles0: labels of the edges between v0 and (v2, v3, v4)
// dihedralAngles1: labels of the edges between v1 and (v2, v3, v4)
// dihedralAngles0_1: label of the edge between (v0, v1)
// isRealBall: specify which of (v1, v2, v3, v4) has real balls
void init(int triangleType,
          vec3 dihedralAngles0,
          vec3 dihedralAngles1,
          float dihedralAngle0_1,
          bvec4 isRealBall) {
    const float s2 = sqrt(2.);
    const float s3 = sqrt(3.);
    mat3 M0, M1;
    vec3 b;
    Ball B0, B1, B2, B3, B4;
    vec3 t0 = dihedral(dihedralAngles0);
    vec3 t1 = dihedral(dihedralAngles1);
    float t01 = dihedral(dihedralAngle0_1);
    // A, B, C are the vertices of the triangle formed by mirror plane v2, v3, v4 and z=0 plane
    vec2 A, B, C;

    // the 236 case
    if (triangleType == 236) {
        A = vec2(0, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s3/2., -0.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(s3/2., 0.5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(s3/2., 0.5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s3/2., 0);
    }

    // the 244 case
    else if (triangleType == 244) {
        A = vec2(0, 0), B = vec2(0, 1), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s2/2., -s2/2., 0), s2/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(1./s2, 1./s2, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(1./s2, 1./s2, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s2/2., 0);
    }

    // the 333 case
    else {
        A = vec2(-1, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(s3/2., -.5, 0), s3/2.);
        B3 = from_plane(vec3(-s3/2., -.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(-s3/2., 0.5, t1.x), vec3(s3/2., .5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(-s3/2., 0.5, t0.x), vec3(s3/2., .5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(s3, s3, 0)/2.;
    }

    // now we solve the virtual ball B1, this can't be a plane
    B1 = solveBall(M1, b);
    invertBall(B1);

    // now we solve the virtual ball B0, this can be either a plane or a sphere
    // this depends on if all entries in dihedralAngles0 are all 2
    if (dot(dihedralAngles0, vec3(1)) == 6.) {
        B0 = from_plane(vec3(0, 0, -1), B1.r*t01);
    }
    else {
        B0 = solveBall(M0, b);
        float r1 = B1.r, r0 = B0.r;
        B0.n.z = sqrt(r0*r0 + r1*r1 + 2.*r0*r1*t01 - L2(B1.n.xy - B0.n.xy));
        invertBall(B0);
    }
    coclusters = Ball[5] (B0, B1, B2, B3, B4);

    //now we process the real balls
    for (int k = 0; k < 5; k++)
        clusters[k] = defaultBall();

    if (isRealBall.x) {
        clusters[1] = from_plane(vec3(0, 0, -1.), B0.n.z);
        clusters[1].isRealBall = true;
    }
    if (isRealBall.y) {
        clusters[2] = solveBall(C, B0, B1);
        clusters[2].isRealBall = true;
    }
    if (isRealBall.z) {
        clusters[3] = solveBall(A, B0, B1);
        clusters[3].isRealBall = true;
    }

    if (isRealBall.w) {
        clusters[4] = solveBall(B, B0, B1);
        clusters[4].isRealBall = true;
    }
}

/* Marden's theorem on Steiner inellipse, Zhao Liang.

Motivated by this tweet:

    https://twitter.com/theAlbertChern/status/1395468792788967428?s=20
*/
#define PI 3.141592653

float sl;

const float palNum = 70.;


float sFract(float x, float sm)
{
    // Extra smoothing factor. "1" is the norm.
    const float sf = 1.;

    // The hardware "fwidth" is cheap, but you could take the expensive route and
    // calculate it by hand if more quality was required.
    vec2 u = vec2(x, fwidth(x)*sf*sm);

    // Ollj's original formula with a transcendental term omitted.
    u.x = fract(u.x);
    u += (1. - 2.*u)*step(u.y, u.x);
    return clamp(1. - u.x/u.y, 0., 1.); // Cos term ommitted.
}

// Only correct for nonnegative values, but in this example, numbers aren't negative.
float sFloor(float x)
{
    return x - sFract(x, 1.);
}

float sdCross( in vec2 p, in vec2 b, float r )
{
    p = abs(p); p = (p.y>p.x) ? p.yx : p.xy;

	vec2  q = p - b;
    float k = max(q.y,q.x);
    vec2  w = (k>0.0) ? q : vec2(b.y-p.x,-k);
    float d = length(max(w,0.0));
    return ((k>0.0)?d:-d) + r;
}


vec3 rotHue(vec3 p, float a)
{
    vec2 cs = sin(vec2(1.570796, 0) + a);

    mat3 hr = mat3(0.299,  0.587,  0.114,  0.299,  0.587,  0.114,  0.299,  0.587,  0.114) +
        	  mat3(0.701, -0.587, -0.114, -0.299,  0.413, -0.114, -0.300, -0.588,  0.886) * cs.x +
        	  mat3(0.168,  0.330, -0.497, -0.328,  0.035,  0.292,  1.250, -1.050, -0.203) * cs.y;

    return clamp(p*hr, 0., 1.);
}

float msign(in float x)
{
    return x < 0.0 ? -1.0 : 1.0;
}

mat2 rot2d(float a)
{
    float c=cos(a), s=sin(a);
    return mat2(c, -s, s, c);
}

vec2 cmul(vec2 p, vec2 q)
{
    return vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x);
}

vec2 csqrt(vec2 p)
{
    float a = atan(p.y, p.x) / 2.;
    return vec2(cos(a), sin(a)) * sqrt(length(p));
}

// distance from a 2d point p to a 2d segment (a, b)
float dseg(vec2 p, vec2 a, vec2 b)
{
    vec2 v = b - a;
    p -= a;
    float t = clamp(dot(p, v)/dot(v, v), 0., 1.);
    return length(p - t * v);
}

// iq's triangle signed distance function
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1 - p0;
    vec2 e1 = p2 - p1;
    vec2 e2 = p0 - p2;

    vec2 v0 = p - p0;
    vec2 v1 = p - p1;
    vec2 v2 = p - p2;

    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

    return -sqrt(d.x)*sign(d.y);
}

// ellipse signed distance function
float sdEllipse( vec2 p, vec2 cen, float theta, in vec2 ab )
{
    p -= cen;
    float c = cos(theta), s = sin(theta);
    p *= mat2(c, s, -s, c);
    vec2 pab = p / (ab * ab);
    return (0.5 * dot(pab, p) - 0.5) / length(pab);
}

float sdEllipseFromTriangle(vec2 p, vec2 p0, vec2 p1, vec2 p2, out vec2 f1, out vec2 f2)
{
    vec2 m = (p0 + p1 + p2) / 3.;
    vec2 n = (cmul(p0, p1) + cmul(p1, p2) + cmul(p2, p0)) / 3.;
    f1 = m + csqrt(cmul(m, m) - n);
    f2 = m - csqrt(cmul(m, m) - n);
    vec2 mid = (p0 + p1) / 2.;
    float a = (length(mid - f1) + length(mid - f2)) / 2.;
    vec2 cen = (f1 + f2) / 2.;
    vec2 dir = (f1 - f2) / 2.;
    float b = sqrt(a*a -dot(dir, dir));
    float theta = atan(dir.y, dir.x);
    return sdEllipse(p, cen, theta, vec2(a, b));
}

float getVoltage( vec2 p, vec2 p0, vec2 p1, vec2 p2 )
{
    float c = length(p - p0) * length(p - p1) * length(p - p2);
    c = log(max(c, 0.001));
    c = c / 10. + 0.5;
    c = clamp(c, 0., 1.);
    float level = sFloor(c*(palNum - .001));
    sl = level;
    return clamp(level/(palNum - 1.), 0., 1.) * .85 + .15 * c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float sf = 2. / iResolution.y;
    vec2 p = uv * 2.;

    // triangle vertices
    vec2 A = vec2(1.5*sin(iTime), 1.5);
    vec2 B = vec2(-2.+0.4*cos(iTime*0.7), -1.+0.6*cos(iTime*0.7));
    vec2 C = vec2(1.8+cos(iTime), -1.6);

    // distance to the triangle
    float dtri = sdTriangle(p, A, B, C);

    // foci of the Steiner inscribed ellipse.
    vec2[2] foci;

    // solve the ellipse and foci
    vec2 m = (A + B + C) / 3.;
    vec2 n = (cmul(A, B) + cmul(B, C) + cmul(C, A)) / 3.;
    foci[0] = m + csqrt(cmul(m, m) - n);
    foci[1] = m - csqrt(cmul(m, m) - n);
    // the tangent point is the middle point of an edge.
    vec2 mid = (A + B) / 2.;
    // semi-axis
    float a = (length(mid - foci[0]) + length(mid - foci[1])) / 2.;
    vec2 cen = (foci[0] + foci[1]) / 2.;
    vec2 dir = (foci[0] - foci[1]) / 2.;
    float b = sqrt(a*a - dot(dir, dir));
    float theta = atan(dir.y, dir.x);
    // now we have the distance to the ellipse and the foci
    float dellipse = sdEllipse(p, cen, theta, vec2(a, b));
    float dfoci = min(length(p - foci[0]), length(p - foci[1])) - 0.04;

    // distance to the medians
    float dlines = 1e5;
    dlines = min(dlines, dseg(p, A, (B+C)/2.));
    dlines = min(dlines, dseg(p, B, (A+C)/2.));
    dlines = min(dlines, dseg(p, C, (B+A)/2.));

    // inside the ellipse/between ellipse and triangle/outside the triangle
    float sgn = dellipse < 0. ? -2. : (dtri < 0. ?  0. : 1.);

    // get voltage at this point
    float volt = getVoltage(p, A, B, C);
    float ssl = sl;

    vec2 e = vec2(8./clamp(iResolution.y, 300., 800.), 0);
    float fxl = getVoltage(p + e.xy, A, B, C);
    float fxr = getVoltage(p - e.xy, A, B, C);
    float fyt = getVoltage(p + e.yx, A, B, C);
    float fyb = getVoltage(p - e.yx, A, B, C);

    // color the region between voltage contours
    vec3 col = vec3(0.4, 0.7, 0.4) - sgn*vec3(0.2, 0., 0.2)*ssl/palNum;
    // dissipate the colors by doing some rotation
    col = rotHue(col, -(min(ssl/palNum, 0.85))*12.+1.);
    // darken the edges
    col *= max(1. - (abs(fxl - fxr) + abs(fyt - fyb))*10., 0.);
    // make some highlight
    fxl = getVoltage(p + e.xy*1.5, A, B, C);
    fyt = getVoltage(p + e.yx*1.5, A, B, C);
    col += vec3(.9, .7, 1.)*(max(volt - fyt, 0.) + max(volt - fxl, 0.)
                             + max(volt - fxr, 0.) + max(volt - fyb, 0.))*ssl;
    // draw the triangle
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dtri)-0.01);
    // draw the ellipse
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dellipse)-0.005);
    // draw the medians
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dlines)-0.002);
    // draw the segment connecting the foci
    col = mix(col, vec3(0.91, 0.1, 0.1), 1.-smoothstep(0., 0.005+2.*sf, dseg(p, foci[0], foci[1])-0.01));

    // decorate the vertices and foci
    vec2[3] verts; verts[0] = A; verts[1] = B; verts[2] = C;
    float lw = 0.02;

    for (int i=0; i<3; i++)
    {
        float dv = length(p - verts[i]) - .12;
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dv))*.5);
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv));
        col = mix(col, vec3(1, .7, .6), 1. - smoothstep(0., sf, dv + lw*1.6));
        float cro = sdCross(p - verts[i], vec2(0.06, 0.012), 0.01);
        cro = abs(cro);
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, cro - lw));
    }
    for (int i=0; i<2; i++)
    {
        float dv = length(p - foci[i]) - .07;
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dv))*.5);
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv));
        col = mix(col, vec3(.4, .6, 1.), 1. - smoothstep(0., sf, dv + lw*1.2));
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv + .075 - lw));
    }

    col *= 1.25 - 0.2*length(p);

    fragColor = vec4(sqrt(max(col, 0.)), 1.0);
}

/*
    Keyboard control:

    Key 1 turn on/off applying the Mobius transformation
    Key 2 turn on/off applying the elliptic rotation
    Key 3 turn on/off applying the hyperbolic scaling
    Key 4 turn on/off showing the Riemann sphere
*/

#define PI  3.1415926536
#define E_  2.71828118285

// Raymarching constants
#define AA               1
#define MIN_TRACE_DIST   0.01
#define MAX_TRACE_STEPS  255
#define PRECISION        1e-5
#define FAR              100.

// Animation speed
#define anim_speed (iTime * .5)
#define hue_speed  (iTime * .3)

// grid and cone size
const vec2 polar_grid = vec2(0.4, PI / 7.0);
const vec2 cone_angle = normalize(vec2(1.5, 1.0));

// Intensity constants
const float intensity_divisor = 40000.;
const float intensity_factor_max = 7.2;
const float center_intensity = 12.;
const float dist_factor = 3.;
const float ppow = 1.9;

// Color constants
const float center_hue = 0.5;
const float center_sat = 0.18;

// shape constants
const float strong_factor = 7.;
const float weak_factor = 1.;
const vec2 star_hv_factor = vec2(30, 1);
const vec2 star_diag_factor = vec2(30, 1);
//const vec2 star_hv_factor = vec2(9.0, 0.3);
//const vec2 star_diag_factor = vec2(12.0, 0.6);


// b_apply: apply Mobius transformation or not
// b_elliptic: apply rotation or not
// b_hyperbolic: apply scaling or not
// b_parabolic is true if b_elliptic and b_parabolic are both false
// b_loxodromic is true if b_elliptic and b_parabolic are both true
bool b_apply = true;
bool b_elliptic = true;
bool b_hyperbolic = true;
bool b_riemann = true;  // action on Riemman sphere or not
bool b_parabolic, b_loxodromic;

// hsv to rgb conversion
vec3 hsv2rgb(vec3 hsv)
{
    const vec3 p = vec3(0.0, 2.0/3.0, 1.0/3.0);
    hsv.yz = clamp(hsv.yz, 0.0, 1.0);
    return hsv.z*(0.63*hsv.y*(cos(2.*PI*(hsv.x + p)) - 1.0) + 1.0);
}

// Conversion between Euclidean distance and hyperbolic distance
// in upper half space. They are inverse of each other.
float eucToHyp(float d) { return log(d); }
float hypToEuc(float d) { return pow(E_, d); }

// 2d rotation
vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }

// 1d and 2d rectangular grids
float grid1d(float x, float size)
{
    return mod(x + 0.5 * size, size) - 0.5 * size;
}

vec2 grid2d(vec2 p, vec2 size)
{
    return mod(p + 0.5 * size, size) - 0.5 * size;
}

// 2d polar grids
vec2 polarGrid(vec2 p, vec2 size)
{
    float theta = atan(p.y, p.x);
    float r = eucToHyp(length(p));
    return grid2d(vec2(r, theta), size);
}

/*
 * Complex arithmetic
*/
vec2 cmul(vec2 z, vec2 w)
{
    return vec2(z.x * w.x - z.y * w.y, z.x * w.y + z.y * w.x);
}

vec2 cdiv(vec2 z, vec2 w)
{
    return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

vec2 csqrt(vec2 z)
{
    float r2 = dot(z, z);
    float r = sqrt(sqrt(r2));
    float angle = atan(z.y, z.x);
    return r * vec2(cos(angle / 2.0), sin(angle / 2.0));
}

/*
 * Quaternion arithmetic
*/
vec4 qmul(vec4 p, vec4 q)
{
    return vec4(p.x * q.x - dot(p.yzw, q.yzw),
                p.x * q.yzw + q.x * p.yzw + cross(p.yzw, q.yzw));
}

vec4 qdiv(vec4 p, vec4 q)
{
    return qmul(p, vec4(q.x, -q.yzw) / dot(q, q));
}

/*
 * Mobius transformation z --> (Az + B) / (Cz + D)
*/
struct Mobius
{
    vec2 A, B, C, D;
};

const Mobius mob = Mobius(
    vec2(1, 0),
    vec2(-1, 0),
    vec2(1, 0),
    vec2(1, 0)
);

// Apply Mobius transformation on complex plane
vec2 applyMobius(vec2 z)
{
    vec2 z1 = cmul(mob.A, z) + mob.B;
    vec2 z2 = cmul(mob.C, z) + mob.D;
    return cdiv(z1, z2);
}

// Apply Mobius transformation on upper half space as quaternions
// (x, y, z) --> (x + yi + zj + 0k)
vec4 applyMobius(vec4 p)
{
    vec4 p1 = qmul(vec4(mob.A, 0., 0.), p) + vec4(mob.B, 0., 0.);
    vec4 p2 = qmul(vec4(mob.C, 0., 0.), p) + vec4(mob.D, 0., 0.);
    return qdiv(p1, p2);
}

float applyMobius(inout vec3 p)
{
    if (!b_apply)
        return 1.0;

    p = applyMobius(vec4(p, 0)).xyz;
    float scale = length(p);
    return scale > 1.0 ? 1.0 / scale : scale;
}

// A Mobius transformation of hyperbolic type is conjugate to a pure scaling
void trans_hyperbolic(inout vec2 p)
{
    float d = eucToHyp(length(p)) - anim_speed * polar_grid.x;
    // This avoids running out of resolution.
    d = grid1d(d, polar_grid.x);
    p = normalize(p) * hypToEuc(d);
}

// A Mobius transformation of elliptic type is conjugate to a pure rotation
void trans_elliptic(inout vec2 p)
{
    p = rot2d(p, anim_speed * polar_grid.y);
}

// A Mobius transformation of parabolic type is conjugate to a pure translation
void trans_parabolic(inout vec2 p)
{
    p.x += iTime * polar_grid.x / 3.;
}

// signed distance function for sphere kissing at y=0 with radius r
float sdSphere(vec3 p, float r) { p.y -= r; return length(p) - r; }
// signed distance functions for plane y=0 and y=c
float sdPlane(vec3 p) { return p.y; }
float sdPlane(vec3 p, float c) { return p.y - c; }
// a cone in the upper hyperbolic space may be a usual cone at the origin
// or a Dupin cyclide with its two horns on the plane
float sdCone(vec3 p)
{
    float t = 1.0;
    if (b_apply)
    {
        t = applyMobius(p);
        p = normalize(p);
    }
    float q = length(p.xz);
    return dot(cone_angle, vec2(q, -p.y)) * t;
}

// signed distance function for parabolic case
float sdScene1(vec3 p)
{
    return b_apply ? min(sdPlane(p), sdSphere(p, 1.0)) : sdPlane(p, 0.5);
}

// signed distance function for elliptic/hyperbolic case
float sdScene2(vec3 p)
{
    if (b_riemann)
        return min(sdPlane(p), sdSphere(p, 1.));

    return min(sdPlane(p), sdCone(p));
}

vec3 getColor(vec2 p, float pint)
{
    float sat = 0.75 / pow(pint, 2.5) + center_sat;
    // change hue by time
    float hue2 = b_parabolic ?
        hue_speed - length(p.y) / 5.0 :
        hue_speed - eucToHyp(length(p)) / 7.0;
    float hue = center_hue + hue2;
    return hsv2rgb(vec3(hue, sat, pint)) + pint / 3.;
}

float getIntensity1(vec2 p)
{
    float dist = length(p);
    float disth = length(p * star_hv_factor);
    float distv = length(p * star_hv_factor.yx);

    vec2 q = 0.7071 * vec2(dot(p, vec2(1.)), dot(p, vec2(1., -1.)));
    float dist1 = length(q * star_diag_factor);
    float dist2 = length(q * star_diag_factor.yx);

    // Middle point star intensity
    float pint1 = .5 / (dist * dist_factor + 0.015)
        + strong_factor / (distv * dist_factor + 0.01)
        + weak_factor / (disth * dist_factor + 0.01)
        + weak_factor / (dist1 * dist_factor + 0.01)
        + weak_factor / (dist2 * dist_factor + 0.01);

    return center_intensity * intensity_factor_max * pow(pint1, ppow) / intensity_divisor;
}

float getIntensity2(vec2 p)
{
    float angle = atan(polar_grid.x, polar_grid.y);
    float dist  = length(p);
    float disth = length(p * star_hv_factor);
    float distv = length(p * star_hv_factor.yx);

    vec2 q1 = rot2d(p, angle);
    float dist1 = length(q1 * star_diag_factor);
    vec2 q2 = rot2d(p, -angle);
    float dist2 = length(q2 * star_diag_factor);

    float pint1 = 1. / (dist * dist_factor  + .5);
    if (b_loxodromic)
    {
        pint1 = strong_factor / (dist2 * dist_factor + 0.01)
            + weak_factor  / (dist1 * dist_factor + 0.01)
            + weak_factor / (disth * dist_factor + 0.01)
            + weak_factor / (distv * dist_factor + 0.01);
    }
    else if (b_elliptic)
    {
        pint1 += weak_factor / (distv * dist_factor + 0.01) +
            strong_factor / (disth * dist_factor + 0.01) +
            weak_factor / (dist1 * dist_factor + 0.01) +
            weak_factor / (dist2 * dist_factor + 0.01);
    }
    else
    {
        pint1 += weak_factor / (disth * dist_factor + 1.) +
            strong_factor / (distv * dist_factor + .01) +
            weak_factor / (dist1 * dist_factor + 0.01) +
            weak_factor / (dist2 * dist_factor + 0.01);
    }
    return intensity_factor_max * pow(pint1, ppow) / intensity_divisor * center_intensity * 3.;
}

float map(vec3 pos)
{
    return b_parabolic ? sdScene1(pos) : sdScene2(pos);
}


vec3 getNormal(vec3 p)
{
    vec2 e = vec2(.003, 0);
    float d1 = map(p + e.xyy), d2 = map(p - e.xyy);
    float d3 = map(p + e.yxy), d4 = map(p - e.yxy);
    float d5 = map(p + e.yyx), d6 = map(p - e.yyx);
    float d = map(p) * 2.;
    return normalize(vec3(d1 - d2, d3 - d4, d5 - d6));
}

float softShadow(vec3 ro, vec3 rd, float tmin, float tmax, float k)
{
    const int maxShadeIterations = 20;
    float res = 1.0;
    float t = tmin;
    for (int i = 0; i < maxShadeIterations; i++)
    {
        float h = map(ro + rd * t);
        res = min(res, smoothstep(0., 1., k * h / t));
        t += clamp(h, 0.01, 0.2);
        if (abs(h) < 1e-4 || t > tmax)
            break;
    }
    return clamp(res+0.15, 0.0, 1.0);
}


// iq's ambient occlusion
float calcAO(vec3 p, vec3 n)
{
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++)
    {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n);
        occ += (h - d) * sca;
        sca *= 0.7;
    }
    return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

float trace(vec3 ro, vec3 rd, out vec2 p, out float pint)
{
    float depth = MIN_TRACE_DIST;
    float dist;
    vec3 pos;
    for (int i = 0; i < MAX_TRACE_STEPS; i++)
    {
        pos = ro + rd * depth;
        dist = map(pos);
        if (dist < PRECISION || depth >= FAR)
            break;
        depth += dist;
    }
    if (b_parabolic)
    {
        if (b_apply)
            pos /= dot(pos, pos);

        p = pos.xz;
        trans_parabolic(pos.xz);
        pos.xz = grid2d(pos.xz, vec2(polar_grid.x / 2.0));
        pint = getIntensity1(pos.xz);
    }
    else
    {
        applyMobius(pos);
        p = pos.xz;
        if (b_hyperbolic) trans_hyperbolic(pos.xz);
        if (b_elliptic)   trans_elliptic(pos.xz);
        pos.xz = polarGrid(pos.xz, polar_grid);
        pint = getIntensity2(pos.xz);
    }
    return depth;
}

// ACES tone mapping
// https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 tonemap(vec3 color)
{
    const float A = 2.51;
    const float B = 0.03;
    const float C = 2.43;
    const float D = 0.59;
    const float E = 0.14;
    return (color * (A * color + B)) / (color * (C * color + D) + E);
}

const int CHAR_1 = 49;
const int CHAR_2 = 50;
const int CHAR_3 = 51;
const int CHAR_4 = 52;

// https://www.shadertoy.com/view/lsXGzf
bool keypress(int code) 
{
	return texelFetch(iChannel0, ivec2(code,2), 0).x != 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    b_apply = !keypress(CHAR_1);
    b_elliptic = !keypress(CHAR_2);
    b_hyperbolic = !keypress(CHAR_3);
    b_riemann = keypress(CHAR_4);

    b_parabolic = !(b_elliptic || b_hyperbolic);
    b_loxodromic = b_elliptic && b_hyperbolic;
    vec3 ro = vec3(-2., 4.2, 5.0);
    //ro.xz = rot2d(ro.xz, iTime*0.3);
    vec3 lookat = vec3(0.0, 0.6, 0.0);
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 f = normalize(lookat - ro);
    vec3 r = normalize(cross(f, up));
    vec3 u = normalize(cross(r, f));
    vec3 tot = vec3(0);
    vec3 lp = ro + vec3(.2, .8, -0.2);


    for (int ii = 0; ii < AA; ii++)
    {
        for (int jj = 0; jj < AA; jj++)
        {
            vec2 offset = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (fragCoord + offset) / iResolution.xy;
            uv = 2.0 * uv - 1.0;
            uv.x *= iResolution.x / iResolution.y;
            vec3 rd = normalize(uv.x * r + uv.y * u + 4.0 * f);
            vec2 p;
            float pint;
            float t = trace(ro, rd, p, pint);
            if (t >= 0.0)
            {            
                vec3 col = tonemap(4.0 * getColor(p, pint));
                vec3 pos = ro + rd * t;
                vec3 nor = getNormal(pos);
                vec3 ld = lp - pos;
                float dist = max(length(ld), 0.001);
                ld /= dist;
                float at = 2.2/(1. + dist*.1 + dist*dist*.05); 
                float ao = calcAO(pos, nor);
                float sh = softShadow(pos, ld, 0.04, dist, 8.);

                float diff = clamp(dot(nor, ld), 0.0, 1.0);
                float spec = max( 0.0, dot( reflect(-ld, nor), -rd));
	            spec = pow(spec, 50.0);
                tot += diff * 2.5 * col + vec3(0.6, 0.8, 0.8) * spec * 2.;
                tot *= ao * sh * at;
            }
            if(t >= FAR)
                lp = normalize(lp - ro - rd*FAR);

            vec3 bg = mix(vec3(.5, .7, 1), vec3(1, .5, .6), .5 - .5*lp.y) * .3;
            tot = mix(clamp(tot, 0., 1.), bg, smoothstep(0., FAR-2., t));
        }
    }
    tot /= float(AA * AA);
    fragColor = vec4(sqrt(clamp(tot, 0., 1.)), 1.0);
}

/*
This animation shows that Mobius transformations are rigid motions
of "admissible" spheres in the upper half-space.

A sphere is called admissible if its highest point is above the xy-plane.

The procedure involves four steps:

1. Map the complex plane (xy) to the sphere using inverse
   stereographic projection.

2. Translate and rotate the sphere, but keeping it admissible..

3. Map the sphere back to the complex plane using stereographic projection, 
   with the north pole being the highest point of the sphere.

4. Combine steps 1-3 to get a Mobius transformation of the complex plane.
   All Mobius transformations can be obtained this way.


In this process,

1. moving the sphere in the xy-plane is a translation of the complex plane.

2. moving it along the z-axis is a scaling of the complex plane.

3. rotating it in the xy-plane is a rotation of the complex plane.

4. rotating 180 degress about the x-axis is the inverse map z -> 1/z.


For a given Mobius transformation M and a chosen initial position
of the sphere, the rigid motion that gives M is unique.
*/

#define PI            3.141592654
#define TAU           (2.0*PI)
#define ROT(a)        mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(x)       (0.5 + 0.5*cos(x))
#define DOT2(x)       dot(x, x)
#define TIME          iTime
#define Y             vec3(0, 1, 0)
#define NPOLE(sph)    (sph.xyz + sph.w * Y)
#define PLANE         vec4(0, 1, 0, 1.5)
#define aa            (2.0/iResolution.y)

const vec4 hsv2rgb_K = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float grid_max     = 3.0;
const float grid_size    = 0.5;
const vec3  light_pos    = vec3(3, 4, 3);
const vec3  light_dir    = normalize(light_pos);
const vec3  sky0_color   = HSV2RGB(vec3(0.0, 0.65, 0.95)); 
const vec3  sky1_color   = HSV2RGB(vec3(0.6, 0.5, 0.5));
const vec3  grid_color   = HSV2RGB(vec3(0.6, 0.6, 1.0)); 
const vec3  light_color  = 12.0*HSV2RGB(vec3(0.6, 0.5, 1.0));
const vec3  plane_color  = HSV2RGB(vec3(0.7, 0.125, 1.0/32.0)); 

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b-a)/k, 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0-h);
}

vec2 mod2(inout vec2 p, vec2 size) {
    vec2 id = floor(p / size);
    p = mod(p + size*0.5, size) - size*0.5;
    return id;
}

float rayPlane(vec3 ro, vec3 rd, vec4 p) {
    return -(dot(ro, p.xyz) + p.w) / dot(rd, p.xyz);
}

vec2 raySphere(vec3 ro, vec3 rd, vec4 sph) {
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if (h < 0.0) return vec2(-1.0);
    h = sqrt(h);
    return vec2(-b - h, -b + h);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(12.9898, 58.233))) * 13758.5453);
}

float softShadow(vec3 ro, vec3 rd, vec4 sph, float k) {
    vec3 oc = ro - sph.xyz;
    float r2 = sph.w * sph.w;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - r2;
    float h = b * b - c;
    float d = -sph.w + sqrt(max(0.0, r2 - h));
    float t = -b - sqrt(max(0.0, h));
    return (t < 0.0) ? 1.0 : smoothstep(0.0, 1.0, k * d / t);
}

float bounce() {
    float t = fract(TIME) - 0.5;
    return 0.25 - t*t;
}

vec2 path(float t) {
    const float rad = 0.8;
    return rad * vec2(cos(t), sin(t));
}

void rot(inout vec3 sp) {
    sp.xy    *= ROT(TIME*0.3);
    sp.xz    *= ROT(TIME*0.3);
}

vec3 diffuse(vec3 pos, vec3 nor) {
    float ll = DOT2(light_pos - pos);
    vec3  ld = normalize(light_pos - pos);
    return light_color * max(dot(nor, ld), 0.0) / ll * 20.;
}

vec3 restrictColor(vec2 p, vec3 col) {
    float cond =  step(abs(p.x), grid_max);
          cond *= step(abs(p.y), grid_max);
    return mix(plane_color, col, cond);
}

vec3 planeToSphere(vec3 p, vec4 sph) {
    vec3 N = NPOLE(sph);
    vec3 rd = normalize(N - p);
    float t = raySphere(p, rd, sph).x;
    return p + rd*t;
}

vec3 sphereToPlane(vec3 p, vec4 sph, vec4 plane) {
    vec3 N = NPOLE(sph);
    vec3 rd = normalize(p - N);
    float t = rayPlane(p, rd, plane);
    return p + rd*t - sph.xyz;
}

vec3 computeGridColor(vec3 pos, vec2 pp) {
    vec2 z = pp;
    mod2(z, vec2(grid_size));
    float d1 = smin(abs(z.x), abs(z.y), .05);
    float gm = PCOS(-TAU * TIME + 0.25 * TAU * length(pos.xz));

    z = pp;
    vec2 id = mod2(z, vec2(grid_size / 6.));
    float d2 = min(abs(z.x), abs(z.y)) - 0.0125;

    float n  = hash21(id);
    float n2 = n * 0.3 + .7;
    if (n < 0.1 * sin(TIME) + 0.1)
        n = 1E6;
    else
        n = 0.0;

    vec3 gcol = vec3(0);
    gcol = mix(gcol, HSV2RGB(vec3(hash21(id), 1.0, n2)), vec3(n > 1.0));
    gcol -= 0.2*vec3(2.0, 1.0, 1.0)*exp(-100.0*max(d2+0.01, 0.0));
    gcol = mix(gcol, 6.*vec3(0.1, 0.09, 0.125), smoothstep(-aa, aa, -(d2+0.0075)));
    gcol += 0.5*vec3(2.0, 1.0, 1.0)*exp(-900.0*abs(d2-0.00125));

    vec3 col = clamp(gcol, -1.0, 1.0);

    gcol = mix(vec3(0.75), 2.0*vec3(3.5, 2.0, 1.25), gm);
    gcol *= exp(-mix(400.0, 100.0, gm) * max(d1-0.0125, 0.0));
    gcol *= 0.3;
    col = mix(col, gcol, smoothstep(-aa, aa, -d1 + 0.0125)); 
    return col;
}


vec3 renderBackground(vec3 ro, vec3 rd, vec4 sph, float T) {
    vec3 sky = smoothstep(1.0, 0.0, rd.y) * sky1_color + 
               smoothstep(0.5, 0.0, rd.y) * sky0_color;

    sky += pow(max(dot(rd, light_dir), 0.0), 800.0)*light_color;
    if (rd.y >= 0.0)
        return sky;

    float ht  = 1.0 + 0.2 * smoothstep(-0.05, 0.1, bounce());
    float t   = rayPlane(ro, rd, vec4(vec3(0.0, ht, 0.0), 0.5));
    vec3 pos  = ro + t*rd;
    vec3 dif  = diffuse(pos, Y);
    float sha = softShadow(pos, normalize(light_pos - pos), sph, 2.0);
    dif *= sha;

    vec3 sp = planeToSphere(pos, sph);
    sp -= sph.xyz;
    rot(sp);
    sp += sph.xyz;
    vec3 pp = sphereToPlane(sp, sph, PLANE);

    vec3 col = computeGridColor(pos, pp.xz);
    col = restrictColor(pp.xz, col); 
    col += plane_color * dif;
    col /= (1.0 + 0.25 * DOT2(pos.xz)); 
    col = mix(sky, col, tanh(500.0/(1.0 + DOT2(pos))));
    return col;
}

vec3 renderBall(vec3 ro, vec3 rd, vec4 sph, vec2 st, float T) {
    vec3 pos  = ro + st.x*rd;
    vec3 sp   = pos - sph.xyz;
    vec3 nor  = normalize(sp);
    vec3 ref  = reflect(rd, nor);
    vec3 dif  = diffuse(pos, nor);
    rot(sp);
    pos       = sp + sph.xyz;
    vec3 pp   = sphereToPlane(pos, sph, vec4(0, 1, 0, 0.5));
    vec3 rcol = renderBackground(pos, ref, sph, T);

    vec2 z = pp.xz;
    mod2(z, vec2(grid_size));
    float d1 = smin(abs(z.x), abs(z.y), .05);
    float gm = PCOS(-TAU * T + 0.25 * TAU * length(pp));

    z = pp.xz;
    vec2 id = mod2(z, vec2(grid_size / 6.));
    float d2 = min(abs(z.x), abs(z.y)) - 0.0125;

    float n = hash21(id);
    float n2 = n * 0.3 + .7;
    if (n < 0.1 * sin(T) + 0.1)
        n = 1E6;
    else
        n = 0.0;

    vec3 gcol = vec3(0);
    gcol = mix(gcol, HSV2RGB(vec3(hash21(sin(id * TAU)), 1.0, n2)), vec3(n > 1.0));
    gcol -= 0.2*vec3(2.0, 1.0, 1.0)*exp(-100.0*max(d2+0.01, 0.0));
    gcol = mix(gcol, 6.*vec3(0.09, 0.1, 0.125), smoothstep(-aa, aa, -(d2+0.0075)));
    gcol += 1.*vec3(2.0, 1.0, 1.0)*exp(-900.0*abs(d2-0.00125));
    vec3 col = clamp(gcol, -1., 1.);
    gcol = mix(vec3(0.75), 2.0*vec3(3.5, 2.0, 1.25), gm);
    gcol *= exp(-mix(400.0, 100.0, gm) * max(d1-0.0125, 0.0));
    gcol *= 0.3;
    col = mix(col, gcol, smoothstep(-aa, aa, -d1 + 0.0125));
    col = restrictColor(pp.xz, col);
    col += rcol * 0.175;
    return col;
}

vec3 render(vec2 p, vec2 uv) {
    vec2 ph = path(TIME * 0.5);
    vec3 ro = vec3(2.5, 1.0, 0.);
    ro.xz *= ROT(TIME / 3.);
    vec3 lookat = vec3(0., 0., 0.);
    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, Y));
    vec3 up = normalize(cross(right, forward));
    vec3 rd = normalize(p.x*right + p.y*up + 2.0*forward);
    vec4 sph = vec4(vec3(0., bounce(), 0.), 0.5);
    sph.xz += ph;
    vec2 st = raySphere(ro, rd, sph);
    if (st.x >= 0.0)
        return renderBall(ro, rd, sph, st, TIME);
    else
        return renderBackground(ro, rd, sph, TIME);
}

vec3 postprocess(vec3 col, vec2 q) {
    col = pow(clamp(col, 0.0, 1.0), vec3(1.0/2.2)); 
    col = col*0.6 + 0.4*col*col*(3.0 - 2.0*col);
    col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
    col *= 0.5 + 0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.7);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec3 col = render(p, uv);

    //float fi = smoothstep(0.0, 5.0, TIME);
    //col = mix(vec3(0.0), col, fi);

    col = postprocess(col, uv);
    fragColor = vec4(col, 1.0);
}

vec3 get_floor_color(vec2 z) {
    // Modify P, Q, R to change the tiling
    const float P = 2.;
    const float Q = 3.;
    const float R = 7.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const vec2 mB = vec2(-cp, sp);
    const float k1 = cos(PI / Q);
    const float k2 = (cos(PI / R) + cp * k1) / sp;
    const float rad = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);
    const vec2 cen = vec2(k1 * rad, k2 * rad);
    const vec2 v0 = vec2(0, cen.y - sqrt(rad * rad - cen.x * cen.x));
    const vec2 n_ = vec2(-mB.y, mB.x);
    const float b_ = dot(cen, n_);
    const float c_ = dot(cen, cen) - rad * rad;
    const float k_ = b_ + sqrt(b_ * b_ - c_);
    const vec2 m0 = k_ * n_;

    vec2 p = square_to_disc(z);
    vec2 invCtr = vec2(1);
    float t = 1. / dot(p -invCtr, p - invCtr);
    p = mix(invCtr, p, t);
    p.x = -p.x;
    p = rot2(iTime/12.)*p;

    if (length(p) > 1.)
        p /= dot(p, p);

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        float d = length(p - cen) - rad;
        if (d < 0.) {
            p -= cen;
            p *= rad * rad / dot(p, p);
            p += cen;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, vec2(0), v0, .007));    
    ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, length(p - cen) - rad - 0.007);

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime*.5 + count)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 4. + vec3(0, 1, 2));
    oCol = mix(oCol*.1, clamp(oCol*3.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.8, .97, sin(iTime * 1.5)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

vec3 get_ceil_color(vec2 p) {
    const float P = 3.;
    const float Q = 3.;
    const float R = 3.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const float cq = cos(PI / Q), cr = cos(PI / R);
    const vec2 mB = vec2(-cp, sp);
    const vec3 mC = vec3(-cr, -(cq + cr * cp) / sp, 1.);
    const vec2 v0 = vec2(0, -1./mC.y);
    const float k_ = mB.x / mB.y;
    const float x_ = 1. / (k_ * mC.y - mC.x);
    const float y_ = -k_ * x_;
    const vec2 m0 = vec2(x_, y_);

    p = rot2(iTime/12.)*p;

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        k = dot(vec3(p, 1.), mC);
        if (k < 0.) {
            p -= 2. * k * mC.xy;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    //ln = min(ln, lBox(p, vec2(0), v0, .05));    
    //ln = min(ln, lBox(p, vec2(0), m0, .05));
    ln = min(ln, lBox(p, v0, m0, .05));

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime + count * PI)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 8. + vec3(0, 1, 2)).yzx;
    oCol = mix(oCol*.1, clamp(oCol*1.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*15. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize * 16.)));

    pnt -= .15;
    pnt = min(pnt, length(p) - .15);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.4, .5, sin(iTime)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

// distance to the four sides of mirrors 
float sdMirror(vec3 p) {
    vec2 d = RoomSize.xz - abs(p.xz);
    return min(d.x, d.y);
}

// the floor and the ceil
float sdFloor(vec3 p) {
    return RoomSize.y - abs(p.y);
}

// wood framework
float sdRoomFrame(vec3 p) {
    return sdBoxFrame(p, RoomSize, vec3(RoomFrameSize));
}

float sdMirrorFrame(vec3 p) {    
    float d1 = sdBoxFrame(p, RoomSize, WindowFrameSize.xyy);
    float d2 = sdBoxFrame(p, RoomSize, WindowFrameSize.yxy);
    float d3 = sdBoxFrame(p, RoomSize, WindowFrameSize.yyx);
    return min(d1, min(d2, d3));
}

// polyhedra with faces and edges
vec2 sdPolyhedra(vec3 p) {
    p *= RotObj;
    p = fold_spherical(p);
    p -= v0S;
    float d1 = sdPolyhedraFaces(p);
    float d2 = sdPolyhedraEdges(p);
    float d = min(d1, d2);
    float id = d1 < d2 ? 1. : 2.;
    return vec2(d, id);
}

float sdScene(vec3 p) {
    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    float d5 = sdPolyhedra(p).x;
    return min(d1, min(d2, min(d3, min(d4, d5))));
}

vec3 trace(vec3 pos, vec3 rd) {
    float h = 1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        if (h < PRECISION)
            break;
        h = sdScene(pos);
        pos += rd * min(h, MAX_MARCH_STEP);
    }
    return pos;
}

float soft_shadow(vec3 pos, vec3 lp) {
    const float softness = 16.;
    const float shadowStartDistance = .1;
    const int shadowLoopCount = 16;

    vec3 ld = lp - pos;
    float ldist = max(length(lp), 0.001);
    ld /= ldist;
    float epsilon = .001;
    float t = shadowStartDistance;
    float h = 1.;
    float shade = 1.0;
    for (int i=0; i < shadowLoopCount; i++ ) {
        if (h < epsilon || t > ldist)
            break;
        h = sdScene(pos + ld * t);
        shade = min(shade, smoothstep(0.0, 1.0, softness*h/t));
        t += h;
    }
    return clamp(shade + 0.25, 0., 1.);
}

vec3 get_normal(vec3 p) {
    const vec2 d = vec2(-1, 1) * .001;
    return normalize(
            sdScene(p + d.xxx)*d.xxx +
            sdScene(p + d.yyx)*d.yyx +
            sdScene(p + d.yxy)*d.yxy +
            sdScene(p + d.xyy)*d.xyy);
}

struct Light {
    vec3 pos;
    vec3 col;
};

Light lights[] = Light[] (
    Light(vec3(2.5, 2.2, 2.5), vec3(1) * 5.),
    Light(vec3(-1, -.5, -1), vec3(1)),
    Light(vec3(-2.5,-1.9,-2.5), vec3(0, .3, 1))
);

void ray_bounce(in vec3 p,
                in vec3 rd,
                out vec3 diffuse,
                out vec3 ref_dir,
                out vec3 bounceTint,
                in vec3 normal) {

    vec3 bCol = vec3(0);
    for( int i=0; i < lights.length(); i++ ) {
        vec3 ld = lights[i].pos - p;
        float ldist = max(length(ld), 0.001);
        ld /= ldist;
        float diff = max(0., dot(normal, ld));
        float at = 1.5 / (1. + ldist * 0.3 + ldist * ldist * 0.1);
        float sh = soft_shadow(p, lights[i].pos);
        bCol += lights[i].col * sh * (.2 + diff) * at;
    }
    // ambient light
    vec3 ao = vec3(.03,.05,.07);
    // sample SDF to approximate occlusion
    ao *= exp2(min(0., sdScene(p + normal * 0.3) / 0.3 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .15) / .15 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .07) / .07 - 1.));
    bCol += ao;

    // https://www.shadertoy.com/view/tlBGR1
    vec3 albedo = vec3(.3,.5,.6) * .4;
    vec4 specLevel = vec4(0.9, 0.9, 0.9, 1);

    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    vec2 sdPoly = sdPolyhedra(p);
    float d5 = sdPoly.x, id = sdPoly.y;
    float d = min(d1, min(min(d2, d3), min(d4, d5)));

    if (d == d3) {  // room frame
        albedo = vec3(.25);
        specLevel = vec4(0.08);
    }
    else if (d == d4) {  // mirror border
        albedo = vec3(0.01);
        specLevel = vec4(0.2, 0.2, 0.2, 1.);
    }
    else if (d == d5) {  // polyhedra
        if (id == 2.) {  // polyhedra edges
            albedo = vec3(0.15, 0.3, 0.8);
            specLevel = vec4(vec3(0.05), 1.);
        }
        else {  // polyhedra faces
            specLevel = vec4(0.7);
        }
    }
    else if (d == d2) {  // floor and ceil
        vec2 uv = p.xz;
        albedo = p.y < 0. ? get_floor_color(uv / (RoomSize.x - RoomFrameSize))
                          : get_ceil_color(uv * 2.);
        float ior = 1.33;
        float schlick = pow((ior - 1.) / (ior + 1.), 2.);
	    specLevel = vec4(vec3(schlick), 1);
    	specLevel *= mix(.2, .7, p.x);
    	normal = normalize(normal + (texture(iChannel0, vec2(p.xz) /2.).rgb - .5) * .005);
    }

    float fresnel = pow(dot(normal, rd) + 1., 5.);
    vec3 spec = mix(specLevel.xyz, specLevel.www, fresnel);

    bounceTint = spec;
    diffuse = albedo * (vec3(1) - spec) * bCol;
    ref_dir = reflect(rd, normal);
}

vec3 get_ray_color(vec3 ro, vec3 rd) {
    vec3 pos = ro;
    vec3 tint = vec3(1);

    vec3 col = vec3(0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        pos = trace(pos, rd);
        vec3 normal = get_normal(pos);
        vec3 bounceTint, diffuse;
        ray_bounce(pos, rd, diffuse, rd, bounceTint, normal);
        pos += normal * PRECISION * 2.;
        col += diffuse * tint;
        tint *= bounceTint;
        if (length(tint) < .01)
            break;
    }
    return col;
}

mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init_spherical();

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 movement = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);
    vec3 eye = 2.5 * vec3(
        cos(movement.x) * cos(movement.y),
        0.5*sin(movement.y),
        sin(movement.x) * cos(movement.y));
    eye += vec3(1, 0.3, 1) * cos(vec3(0.75, 0.3, 0.2) * iTime);

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.) {
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
        eye = rot_y(mouse.x) * rot_x(mouse.y) * eye;
    }
    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    mat3 M = camera_matrix(eye, lookat, up);

    vec3 ray = M * normalize(vec3(uv, -2.));
    vec3 col = get_ray_color(eye, ray);
    fragColor = vec4(clamp(sqrt(col), 0., 1.), 1.);
}

#define PI  3.141592654
#define BlackRegionSize  0.03
#define L2(x)  dot(x, x)
#define RotObj  rot_x(iTime/2.)*rot_z(iTime/3.)
#define PolyhedraEdgeSize  0.02
#define PolyhedraFaceOffset  0.
#define RoomSize  vec3(5, 2.5, 5)
#define RoomFrameSize  0.5
#define WindowFrameSize vec2(RoomFrameSize*1.2, 0)
#define MAX_TILING_REFLECTIONS 12
#define MAX_TRACE_STEPS  128
#define MAX_RAY_BOUNCES  15
#define PRECISION        1e-3
#define MAX_MARCH_STEP   1.0

const vec3 pqr_wythoff = vec3(5, 2, 3);
const vec3 bary = vec3(1, 1, 1);

mat3 matS, triS;
vec3 v0S;

void init_spherical() {
    vec3 c = cos(PI / pqr_wythoff);
    float sp = sin(PI / pqr_wythoff.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x*c.z)/sp;
    float z3 = sqrt(1.0 - x3*x3 - y3*y3);
    vec3 m3 = vec3(x3, y3, z3);
    matS = mat3(m1, m2, m3);
    triS[0] = normalize(cross(m2, m3));
    triS[1] = normalize(cross(m3, m1));
    triS[2] = normalize(cross(m1, m2));
    v0S = normalize(bary * inverse(matS));
}

vec3 fold_spherical(vec3 p) {
    for (int i = 0; i < 5; i++)
    for (int j = 0; j < 3; j++) {
        p -= 2. * min(dot(p, matS[j]), 0.) * matS[j];
    }
    return p;
}

vec3 tex3D(sampler2D tex, in vec3 p, in vec3 n ) {
    n = max(n * n, 0.001);
    n /= (n.x + n.y + n.z ); 
	return (
        texture(tex, p.yz) * n.x +
        texture(tex, p.zx) * n.y +
        texture(tex, p.xy) * n.z
    ).xyz;
}

mat2 rot2(in float a) {
    float c = cos(a), s = sin(a); return mat2(c, -s, s, c);
}

mat3 rot_x(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0,  0, 
                0, cx, sx,
                0, -sx, cx);
}

mat3 rot_y(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);
}

mat3 rot_z(in float t) {
    float cz = cos(t), sz = sin(t);
    return mat3(cz, -sz, 0.,
                sz, cz, 0.,
                0., 0., 1.);
}

float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}

// https://www.shadertoy.com/view/3ljcRh
// I changed e to vec3 so it works for non-uniform edge size
float sdBoxFrame(vec3 p, vec3 b, vec3 e) {
    p = abs(p) - b;
    vec3 q = abs(p + e) - e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float sdPolyhedraEdges(vec3 p) {
    float d = 1e5;
    for (int i = 0; i < 3; i++) {
        d = min(d, L2(p - min(0., dot(p, matS[i])) * matS[i]));
    }
	return sqrt(d) - PolyhedraEdgeSize;
}

float sdPolyhedraFaces(vec3 p) {
   return max(dot(p, triS[0]), max(dot(p, triS[1]), dot(p, triS[2]))) - PolyhedraFaceOffset;
}

//complex multiplication
vec2 cMul(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

// complex inverse 
vec2 cDiv( vec2 a, vec2 b ) {
  float d = dot(b, b);
  return vec2(dot(a, b), a.y*b.x - a.x*b.y ) / d;
}

// complex inverse 
vec2 cInv(vec2 a) {
	return vec2(a.x, -a.y) / dot(a, a);
}

// code below taken from @mla's https://www.shadertoy.com/view/Mlsfzs
void sncndn(float u, float k2,
            out float sn, out float cn, out float dn) {
    float emc = 1.0 - k2;
    float a = 1.0, b, c;
    const int N = 4;
    float em[N], en[N];
    dn = 1.0;
    for (int i = 0; i < N; i++) {
        em[i] = a;
        emc = sqrt(emc);
        en[i] = emc;
        c = 0.5*(a + emc);
        emc *= a;
        a = c;
    }

    u = c*u;
    sn = sin(u);
    cn = cos(u);
    if (sn != 0.0) {
        a = cn / sn;
        c *= a;
        for(int i = N - 1; i >= 0; i--) {
            b = em[i];
            a *= c;
            c *= dn;
            dn = (en[i] + a) / (b + a);
            a = c/b;
        }
        a = 1.0 / sqrt(c*c + 1.0);
        if (sn < 0.0)
            sn = -a;
        else
            sn = a;
        cn = c * sn;
    }
}

vec2 cn(vec2 z, float k2) {
  float snu, cnu, dnu, snv, cnv, dnv;
  sncndn(z.x, k2, snu, cnu, dnu);
  sncndn(z.y, 1.0-k2, snv, cnv, dnv);
  return vec2(cnu * cnv, -snu*dnu*snv*dnv) / (1. - dnu*dnu*snv*snv);
}

vec2 square_to_disc(vec2 z) {     
    z = cDiv(z, vec2(1));
    z -= vec2(1, 0);
    z *= 1.854;
    z = cn(z, 0.5);
    z = cMul(z, vec2(0.70711));
    return z;
}

/*
    Poncelet's triangle and degree 3 Blaschke product.

A Blaschke product B(z) of degree n is an mapping of the unit disc D to itself and has the form

B(z) = \prod_{i=1}^n (z - a_i) / (1 - conjugate(a_i)*z) where a_i, i=1,2,...,n are all in D.

B(z) preserves D. Further more, for each point P on D, there are n preimages
z1, ..., zn on D such that B(z1) = ... = B(zn) = P.

Now fix n=3, and let a, b be complex numbers in D. For simplicity we choose a, b
both are reals and a is always the origin. Let

B(z) = z * (z-a)/(1-a*z) * (z-b)/(1-b*z)

So B(z) is a Blaschke product of degree 3 and it has zeros at 0, a, b.

Then for each point P on D, the preimages z1, z2, z3 form an inscribed triagle
ABC of D, interestingly, ABC is also the circumscribe triangle of an ellipse
with foci at a and b:

|z - a| + |z - b| = |1 - conjugate(a)*b|.

In the animtion P is colored yellow, z1, z2, z3 are colored pink.

To show that I'm not cheating, I choosed a, b and draw the ellipse using the
expression as above, then solve the cubic equation B(z) = P for P moving along
the circle and draw the triangle ABC. You can see the triangle does circumscribe
the ellipse!

This result generalizes to Blaschke product of degree n, the curve that the n-gon
circumscribes is a Poncelet curve.

*/
#define PI 3.141592653

vec3 rotHue(vec3 p, float a) {
    vec2 cs = sin(vec2(1.570796, 0) + a);

    mat3 hr = mat3(0.299,  0.587,  0.114,  0.299,  0.587,  0.114,  0.299,  0.587,  0.114) +
              mat3(0.701, -0.587, -0.114, -0.299,  0.413, -0.114, -0.300, -0.588,  0.886) * cs.x +
              mat3(0.168,  0.330, -0.497, -0.328,  0.035,  0.292,  1.250, -1.050, -0.203) * cs.y;

    return clamp(p*hr, 0., 1.);
}


vec2 cmul(vec2 p, vec2 q) {
    return vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x);
}


vec2 cdiv(vec2 z, vec2 w) {
    return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

vec2 csqrt(vec2 p) {
    float a = atan(p.y, p.x) / 2.;
    return vec2(cos(a), sin(a)) * sqrt(length(p));
}

vec2 ccubrt(vec2 z) {
    float r = length(z);
    r = pow(r, 1./3.);
    float angle = atan(z.y, z.x);
    return r * vec2(cos(angle / 3.0), sin(angle / 3.0));
}

vec2 cpow2(vec2 z) {
    return cmul(z, z);
}

vec2 cpow3(vec2 z) {
    return cmul(z, cmul(z, z));
}

void solveCubic(float b, vec2 k, inout vec2 z1, inout vec2 z2, inout vec2 z3) {
    vec2 t1 = vec2(2.*b*b*b, 0) - 9.*b*b*k;
    vec2 t2 = cpow2(t1 + 27.*k);
    vec2 t3 = 3.*b*k - vec2(b*b, 0);
    vec2 t4 = 4.*cpow3(t3);
    vec2 s1 = ccubrt(csqrt(t4 + t2) + 27.*k + t1);
    vec2 s2 = cdiv(t3, s1);
    z1 = 0.264566842 * s1 - 0.41997368 * s2 + vec2(b/3.,0.);
    z2 = cmul(vec2(-0.132283421, 0.2291216), s1) + cmul(vec2(0.209998684, 0.36371), s2) + vec2(b/3., 0);
    z3 = cmul(vec2(-0.132283421, -0.2291216), s1) + cmul(vec2(0.209998684, -0.36371), s2) + vec2(b/3., 0);
}

// distance from a 2d point p to a 2d segment (a, b)
float dseg(vec2 p, vec2 a, vec2 b) {
    vec2 v = b - a;
    p -= a;
    float t = clamp(dot(p, v)/dot(v, v), 0., 1.);
    return length(p - t * v);
}

vec3 cubehelix(vec3 c) {
    vec2 sc = vec2(sin(c.x), cos(c.x));
    return c.z * (1.0 + c.y * (1.0 - c.z) * (
        sc.x * vec3(0.14861, 0.29227, -1.97294) +
        sc.y * vec3(1.78277, -0.90649, 0.0)
        ));
}

vec3 rainbow(float t) {
    return cubehelix(vec3(
        2. * PI * t - 1.74533,
        (0.25 * cos(2. * PI * t) + 0.25) * vec2(-1.5, -0.9) + vec2(1.5, 0.8)
        ));
}

// iq's triangle signed distance function
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 ) {
    vec2 e0 = p1 - p0;
    vec2 e1 = p2 - p1;
    vec2 e2 = p0 - p2;

    vec2 v0 = p - p0;
    vec2 v1 = p - p1;
    vec2 v2 = p - p2;

    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

    return -sqrt(d.x)*sign(d.y);
}

// ellipse signed distance function
float sdEllipse( vec2 p, vec2 cen, in vec2 ab ) {
    p -= cen;
    vec2 pab = p / (ab * ab);
    return (0.5 * dot(pab, p) - 0.5) / length(pab);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float sf = 2. / iResolution.y;
    vec2 p = uv * 1.1;

    mat2 foci;
    float b = sin(iTime) * 0.3 + 0.6;
    // for a fixed ellipse, you can see each point on the unit cicle is a Poncelet point.
    //float b = 0.6;
    foci[0] = vec2(0);
    foci[1] = vec2(b, 0);

    vec2 k = vec2(cos(iTime), sin(iTime));
    vec2 A, B, C;
    solveCubic(b, k, A, B, C);

    // distance to the triangle
    float dtri = sdTriangle(p, A, B, C);
    float dcirc = length(p) - 1.;

    vec2 cen = vec2(b*0.5, 0);
    vec2 ab = vec2(0.5, sqrt(1.-b*b)/2.);
    float dellipse = sdEllipse(p, cen, ab);
    float dfoci = min(length(p), length(p - foci[1])) - 0.04;

    float sgn = dellipse < 0. ? -2. : (dtri < 0. ?  0. : 1.);

    vec3 col = vec3(0.4, 0.7, 0.4) - sgn*vec3(0.2, 0., 0.2);
    col = rotHue(col, sgn + 5.);
    const float numlines = 30.;
    for (float i=0.; i<numlines; i+=1.0) {
        vec2 Q = vec2(cos(i/numlines*2.*PI), sin(i/numlines*2.*PI));
        vec2 z1, z2, z3;
        solveCubic(b, Q, z1, z2, z3);
        float ds = dseg(p, z1, z2);
        ds = min(ds, dseg(p, z2, z3));
        ds = min(ds, dseg(p, z3, z1));
        col = mix(col, rainbow(i/numlines), 1.-smoothstep(0., 0.003+2.*sf, ds));
    }
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dtri)-0.01);
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dcirc)-0.01);
    col *= smoothstep(0.005, 0.005+2.0*sf, abs(dellipse)-0.005);
    col = mix(col, vec3(0.91, 0.1, 0.1), 1.-smoothstep(0., 0.005+2.*sf, dseg(p, foci[0], foci[1])-0.01));

    // decorate the vertices and foci
    mat4x2 verts; verts[0] = A; verts[1] = B; verts[2] = C, verts[3] = k;
    float lw = 0.02;
    for (int i=0; i<4; i++) {
        float dv = length(p - verts[i]) - .08;
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dv))*.5);
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv));
        if (i < 3)
            col = mix(col, vec3(1, .5, .8), 1. - smoothstep(0., sf, dv + lw*1.6));
        else
            col = mix(col, vec3(1, 1., .2), 1. - smoothstep(0., sf, dv + lw*1.6));
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv + .08 - lw));
    }
    for (int i=0; i<2; i++) {
        float dv = length(p - foci[i]) - .06;
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dv))*.5);
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv));
        col = mix(col, vec3(.4, .6, 1.), 1. - smoothstep(0., sf, dv + lw*1.2));
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, dv + .065 - lw));
    }

    col *= 1.25 - 0.4*length(p);

    fragColor = vec4(sqrt(max(col, 0.)), 1.0);
}

/*
Wythoff reflective polyhedra, code adapted from

    https://www.shadertoy.com/view/tlX3WH

I basically rewrote the polyhera construction and raytracing part there,
and kept the rendering part untouched.

The code should look simpler now?

To play with:

1. Change pqr and truncation_type to see other polyhedron.
2. use 'INSIDE_VIEW' to view from the inside.
3. use 'DUAL' to view the dual polyhera.
   pqr=(2, 3, 4) and truncation_type=(0, 0, 1) will give the
   rhombic dodecahedron that tiles the whole inner space.
*/
#define PI                3.141592654
#define L2(x)             dot(x, x)
#define EDGE_THICKNESS    0.05
#define MAX_TRACE_STEPS   128
#define MAX_RAY_BOUNCES   12
#define EPSILON           1e-4
#define FAR               20.
#define min3(x, y, z)     (min(x, min(y, z)))
#define max3(x, y, z)     (max(x, max(y, z)))


//#define INSIDE_VIEW

//#define DUAL

const vec3 pqr = vec3(2, 3, 5);   // (2, 3, 3), (2, 3, 4)
const vec3 truncation_type = vec3(1, 1, 1);

const float size = 1.35;  // polyhera size

mat3 M;  // normals of reflection mirrors
mat3 T;  // three vertices of the fundamental triangle  
vec3 v0; // initial vertex

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

void init() {
    vec3 c = cos(PI / pqr);
    float sp = sin(PI / pqr.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x*c.z)/sp;
    float z3 = sqrt(1.0 - x3*x3 - y3*y3);
    vec3 m3 = vec3(x3, y3, z3);
    M = mat3(m1, m2, m3);
    T[0] = normalize(cross(m2, m3));
    T[1] = normalize(cross(m3, m1));
    T[2] = normalize(cross(m1, m2));
    v0 = normalize(truncation_type * inverse(M)) * size;
#ifdef DUAL
    #define proj(p, n)  (length(p - dot(p, n) * n))
    float scale = min3(proj(v0, m1), proj(v0, m2), proj(v0, m3));
    v0 /= scale;
#endif
}

vec3 fold(vec3 p) {
    for (int i = 0; i < 5; i++)
    for (int j = 0; j < 3; j++) {
        p -= 2. * min(dot(p, M[j]), 0.) * M[j];
    }
    return p;
}

float dSegment(vec3 p, vec3 a, vec3 b) {
  vec3 pa = p - a;
  vec3 ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  float d = length(pa - ba * h);
  return d;
}

vec3 distEdges(vec3 p) {
    p = fold(p);
#ifdef DUAL
	vec3 ed = vec3(1e5);
    vec3 a = T[0] / dot(T[0], v0) * size;
    vec3 b = T[1] / dot(T[1], v0) * size;
    vec3 c = T[2] / dot(T[2], v0) * size;
	if(bool(truncation_type.x))
        ed.x = dSegment(p, b, c);
	if(bool(truncation_type.y))
        ed.y = dSegment(p, c, a) ;
	if(bool(truncation_type.z))
        ed.z = dSegment(p, a, b);
	return ed;
#else
    p -= v0;
    vec3 ed;
    for (int i = 0; i < 3; i++) {
        ed[i] = L2(p - min(0., dot(p, M[i])) * M[i]);
    }
	return sqrt(ed);
#endif
}

float map(vec3 p) {
    p = fold(p);
#ifdef DUAL
    return dot(p, v0) - size;
#else
    p -= v0;
    return max(dot(p, T[0]), max(dot(p, T[1]), dot(p, T[2])));
#endif
}

float trace(vec3 pos, vec3 rd, bool outside) {
    float t = 0.0;
    float sgn = outside ? 1.0 : -1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        float d = map(pos + t * rd);
        if (abs(d) < EPSILON)
            return t;
        if (t > FAR)
            break;
        t += sgn * d  * 0.9;
    }
    return FAR;
}

vec4 wallColor(vec3 dir, vec3 nor, vec3 eds) {
    float d = min3(eds.x, eds.y, eds.z);

    // Texturing of walls
    vec3 albedo = texture(iChannel1, vec2(eds.xy * 2.0)).rgb;
    albedo = pow(albedo, vec3(2.2)) * 0.5;

    // Simple diffuse lighting
    float lighting = 0.2 + max(dot(nor, vec3(0.8, 0.5, 0.0)), 0.0);

    if (dot(dir, nor) < 0.0) {
        // Outer walls, just add a black line to hide seams
        float f = clamp(d * 1000.0 - 3.0, 0.0, 1.0);
        albedo = mix(vec3(0.01), albedo, f);
        return vec4(albedo * lighting, f);
    } else {
        // Inner walls, add fancy lights
        float m = max3(eds.x, eds.y, eds.z);
        vec2 a = fract(vec2(d, m) * 40.6) - 0.5;
        float b = 1.0 - sqrt(dot(a, a));
        b = 0.2 / (dot(a, a) + 0.2);

        float lightShape = 1.0 - clamp(d * 100.0 - 2.0, 0.0, 1.0);
        lightShape *= b;

        vec3 emissive = vec3(3.5, 1.8, 1.0);
        return vec4(mix(albedo * lighting, emissive, lightShape), 0.0);
    }
	return vec4(1.0);
}


mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

vec3 get_normal( in vec3 pos ) {
    vec3 eps = vec3(0.001,0.0,0.0);
	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

vec3 background(vec3 dir) {
	vec3 col = texture(iChannel0, dir).rgb;
    col = pow(col, vec3(2.2));
    float origLuma = dot(col, vec3(0.2126, 0.7152, 0.0722)) * 0.7;
    return 2.5 * col / (1.0 - origLuma);
}

vec3 drawRay(vec3 pos, vec3 rd) {
    vec3 color = vec3(0.0);
#ifndef INSIDE_VIEW
    float t = trace(pos, rd, true);
    if (t == FAR) {
    	return background(rd);
    }
    pos = pos + t * rd;
    vec3 nor = get_normal(pos);
    vec3 reflDir = reflect(rd, nor);
    vec3 bgColor = pow(background(reflDir), vec3(1.0));
    float fresnel = 0.04 + 0.96 * pow(1.0 - max(dot(rd, -nor), 0.0), 5.0);
    color += bgColor * fresnel;
    vec3 eds = distEdges(pos);
    float d = min3(eds.x, eds.y, eds.z);
    if (d < EDGE_THICKNESS) {
        vec4 wc = wallColor(rd, nor, eds);
    	return color * wc.a + wc.rgb;
    }
#endif
    vec3 transmittance = vec3(1.0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        float t = trace(pos, rd, false);
        pos = pos + t * rd;
        vec3 eds = distEdges(pos);
        vec3 nor = get_normal(pos);
    	float d = min3(eds.x, eds.y, eds.z);
        if (d < EDGE_THICKNESS) {
        	return color + transmittance * wallColor(rd, nor, eds).rgb;
        }
        rd = reflect(rd, nor);
        pos += rd * 0.005;
        transmittance *= vec3(0.4, 0.7, 0.7);
    }

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init();
    vec2 uv = (fragCoord.xy - iResolution.xy*0.5) / iResolution.y;
    vec2 move = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);

#ifdef INSIDE_VIEW
    float radius = 0.1;
#else
    float radius = 2.0;
#endif
    vec3 eye = radius * vec3(
        cos(move.x)*cos(move.y),
        sin(move.y),
        sin(move.x)*cos(move.y)
    );

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.)
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
    eye = rotateY(mouse.x) * rotateX(-mouse.y) * eye;

    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    vec3 ray = normalize(uv.x*right + uv.y*up + forward * 1.0);
    vec3 color = drawRay(eye, ray);
    color = color / (color * 0.5 + 0.5);
    color = pow(color, vec3(1.0 / 2.2));

    fragColor = vec4(color,1.0);
}

/*

Winding number theorem:

A man is walking his dog along closed paths represented by f(z) and g(z),
respectively. If the man holds the leash tight enough to prevent the dog
from touching the tree throughout their walk, they will circle the tree the
same number of times when they return to their initial positions.

Rouch's theorem:

When f and g are analytic functions in a region that encloses the unit circle S
and |f(z)| > |f(z) - g(z)| whenever z in S (here |f-g| is the length of the leash),
then f(S) and g(S) will circle the origin the same number of times, and this number
equals the number of their zeros inside S.
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord - iResolution.xy*.5) / iResolution.y;

    vec2 p = uv * 9.;
    p -= vec2(1.5, .5); // adjust the postion of the curves
    vec2 O = vec2(0.5);

    // Smoothing factor.
    float sf = 8./iResolution.y;

    // background color
    vec3 col = vec3(1, .92, .75);
    float hatch = doHatch(uv, iResolution.y);
    col *= hatch*.2 + .8;

    vec2 e = vec2(.015, .03);
    float le = length(e);

    // curve width
    float lw = .02;

    float dc1 = dcurve(p, 0);
    float dc2 = dcurve(p, 1);

    float tA = mod(iTime*.2, 2.*PI);
    float tB = max(0., tA + sin(iTime) * .1 - .1);
    vec2 A0 = map1(0.), B0 = map2(0.);

    vec2 A = map1(tA);
    vec2 B = map2(tB);
    float dA = length(p - A) ;
    float dB = length(p - B) ;

    // dash grid lines
    vec2 p1 = uv * 6.;
    p1 -= floor(p1) + 0.5;
    float bord = max(abs(p1.x), abs(p1.y))-0.49;
    vec2 q1 = abs(mod(p1, 1./8.) - .5/8.);
    float lines = (min(q1.x, q1.y) - .5/8./3.);
    bord = min(bord, lines);
    bord = step(0., bord);

    float dlink = dSegment(p, A, B);
    // noisy background pattern
    col *= fbm(p*48.)*.4 + .6;
    // draw the grid lines
    col = mix(col, vec3(0), bord*.8);

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*16., dc2))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*8., dc2 - lw*2.4));
    col = mix(col, colorB, (1. - smoothstep(0., sf*4., dc2 - lw*1.6))*.8);

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*16., dc1))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*8., dc1 - lw*2.4));
    col = mix(col, colorA, 1. - smoothstep(0., sf*4., dc1 - lw*1.6));

    col = mix(col, vec3(0), (1. - smoothstep(0., sf*.7, dlink - 0.005))*.5);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf*.7, dlink - .008));
    col = mix(col, vec3(1, 1, .3), 1. - smoothstep(0., sf*.7, dlink));

    lw *= 12.;

    dA -= lw;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dA - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dA));
    dA += .12;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA - .03));
	col = mix(col, vec3(1, .8, .6), 1. - smoothstep(0., sf, dA)); 
    dA += .08;
	col = mix(col, vec3(0), (1. - smoothstep(0., sf, dA))); 

    dB -= lw;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dB - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB - 0.04));
	col = mix(col, colorB, 1. - smoothstep(0., sf, dB));
    dB += .12;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB - .03));
	col = mix(col, vec3(1, .8, .6), 1. - smoothstep(0., sf, dB)); 
    dB += .08;
	col = mix(col, vec3(0), (1. - smoothstep(0., sf, dB)));

    float dA0 = length(p - A0);
    dA0 -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dA0 - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA0 - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dA0));
    dA0 += .06;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dA0));

    float dB0 = length(p - B0);
    dB0 -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dB0 - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB0 - 0.04));
	col = mix(col, colorB, 1. - smoothstep(0., sf, dB0));
    dB0 += .06;
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dB0));

    vec2 q = p - O;
    float ci = length(q) - .7;
    float sh = max(.75 - ci*4., 0.);
    col = mix(col, vec3(0), (ss(sf*6., ci - .04))*.5);
    col = mix(col, vec3(0), ss(sf, ci));
    col = mix(col, vec3(1, .7, .4)*(ci + sh*sh*.1 + .5), ss(sf, ci + .03));
    col = mix(col, col*1.6, ss(sf*4., ci + .15));
   	col = mix(col, vec3(0), ss(sf, abs(ci + .1) - .01));   
    col = mix(col, vec3(0), (ss(sf, length(q) - .18))*.5);
    col = mix(col, vec3(0), ss(sf, abs(length(q) - .12) - .01));
    col = mix(col, vec3(0), ss(sf, length(q) - .05));

    A -= O;
    B -= O;
    vec2 qA = rot2(atan(A.y, A.x-.5) -PI/2.) * q;
    vec2 qB = rot2(atan(B.y, B.x)-PI/2.) * q;
    float indA = distLine(qA - vec2(0, -.005), qA - vec2(0, .46)) - .01;
    float indB = distLine(qB - vec2(0, -.005), qB - vec2(0, .3)) - .01;

    const float rad = .45;
    const float aNum = 12.;
    q = rot2(3.14159/aNum)*q;
    float a = atan(q.y, q.x);
    float ia = floor(a/6.283*aNum) + .5; // .5 to center cell.
    ia = ia*6.283/aNum;
    q = rot2(ia)*q;
    q.x -= rad;

    // Markings.
    float mark = sBox(q, vec2(.04, .022), 0.);
    col = mix(col, vec3(.5), ss(sf, mark - .015));
    col = mix(col, vec3(0), ss(sf, mark));

    // Indicator.        
    col = mix(col, vec3(0), ss(sf, indA - .025));
    col = mix(col, colorA, ss(sf, indA));
    col = mix(col, vec3(0), ss(sf, indB - .025));
    col = mix(col, colorB, ss(sf, indB));

    p -= vec2(-7.5, -3.);
    p *= .8;
    float ds1 = min(abs(length(p) - 1.), sqrt(dSegment(p, vec2(0), vec2(1, 0))));
    col = mix(col, vec3(0.2), ss(sf, ds1 - 0.025));
    float dr = distRoots(p);
    col = mix(col, vec3(0.5, 0, 0), ss(sf, dr - .05));
    col = mix(col, vec3(0.2), ss(sf, length(p - vec2(1, 0)) - .1));

    vec2 P = vec2(cos(tA), sin(tA));
    float dP = length(p - P);

    dP -= lw * .5;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., dP - 0.02))*.75);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, dP - 0.04));
	col = mix(col, colorA, 1. - smoothstep(0., sf, dP));

    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    col *= pow(16.*(1. - uv.x)*(1. - uv.y)*uv.x*uv.y, 1./16.)*1.05;

    // Output to screen
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

#define NUM_SEGMENTS  100
#define PI            3.141592653
#define ss(a, b)      (1. - smoothstep(0., a, b))
#define colorA   vec3(1, 0.2, 0.2)
#define colorB   vec3(0.3, 1., .6)


mat2 rot2(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, -s, s, c);
}

// complex multiplication
vec2 cmul(vec2 p, vec2 q) {
    return vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x);
}

// complex division
vec2 cdiv(vec2 z, vec2 w) {
    return vec2(z.x * w.x + z.y * w.y, -z.x * w.y + z.y * w.x) / dot(w, w);
}

vec2 transform(vec2 z, vec2 a) {
    return cdiv(z - a, vec2(1, 0) - cmul(vec2(a.x, -a.y), z));
}

float n2D(vec2 p) {
    const vec2 s = vec2(1, 113);
    vec2 ip = floor(p); p -= ip;
    vec4 h = vec4(0., s.x, s.y, s.x + s.y) + dot(ip, s);
    p = p*p*(3. - 2.*p);
    h = fract(sin(h)*43758.5453);
    h.xy = mix(h.xy, h.zw, p.y);
    return mix(h.x, h.y, p.x);
}

float fbm(vec2 p) {
    return n2D(p)*.533 + n2D(p*2.)*.267 + n2D(p*4.)*.133 + n2D(p*8.)*.067; }

float distLine(vec2 a, vec2 b) {
    b = a - b;
    float h = clamp(dot(a, b)/dot(b, b), 0., 1.);
    return length(a - b*h);
}

float sBox(vec2 p, vec2 b, float r) {
    vec2 d = abs(p) - b + r;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - r;
}

float dot2(in vec2 v) {
    return dot(v, v);
}

float dSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return dot2(pa - ba*clamp(dot(pa,ba) / dot(ba,ba), 0.0, 1.0));
}

vec2 map1(float t) {
    const float s = 2.;
    vec2 p = vec2(cos(t), sin(t));
    vec2 z = transform(p, vec2(.5, .7));
    z = cmul(z, transform(p, vec2(-.6, 0.5)));
    z = cmul(z, transform(p, vec2(-0.4)));
    z = cmul(z, p - vec2(s, s));
    return z;
}

float distRoots(vec2 p) {
    float d = length(p - vec2(.5, .7));
    d = min(d, length(p - vec2(-0.6, 0.5)));
    d = min(d, length(p - vec2(-0.4)));
    return d;
}

vec2 map2(float t) {
    return map1(t) + vec2(cos(t) * 1.6, sin(t) * 1.2);
}

float dcurve(vec2 p, int index) {
    float h = 0.05;
    float t = 0.0;

    vec2  a = index == 0 ? map1(t) : map2(t);
    float d = dot2(p - a);

    for(int i = 0; i < NUM_SEGMENTS; i++) {
        vec2  b = index == 0 ? map1(t) : map2(t);
        d = min(d, dSegment(p, a, b));
        t += clamp(0.01*length(a-p)/length(a-b), 0.01, 0.15);
        a = b;
    }
    return sqrt(d);
}

float hash21(vec2 p) {
    return fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453);
}

float doHatch(vec2 p, float res) {
    p *= res/16.;
    float hatch;
    float hRnd = hash21(floor(p*6.) + .73);
    if(hRnd>.66) hatch = hRnd;
    else if (hRnd > 0.33) hatch = clamp(sin((p.x - p.y)*3.14159*200.)*2. + .5, 0., 1.);
    else hatch = clamp(sin((p.x + p.y)*3.14159*200.)*2. + .5, 0., 1.);
    return hatch;
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Uniform honeycombs and their duals
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

This program draws uniform honeycombs and their duals in 3D Euclidean space.

You can learn more about these honeycombs on the Wikipedia page for convex
uniform honeycombs:

    https://en.wikipedia.org/wiki/Convex_uniform_honeycomb

The main rendering procedure is based on the work of @shane:

    https://www.shadertoy.com/view/4l2cD3.

I particularly like the metallic effects in many of his examples.

The honeycomb data is taken from @mla's work at

    https://www.shadertoy.com/view/WsfcRn.

I'm also grateful to @mla for helpful discussions.

@mla's code is a great reference for the general Wythoff construction,
which saved me a lot of hard (and even boring) computations.
If you have any difficulty understanding this code, I recommend referring to
@mla's version, as the ideas are the same.

The trickiest part of this program is to render the octagonal edges
(or other prism-like shapes) instead of just cylinder edges.
This is due to the fact that Wythoff construction may change the distance
betwwen a point and the cell edges: In the Wythoff construction, one need to
reflect a point P across the mirrors repeatly until it falls within a certain
region, known as the "fundamental domain". Say its final location in the fundamental
domain is Q. In the cases that edges have prism-like shapes, the distances d(P, edges)
and d(Q, edges) may not be equal. This results in annoying artifacts in the final image.
It took me 90% of the time to figure out how to tackle this. My solution is
brute-force: just render more copies of the fundamental domain. This made the
code look ugly and the program take a bit long time to compile, but it worked and
looked right in the end.
*/

// Honeycomb type. 0 for affine A3, 1 for affine B3, 2 for affine C3
#define LATTICE   1

// Barycentric coordinates of the initial vertex v0 in the fundamental tetrahedron.
// This determines the truncation type of the honeycomb.
const vec4 T = vec4(1, 1, 1, 0);

// use another color style
#define GOLD_STYLE

// render the dual honeycomb?
//#define DUAL


// initial vertex
vec3 v0;

// M is the four reflection mirrors of the fundamental tetrahedron,
// M_inv is the inverse of M.
mat4 M, M_inv;

// The four vertices of the fundamental tetrahedron and the four mirror images of v0 about
// the four mirrors
mat4x3 V, E;

const float tbsize = 0.055;

// object id
vec3 objIDs;

float glow;

vec3 do_reflect(vec3 p, vec4 n) {
    vec4 q = vec4(p, 1.);
    return p - 2. * dot(q, n) * n.xyz;
}

vec3 rA(vec3 p) { return do_reflect(p, M[0]); }
vec3 rB(vec3 p) { return do_reflect(p, M[1]); }
vec3 rC(vec3 p) { return do_reflect(p, M[2]); }
vec3 rD(vec3 p) { return do_reflect(p, M[3]); }


void init() {

    const float s = 0.70710678;    // sqrt(2) / 2

#if LATTICE == 0
    // affine A3 lattice
    //
    // A     C
    //  o---o
    //  |   |
    //  o---o
    // D     B
    M = mat4(vec4(-s, -s, 0, 2.*s),
             vec4(s, -s, 0, 0),
             vec4(0, s, -s, 0),
             vec4(0, s, s, 0));

    V = mat4x3(vec3(0, 0, 0),
               vec3(2, 0, 0),
               vec3(1, 1, -1),
               vec3(1, 1, 1));

#elif LATTICE == 1
    // affine B3 lattice
    //          C
    //         o
    //     4  /
    // A o---o B
    //        \
    //         o
    //          D
    M = mat4(vec4(0, 0, 1, 0),
             vec4(0, s, -s, 0),
             vec4(s, -s, 0, 0),
             vec4(-s, -s, 0, 2.*s));

    V = mat4x3(vec3(1, 1, 1),
               vec3(1, 1, 0),
               vec3(2, 0, 0),
               vec3(0, 0, 0));

// affine C3 lattice
#else
    //   4       4
    // o---o---o---o
    // A   B   C   D
    M = mat4(vec4(0, 0, 1, 0),
             vec4(0, s, -s, 0),
             vec4(s, -s, 0, 0),
             vec4(-1, 0, 0, 1));

    V = mat4x3(vec3(0, 0, 0),
               vec3(1, 0, 0),
               vec3(1, 1, 0),
               vec3(1, 1, 1));

#endif

    M_inv = inverse(M);
    vec4 v4 = T * M_inv;
    v4 /= v4.w;
    v0 = v4.xyz;
    for (int i = 0; i < 4; i++) {
        E[i] = v0 - 2. * dot(v4, M[i]) * M[i].xyz;
    }
}


#define FAR     80.
#define PI      3.141592654

float sgn(float x) { return x > 0. ? 1. : -1.; }

// Camera path as a function of time t.
// I think it's unlikely to devise an universal path that flies through all
// honeycombs without hitting the lattice.
vec3 camPath(float t) { return vec3(t, 0, 0); }

// A mirror is active if and only if the initial vertex v0 is not on it,
// or equivalently, the barycentric coordinate with respect to this mirror is non-zero.
bool isActive(int k) { return T[k] != 0.0; }

// try to reflect a point p about mirror with normal `n`, if p and the fundamental domain
// are on the different sides of the mirror
vec4 try_reflect(vec4 p, vec4 n, inout int flips) {
    float k = dot(p, n);
    if (k < 0.) {
        flips += 1;
        p.xyz -= 2. * k * n.xyz;
    }
    return p;
}

vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }


// fold a point p into the fundamental domain.
// we return the final position of p, but also track an intermediate state of p for
// handling dual cases.
vec3 fold(inout vec3 p, inout int flips) {

    vec4 q;

#if LATTICE == 0

    p = mod(p + 2., 4.) - 2.;
    q = vec4(p, 1);
    for (int i = 0; i < 4; i++) {
        for (int j = 0; j < 4; j++) {
            q = try_reflect(q, M[j], flips);
        }
    }

#elif LATTICE == 1

    p = mod(p + 2., 4.) - 2.;
    flips += int(p.x < 0.0) + int(p.y < 0.0) + int(p.z < 0.0);
    p = abs(p);
    q = vec4(p, 1);
    for (int i = 0; i < 2; i++) {
        q = try_reflect(q, M[3], flips);
        q = try_reflect(q, M[2], flips);
        q = try_reflect(q, M[1], flips);
    }

#else

    p = mod(p + 1., 2.) - 1.;
    flips += int(p.x < 0.0) + int(p.y < 0.0) + int(p.z < 0.0);
    p = abs(p);
    q = vec4(p, 1.);
    q = try_reflect(q, M[1], flips);
    q = try_reflect(q, M[2], flips);
    q = try_reflect(q, M[1], flips);

#endif

    return q.xyz;
}


// Shane's tex3D function
vec3 tex3D(sampler2D t, in vec3 p, in vec3 n) {
    n = max(abs(n), 0.001);
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}

// I hacked this function to rotate any edge (a, b) to z-axis
mat3 rotAxis(vec3 n) {
    n = normalize(n);
    vec3 x;
    if (n.x == 0.)
        x = vec3(1, 0, 0);
    else
        x = normalize(vec3(-n.y, n.x, 0.));
    vec3 y = cross(n, x);
    return mat3(x, y, n);
}


float tube(vec2 p, float sc, float rad) {
    return max(max(p.x, p.y), (p.x + p.y)*sc) - rad;
}


// draw an edge with two ends at a, b.
vec3 dSegment(vec3 p, vec3 a, vec3 b) {
    // set local origin at middle point of the edge
    vec3 m = (a + b) / 2.;
    p -= m;
    b -= m;
    a -= m;
    vec3 h = (b - a) / 2.;
    float L = length(h); // length of half the edge

    p = p * rotAxis(h);  // reorient the edge along with z-axis
    p = abs(p);

    // the main tube, currently this has infinite length
    float tb;
    tb = tube(p.xy, 0.75, tbsize);

    float band = 1e5;
    float innerTb = 1e5;

    // add a band of 1/4 length of the edge
    band = max(tb - 0.0075, p.z - L/4.);

    // trick: rotate p to make two smaller tubes
    // remove them from the main tube to make the holes
    float psize = tbsize  / 1.5;
    vec2 peg = vec2(tube(p.xz, .64, psize), tube(p.yz, .64, psize));
    float hole = min(peg.x, peg.y);

    // use planes to cut the band
    float dp1 = max(peg.x, p.y - tbsize - .0095); dp1 = max(dp1, p.y - L / 4.);
    float dp2 = max(peg.y, p.x - tbsize - .0095); dp2 = max(dp2, p.x - L / 4.);
    band = min(band, min(dp1, dp2));

    // make holes on the main tube
    tb = max(tb, -(hole - .015));

    // cut the infinite main tube at the two ends
    tb = max(tb, p.z - L);

    // make holes on the band
    band = max(band, -(hole + .0125));

    // use sphere to create a fake inner tube
    innerTb = length(p) - tbsize;

    return vec3(tb, band, innerTb);
}

// This function is only used for handling dual of A3 honeycombs.
// verts[4] are four non-adjacent vertices of a cube, so each face of the cube
/// contains exactly one pair of vertices in verts[4].
// For p a point in the cube, we find this pair of vertices so that p is nearest to
// the face contains this pair among all six faces.
void findVertexPair(vec3 p, vec3 verts[4], out int i1, out int j1) {
    float d = 1e5;
    float d1;
    for (int i = 0; i < 4; i++) {
        for (int j = i+1; j < 4; j++) {
            // (verts[i] + verts[j]) / 2. is the center of the face since they form a diagonal pair.
            d1 = length(p - (verts[i] + verts[j]) / 2.);
            if (d1 < d) {
                i1 = i;
                j1 = j;
                d = d1;
            }
        }
    }
}


float map(vec3 p) {
    vec3 dedge = vec3(1e5);
    vec3 p0 = p;
    int flips = 0;
    p = fold(p0, flips);

// For the usual uniform honeycomb just render the edges, that's safe since they lie inside
// the fundamental tetrahedron.
#ifndef DUAL

    for (int i = 0; i < 4; i++) {
         dedge = min(dedge, dSegment(p, v0, E[i]));
    }

#else
    /*
    Render the dual honeycomb.

    In the dual honeycomb two cells are adjacent if and only if they share a common face.

    For two mirrors A, B they generate a non-degenerate face if and only if either
    of below holds:

        1. A, B are perpendicular and they are both active
        2. A, B are not perpendicular and at least one of them is active

    For such a face we further check if {A, B, C} and {A, B, D} both generate non-degenerate cells.

    1. If {A, B, C} and {A, B, D} are both non-degenerate, (C, D) form an edge in the dual honeycomb.
    2. If {A, B, D} degenerates to a face, (D, 2*C-D) form an edge in the dual honeycomb.
    3. If both {A, B, C} and {A, B, D} are degenerate, no edge between C and D.
    */

    // The C3 honeycomb is the easiest, handle this case first.
    //   4       4
    // o---o---o---o
    // A   B   C   D
    #if LATTICE == 2
        // if either A or B is active, then {A, B} generate a face and {A, B, C} is a
        // non-degenerate cell. {A, B, D} is non-degenerate iff D is active.
        // Other procedures below are all similar.
        if (isActive(0) || isActive(1)) {
            if (isActive(3))
                dedge = min(dedge, dSegment(p, V[2], V[3]));
            else
                dedge = min(dedge, dSegment(p, V[3], 2.*V[2]-V[3]));
        }
        if (isActive(0) && isActive(2)) dedge = min(dedge, dSegment(p, V[1], V[3]));
        if (isActive(1) || isActive(2)) dedge = min(dedge, dSegment(p0, V[0], V[3]));
        if (isActive(0) && isActive(3)) dedge = min(dedge, dSegment(p, V[1], V[2]));
        if (isActive(1) && isActive(3)) dedge = min(dedge, dSegment(p, V[0], V[2]));
        if (isActive(2) || isActive(3)) {
            if (isActive(0))
                dedge = min(dedge, dSegment(p, V[1], V[0]));
            else
                dedge = min(dedge, dSegment(p, V[0], 2.*V[1]-V[0]));
        }

    #elif LATTICE == 1

        if (isActive(0) || isActive(1)) dedge = min(dedge, dSegment(p, V[2], V[3]));
        if (isActive(0) && isActive(2)) {
            if (isActive(3))
                dedge = min(dedge, dSegment(p, V[1], V[3]));
            else
                dedge = min(dedge, dSegment(p, V[3], 2.*V[1]-V[3]));
        }
        // trick: the edge (V[0], V[3]) does not lie inside the fundamental domain hence octahedral
        // shapes won't work. we render its 4 copies in the cube [0, 0, 0] x [2, 2, 2].
        if (isActive(1) || isActive(2)) {
            dedge = min(dedge, dSegment(p0, V[0], V[3]));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 2, 0)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 0, 2)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 2, 2)));
        }

        if (isActive(0) && isActive(3)) {
            if (isActive(2))
                dedge = min(dedge, dSegment(p, V[1], V[2]));
            else
                dedge = min(dedge, dSegment(p, V[2], 2.*V[1]-V[2]));
        }
        if (isActive(1) || isActive(3)) {
            dedge = min(dedge, dSegment(p0, V[0], V[2]));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 2, 0)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(0, 0, 2)));
            dedge = min(dedge, dSegment(p0, V[0], vec3(2, 2, 2)));
        }
        if (isActive(2) && isActive(3)) {
            if (isActive(1))
                dedge = min(dedge, dSegment(p, V[1], V[0]));
            else
                dedge = min(dedge, dSegment(p, V[0], 2.*V[1]-V[0]));
        } 

    #else
        // The duals of A3 honeycombs are the most difficult. Recall p0 is now in [-2, -2, -2] x [2, 2, 2].
        // We use a completely different way to render them. The idea is, vertices of the A3 regular honeycombs
        // are integer lattices points (i, j, k) with:
        // i + j + k = 0 mod 4 for V0
        // i + j + k = 2 mod 4 for V1
        // i + j + k = 1 mod 4 for V2
        // i + j + k = 3 mod 4 for V3

        // find the center of the 2x2x2 cube that p belongs to
        vec3 C = vec3(sgn(p0.x), sgn(p0.y), sgn(p0.z));

        float k = mod(C.x + C.y + C.z, 4.);
        // if k = 1 mod 4, then the center of this cube is congruent to V[2] of the fundamental tetrahedron.
        // else k = 3 mod 4 and the center is congruent to V[3].
        int type = (k == 1.) ? 2 : 3;

        vec3 V0[4];  // holds the vertices of this 2x2x2 cube that are congruent to V[0]
        vec3 V1[4];  // holds the vertices of this 2x2x2 cube that are congruent to V[1]

        if (type == 2) {
            V0[0] = C + vec3(1, 1, 1);
            V0[1] = C + vec3(-1, -1, 1);
            V0[2] = C + vec3(1, -1, -1);
            V0[3] = C + vec3(-1, 1, -1);
            V1[0] = C + vec3(1, -1, 1);
            V1[1] = C + vec3(-1, 1, 1);
            V1[2] = C + vec3(1, 1, -1);
            V1[3] = C + vec3(-1, -1, -1);
        }
        else {  // switch the role of V0, V1 above
            V1[0] = C + vec3(1, 1, 1);
            V1[1] = C + vec3(-1, -1, 1);
            V1[2] = C + vec3(1, -1, -1);
            V1[3] = C + vec3(-1, 1, -1);
            V0[0] = C + vec3(1, -1, 1);
            V0[1] = C + vec3(-1, 1, 1);
            V0[2] = C + vec3(1, 1, -1);
            V0[3] = C + vec3(-1, -1, -1);
        }

        int r0, s0, r1, s1;
        findVertexPair(p0, V0, r0, s0);
        findVertexPair(p0, V1, r1, s1);
        vec3 P0 = V0[r0] + V0[s0] - C;
        vec3 P1 = V1[r1] + V1[s1] - C;

        if (isActive(2) && isActive(3)) dedge = min(dedge, dSegment(p, V[0], V[1]));
        if (isActive(1) || isActive(3)) {
            if (type == 2) {
                dedge = min(dedge, dSegment(p0, C, V0[r0]));
                dedge = min(dedge, dSegment(p0, C, V0[s0]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P0, V0[r0]));
                dedge = min(dedge, dSegment(p0, P0, V0[s0]));
            }
        }
        if (isActive(1) || isActive(2)) {
            if (type == 3) {
                dedge = min(dedge, dSegment(p0, C, V0[r0]));
                dedge = min(dedge, dSegment(p0, C, V0[s0]));            
            }
            else {
                dedge = min(dedge, dSegment(p0, P0, V0[r0]));
                dedge = min(dedge, dSegment(p0, P0, V0[s0]));
            }
        }
        if (isActive(0) || isActive(3)) {
            if (type == 2) {
                dedge = min(dedge, dSegment(p0, C, V1[r1]));
                dedge = min(dedge, dSegment(p0, C, V1[s1]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P1, V1[r1]));
                dedge = min(dedge, dSegment(p0, P1, V1[s1]));
            }
        }
        if (isActive(0) || isActive(2)) {
            if (type == 3) {
                dedge = min(dedge, dSegment(p0, C, V1[r1]));
                dedge = min(dedge, dSegment(p0, C, V1[s1]));
            }
            else {
                dedge = min(dedge, dSegment(p0, P1, V1[r1]));
                dedge = min(dedge, dSegment(p0, P1, V1[s1]));
            }
        }
        if (isActive(0) && isActive(1)) dedge = min(dedge, dSegment(p, V[2], V[3]));

    #endif
#endif

    // store the object ids.
    objIDs = dedge;
    return min(dedge.x, min(dedge.y, dedge.z));
}


// This reduces the compiling time to about 7x times faster
vec3 calcNormal(vec3 p, inout float edge, float t) {
    float eps = 1./mix(400., iResolution.y, .5)*(1. + t*.5);
    float d = map(p);
    vec3 e = vec3(eps, 0, 0);
    vec3 da = vec3(-2.*d);
    for(int i = min(iFrame, 0); i < 3; i++ ) {
        for(int j = min(iFrame, 0); j < 2; j++)
            da[i] += map(p + e * float(1 - 2*j));
        e = e.zxy;
    }
    da = abs(da);

    edge = da.x + da.y + da.z;
    edge = smoothstep(0., 1., sqrt(edge/e.x*2.));

    vec3 n = vec3(0.0);
    for(int i=min(iFrame, 0); i<4; i++) {
        vec3 e = .57735*(2.*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1)) - 1.);
        n += e*map(p + .001*e);
    }
    return normalize(n);
}


float softShadow(vec3 ro, vec3 lp, float tmin, float tmax, float k) {
    const int maxShadeIterations = 32;
    vec3 rd = lp - ro;
    rd /= max(length(rd), 1e-4);
    float shade = 1.0;
    float t = tmin;
    for (int i = 0; i < maxShadeIterations; i++) {
        float h = map(ro + rd * t);
        t += clamp(h, 0.01, 0.2);
        shade = min(shade, smoothstep(0., 1., k * h / t));
        if (abs(h) < 1e-4 || t > tmax)
            break;
    }
    return min(max(shade, 0.) + .1, 1.);
}

float calcAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n);
        occ += (h - d) * sca;
        sca *= 0.7;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}


float trace(in vec3 ro, in vec3 rd) {
    glow = 0.;
    float ah;
    const float precis = 1e-3;
    float t = 0.0;
    for (int i = 0; i < 128; i++) {
        float h = map(ro + rd * t);
        ah = abs(h);
        glow += 1./(1. + ah*ah*8.);
        if (ah < (t * 0.125 + 1.) * precis || t > FAR)
            break;
        t += h;
    }
    return min(t, FAR);
}


vec3 transform(in vec3 p) {
    if (iMouse.x > 0.0) {
        float phi =  2.0*iMouse.x / iResolution.x * PI - PI;
        float theta = iMouse.y / iResolution.y * PI - 0.5*PI;
        p.xz = rot2d(p.xz, phi / 6.);
        p.yx = rot2d(p.yx, theta / 4.);

    }
    return p;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    init();

    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    vec3 up = vec3(0, 1, 0);
    vec3 ro = camPath(iTime * 1.1);
    vec3 lookat = camPath(iTime * 1.1 + 0.1);

    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, up));
    up = cross(right, forward);
    vec3 rd = normalize(uv.x * right + uv.y * up + forward * 2.);
    ro = transform(ro);
    rd = transform(rd);
    vec3 lp = ro + vec3(-1, 2, -1);
    vec3 col = vec3(0);

    float t = trace(ro, rd);

    float objID = (objIDs.x < objIDs.y && objIDs.x < objIDs.z) ? 0. : (objIDs.y < objIDs.z) ? 1. : 2.;

    if (t < FAR) {
        float ed;
        vec3 pos = ro + t * rd;
        vec3 nor = calcNormal(pos, ed, t);

        vec3 oCol;
        #ifndef GOLD_STYLE
        vec3 bCol = mix(vec3(1, .1, .5).zyx,
                        vec3(1, .3, .1).zyx,
                        dot(sin(pos*8. - cos(pos.yzx*4. + iTime*4.)), vec3(.166)) + .5);
        #else
        vec3 bCol = mix(vec3(1, .5, .1),
                        vec3(1, .1, .2),
                        dot(sin(pos*8. - cos(pos.yzx*4. + iTime*4.)), vec3(.166)) + .5);
        #endif
        if(objID < .5)
            oCol = mix(bCol, vec3(1), .97);

        else if (objID > 1.5)
            oCol = mix(bCol, vec3(1), .05) + bCol*2.;

        else
            oCol = mix(bCol, vec3(1.35), .97)*vec3(1.1, 1, .9);

        vec3 tx = tex3D(iChannel0, pos*2., nor);
        tx = smoothstep(.0, .5, tx)*2.;

        if(objID < 1.5)
            oCol *= tx;
        else
            oCol *= mix(vec3(1), tx, .5);

        float ao = calcAO(pos, nor);
        float sh = softShadow(pos + nor*.002, lp, 0.001, 16., t);

        vec3 ld = lp - pos;
        float dist = max(length(ld), 0.001);
        ld /= dist;

        float atten = 3./(1. + dist*0.05 + dist*dist*0.025);

        float diff = max(dot(ld, nor), 0.);
        if (objID < 1.5)
            diff = pow(diff, 4.)*2.;
        float spec = pow(max(dot( reflect(ld, nor), rd), 0.0 ), 32.0);

        col = oCol*(diff + .25) + mix(bCol.zyx, vec3(1, .7, .3), .5)*spec*4.;

        col += .015/max(abs(.05 - map(pos*1.5 + sin(iTime/6.))), .01)*oCol*mix(bCol, vec3(1, .8, .5), .35);

        // Adding a bit of glow. It was tempting to get my money's worth, but I kept it subtle. :)
        if(objID < 1.5)
            col += bCol*glow*.02;
        else
            col += bCol*glow*1.5;

        // Applying the dark edges, attenuation, shadows and ambient occlusion.
        col *= (1. - ed*.7);
        col *= atten*(sh + ao*.25)*ao;

    }

    float fog = 1./(1. + t*.125 + t*t*.01);
    col = mix(vec3(0), col, fog);//

    uv = fragCoord/iResolution.xy;
    col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .125);
	fragColor = vec4(sqrt(max(col, 0.)), 1);
}

/*
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
Compact and paracompact Hyperbolic honeycombs in H3 space
~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

This program draws compact/paracompact uniform hyperbolic honeycombs in
Poincar's 3D ball model. For a complete list of available honeycombs see

    "https://en.wikipedia.org/wiki/Uniform_honeycombs_in_hyperbolic_space"

Thanks @knighty for his fragmentarium code && help.

Some math stuff:

1. The 4D space R^4 is endowed with Minkowski inner product: for
   p=(x1, y1, z1, w1) and q=(x2, y2, z2, w2), their "hyperbolic dot" is given by

       hdot(p, q) := x1*y1 + x2*y2 * x3*y3 - x4*y4.

   This inner product has Sylvester type (3, 1), the quadratic form is Q(p) = hdot(p, p).

2. The hyperboloid are those vectors in R^4 with Q(p) = -1 (time-like). For those q with
   Q(q) > 0 we call q space-like. The hyperboloid consists of two sheets: w > 0 and w < 0.

3. The distance between two vectors p, q on the same sheet is given by

       cosh(d(p, q)) = -hdot(p, q).

4. Any geodesic line from a point p on the hyperboloid can be parameterized by

       p*cosh(t) + v*sinh(t),

   where v is a unit tangent vector at p (v must be space-like).

5. A reflection across a plane with normal `n` is given by

       ref(p) = p - 2 * hdot(p, n) * n.

   Here p is time-like and n is space-like.

6. We iteratively reflect a 4d point until it's in the fundamental domian, we get
   its distance to the initial vertex/mirrors, then use knighty's magic formula to
   estimate a `safe` distance that we can march in the 3d space. This is the most
   intriguing part in the code.


@mla has another honeycomb demo:

    https://www.shadertoy.com/view/XddyR2

@knighty's fragmentarium code:

    https://github.com/3Dickulus/Fragmentarium_Examples_Folder/tree/master/Knighty%20Collection/Hyperbolic-tesselations-named


~~~~~~~~~~~~~~~~~~~~~~~~~~~~
List of available honeycombs


***9 compact ones***

