// antialiasing level
#define AA    3
// view boundary near the infinite
#define FAR   0.998
// maximum number of raymarch steps
#define MAX_TRACE_STEPS  128

#define FOV   1.
#define fog   vec3(.19, .19, .86)
//#define fog   vec3(0.6, 0.8, 1)


// ===========
// !Important:
// ===========
// Due to the way we construct the mirrors, the first three mirrors must have spherical
// type, so for paracompact honeycombs like (4, 4, x), (6, 3, x), (3, 6, x), you need to
// swap the order of the last two mirrors, i.e. use (4, 2, 2, 2, 4, 4) or (6, 2, 2, 2, 3, x)
// in the Coxeter diagram. Suggested by @mla.
// For diagram = (a, b, c, d, e, f) the corresponding Coxeter matrix is
//
// | 1  a  b  c |
// | a  1, d  e |
// | b  d  1  f |
// | c  e  f  1 |
//
const float[6] diagram = float[6](5., 2., 3., 3., 2., 5.);

// active mirrors, 1 for active, 0 for non-active. This determines the position
// of the initial vertex hence the truncation type of the honeycomb.
const vec4 active_mirrors = vec4(1, 0, 0, 0);

// size of the vertex and segments
// you may need to adjust these for different scenes
const float vradius = 0.12;
const float sradius = 0.06;

// palette for the vertices and segments
const vec3 vcolor = vec3(0.71, 0.65, 0.26);
const vec3[4] ecolors = vec3[4](vec3(.90, .91, 0.98),
                                vec3(0.72, 0.45, 0.2),
                                vec3(0.32, 0.79, 0.36),
                                vec3(0.14, 0.42, 0.56));

vec3 chooseColor(float id) {
    return (id == 4.) ? vcolor : ecolors[int(id)];
}

vec2 map(vec3 p) {
    float r = length(p);
    vec4 q = vec4(2.*p, 1.+r*r) / (1.-r*r);
    bool found = fold(q, M);
    if (found) {
        float id;
        float dvert = dVertex(q, r);
        float dedge = dSegments(q, r, id);
        id = (dvert < dedge) ? 4. : id;
        return vec2(min(dvert, dedge), id);
    }
    return vec2(FAR, -1.0);
}

vec2 trace(vec3 ro, vec3 rd) {
    float t = 0.0;
    vec2 h;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        h = map(ro + rd * t);
        if (h.x < 1e-5 * (t * 1.25 + 1.))
            return vec2(t, h.y);
        if (t > FAR)
            break;
        t += min(h.x, 0.02);
    }
    return vec2(FAR, -1.0);
}

vec3 calcNormal(vec3 p, inout float edge, float t) {

    vec2 e = vec2(1./mix(400., iResolution.y, .5)*(1. + t*.5), 0);
    float d1 = map(p + e.xyy).x, d2 = map(p - e.xyy).x;
    float d3 = map(p + e.yxy).x, d4 = map(p - e.yxy).x;
    float d5 = map(p + e.yyx).x, d6 = map(p - e.yyx).x;
    float d = map(p).x*2.;

    edge = abs(d1 + d2 - d) + abs(d3 + d4 - d) + abs(d5 + d6 - d);
    edge = smoothstep(0., 1., sqrt(edge/e.x*2.));

    e = vec2(.002, 0);
    d1 = map(p + e.xyy).x, d2 = map(p - e.xyy).x;
    d3 = map(p + e.yxy).x, d4 = map(p - e.yxy).x;
    d5 = map(p + e.yyx).x, d6 = map(p - e.yyx).x;
    return normalize(vec3(d1 - d2, d3 - d4, d5 - d6));
}

float softShadow(vec3 ro, vec3 lp, float tmin, float tmax, float k) {
    const int maxShadeIterations = 24;
    float res = 1.0;
    float t = tmin;
    vec3 rd = lp - ro;
    rd /= max(length(rd), 1e-4);
    for (int i = 0; i < maxShadeIterations; i++) {
        float h = map(ro + rd * t).x;
        t += clamp(h, 0.005, 0.2);
        res = min(res, smoothstep(0., 1., k * h / t));
        if (abs(h) < 0.001 || t > tmax)
            break;
    }
    return clamp(res + 0.2, 0.0, 1.0);
}

float calcAO(vec3 p, vec3 n) {
    float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {
        float h = 0.01 + 0.15 * float(i) / 4.0;
        float d = map(p + h * n).x;
        occ += (h - d) * sca;
        sca *= 0.75;
    }
    return clamp(1.0 - occ, 0.0, 1.0);
}

float n3D(vec3 p) {
    const vec3 s = vec3(7, 157, 113);
    vec3 ip = floor(p);
    p -= ip;
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p * p * (3. - 2. * p);
    h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

vec3 envMap(vec3 p) {
    p *= 2.;
    float n3D2 = n3D(p*2.);
    float c = n3D(p)*.57 + n3D2*.28 + n3D(p*4.)*.15;
    c = smoothstep(0.5, 1., c);
    p = vec3(c*.8, c*.9, c);
    return mix(p.zxy, p, n3D2*.34 + .665);
}

vec3 render(vec3 ro, vec3 rd, vec3 lp) {
    vec3 col = vec3(0.);
    vec2 result = trace(ro, rd);
    float t = result.x;
    float id = result.y;
    if (id >= 0.) {
        float ed;
        vec3 pos = ro + rd * t;
        vec3 nor = calcNormal(pos, ed, t);
        vec3 oCol = chooseColor(id);

        float ao = calcAO(pos, nor);
        float sh = softShadow(pos + nor*.001, lp, 0.001, 2., t);

        vec3 ld = lp - pos;
        float dist = max(length(ld), 0.001);
        ld /= dist;

        float at = 2./(1. + dist*0.025 + dist*dist*0.05);

        float diff = max(dot(ld, nor), 0.);
        diff = pow(diff, 4.)*2.;
        float spec = pow(max(dot( reflect(-ld, nor), -rd), 0.0 ), 8.0);
        float fre = pow( clamp(dot(nor, rd) + 1., .0, 1.), 4.);
        vec3 env = envMap(reflect(rd, nor))*1.5;

        col += oCol * (diff + 0.25 + vec3(.8, .9, .97)*fre);
        col += oCol * env;
        col += vec3(.9, .95, .93)*spec;
        col *= (1. - ed*.7);
        col *= at*(sh + .25*ao)*ao;
    }
    vec3 bg = mix(fog*.2, fog*1.2, normalize(ro + rd*t).y*.5 + .5);
    col = mix(col, bg, 1. - exp(-t*t*t*0.2));
    return col;
}

void mouse(inout vec3 lookat) {
    vec2 m = (iMouse.xy / iResolution.xy) + .5;
    float rx = 2. *PI * m.x;
    float ry = - PI* m.y;
    lookat.xz = rot2d(lookat.xz, ry);
    lookat.yz = rot2d(lookat.yz, rx);
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
// -----------------
// setup the fundamental tetrahedron

    init(diagram, active_mirrors, M, v0);

    // cosh(vradius), sinh(vradius), cosh(sradius), sinh(sradius)
    cvr = cosh(vradius); svr = sinh(vradius);
    csr = cosh(sradius); ssr = sinh(sradius);


// ---------------
// view settings

    vec3 ro = vec3(0, 0, 0);
    vec3 up = vec3(0, 1, 0);
    vec3 lookat = vec3(1, 0, 0);
    mouse(lookat);
    vec3 forward = normalize(lookat - ro);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    vec3 lp = vec3(0, .2, 0);

// -----------------
// antialiasing loop

    vec3 finalcol = vec3(0.);

    for (int ii = 0; ii < AA; ii ++) {
        for (int jj = 0; jj < AA; jj++) {
            vec2 o = vec2(float(ii), float(jj)) / float(AA);
            vec2 uv = (2. * fragCoord + o - iResolution.xy) / iResolution.y;
            vec3 rd = normalize(uv.x * right + uv.y * up + forward * FOV);
            finalcol += render(ro, rd, lp);
        }
    }
    finalcol /= (float(AA) * float(AA));

    fragColor = vec4(sqrt(clamp(finalcol, 0., 1.)), 1.);
}

#define PI    3.141592654


// normal vectors of the four mirrors
mat4 M;

// the initial vertex
vec4 v0;

// cosh, sinh of the vertex radius and edge radius
float cvr, svr, csr, ssr;

// 2d rotation
vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }

// Minkowski inner product with Sylvester type (3, 1)
float hdot(in vec4 p, in vec4 q) { return dot(p.xyz, q.xyz) - p.w * q.w; }

// normalize a time-like vector <v, v> < 0
vec4 hnormalize(in vec4 p) { return p / sqrt(-hdot(p, p)); }

// reflection about a plane with normal `n`
float href(inout vec4 p, in vec4 n) {
    float k = min(0., hdot(p, n));
    p -= 2. * k * n;
    return k;
}

// knighty's conservative distance conversion from hyperboloid to 3d flat distance.
// for a 3d point p, it's lifted to a 4d point q on the hyperboloid:
//
//        2p     1+r^2
// q = ( -----,  ----- ).
//       1-r^2   1-r^2
//
// any point with distance d (ca=cosh(d),sa=sinh(d)) to q can be written as q*ca + v*sa,
// wheren v is a unit tangent vector at q. We want v look like (sp, t).
// so we have two unknowns (s, t) and two equations:

// 1. s^2 * r^2 - t^2 = 1  (tangent vector must be space-like)
// 2. 2*s/(1-r^2)*r^2 - (1+r^2)/(1-r^2)*t = 0 (definition of tangent vector at q)

// solve for s, t we have s = (1+r^2)/(r *(1-r^2)) and t = 2*r/(1-r^2).
// hence the point we choose to project to 3d is
// ([ 2*ca/(1-r^2) + (1+r^2)/(r *(1-r^2))*sa ] * p,
//  [ ca*(1+r^2)/(1-r^2) + sa*2*r/(1-r^2) ])
float knightyDD(float ca, float sa, float r) {
    float x = 1. + r * r;
    float y = 2. - x;
    return (2. * r * ca + x * sa) / (x * ca + 2. * r * sa + y) - r;
}


// Given the Coxeter diagram and active mirrors (on/off),
// get the normals of the reflection mirrors and the initial vertex.
// Warning: due to the procedure used here, the first three mirrors must
// have "spherical" type.
void init(in float[6] diagram, in vec4 active_mirrors, inout mat4 M, inout vec4 v0) {
    // Cartan matrix of the Coxeter group:
    // ( -1  c01  c02  c03)
    // (c01   -1  c12  c13)
    // (c02  c12   -1  c23)
    // (c03  c13  c23   -1)
    // cij = -cos(PI / (Coxeter label bewteen i-th and j-th mirror))
    float c01 = -cos(PI / diagram[0]);
    float c02 = -cos(PI / diagram[1]);
    float c03 = -cos(PI / diagram[2]);
    float c12 = -cos(PI / diagram[3]);
    float c13 = -cos(PI / diagram[4]);
    float c23 = -cos(PI / diagram[5]);

    vec4 A, B, C, D;

    // find the reflection mirrors A, B, C, D.
    // A can be always chosen as x-axis
    A = vec4(1, 0, 0, 0);

    B = vec4(c01, sqrt(1. - c01*c01), 0., 0.);

    C = vec4(c02, 0, 0, 0);
    C.y = (c12 - C.x * B.x) / B.y;
    C.z = sqrt(abs(1. - dot(C.xy, C.xy))); // avoid rounding error in paracompact case

    D = vec4(c03, 0, 0, 0);
    D.y = (c13 - D.x * B.x) / B.y;
    D.z = (c23 - dot(D.xy, C.xy) ) / C.z;
    // !important: if you want to make the fundamental chamber lie in the upper
    // sheet of the hyperboloid then you must use "-" sign here.
    D.w = -sqrt(abs(dot(D.xyz, D.xyz) - 1.));

    // below is suggested by @mla
    vec4 H = vec4(1, 1, 1, -1);
    v0 = hnormalize(active_mirrors * inverse(mat4(H*A, H*B, H*C, H*D)));
    M = mat4(A, B, C, D);
}


// if distance between p and q is a, C is a circle with radius VR centered at q,
// then the distance from p to C is a - VR, hence
// cosh(a - VR) = cosh(a)cosh(VR) - sinh(a)sinh(VR)
// sinh(a - VR) = sinh(a)cosh(VR) - cosh(a)sinh(VR)
float dVertex(vec4 p, float r) {
    float ca = -hdot(p, v0);
    float sa = 0.5 * sqrt(-hdot(p - v0, p - v0) * hdot(p + v0, p + v0));
    return knightyDD(ca * cvr - sa * svr,
                     sa * cvr - ca * svr, r);
}

// let pj = a * n + b * v0 be the projection of p onto the plane given by (v0, n).
// take Minkowski dot with p - pj by v0 and n:
// (p, n) = a + b * (v0, n)
// (p, v0) = a * (v0, n) - b
// then solve this 2x2 linear system.
float dSegment(vec4 p, vec4 n, float r) {
    float pn = hdot(p, n);
    float pv = hdot(p, v0);
    float nv = hdot(n, v0);
    float det = -1.0 - nv * nv;
    float a = (-nv * pv - pn) / det;
    float b = (pv - pn * nv) / det;
    vec4 pj = hnormalize(min(a, 0.) * n + b * v0);
    float ca = -hdot(p, pj);
    float sa = 0.5 * sqrt(-hdot(p - pj, p - pj) * hdot(p + pj, p + pj));
    return knightyDD(ca * csr - sa * ssr, sa * csr - ca * ssr, r);
}


// fold a point p in 4d up to a given maximum of steps, return true if
// we successfully transformed p into the fundamental domain else return false.
bool fold(inout vec4 p, mat4 M) {
    const int max_iter = 40;
    float k;
    for(int i = 0; i < max_iter; i++) {
        k = 0.;
        p.x = abs(p.x);
        k += href(p, M[1]);
        k += href(p, M[2]);
        k += href(p, M[3]);
        // break as soon as we find it's already in the fundamental domain
        if(k == 0.) return true;
    }
    return false;
}

// find the shortest distance from p to the 4 base edges
// as well as the index of that edge.
float dSegments(vec4 p, float r, inout float index) {
    float d = 1.0;
    for(int i=0; i<4; i++) {
        float dseg = dSegment(p, M[i], r);
        if (dseg < d) {
            d = dseg;
            index = float(i);
        }
    }
    return d;
}

