	vec2 p=vec2(r.x+r.y, r.x-r.y);
	p.x*=d;
	p*=lh;
	float a=dot(rand2(11.*co+vec2(1.,1.)),vec2(1.,1.));
	a=floor(a*float(ApertureNbrSides))*2.*PI/float(ApertureNbrSides);
	vec2 cs= vec2(cos(a), sin(a));
	p= vec2(cs.x*p.x-cs.y*p.y, cs.x*p.y+cs.y*p.x);
