const bool animate = true;
const bool grid = true; // (when not animating)

const float octaves = 4.0;

// Grid lines:
const float lineWidth = 1.0;
const float lineFeather = 1.0;
const vec3 gridColor = vec3(0);

// Power of contrast ramp function

vec2 pixelToXY (vec2 point) {
  	vec2 aspect = vec2(1, iResolution.y / iResolution.x);
    return (point / iResolution.xy - 0.5) * aspect * 5.0;
}

// Select an animation state
float selector (float time) {
    const float period = 10.0;
    float t = fract(time / period);
    return smoothstep(0.4, 0.5, t) * smoothstep(1.0, 0.9, t);
}

vec3 colorscale (float phase) {
    return rainbow(phase / 2.0 - 0.25);
}

vec2 complexContouringGridFunction (vec2 x) {
// distance to integer grid x=k, k in Z
  return 2. * abs(fract(x - 0.5) - 0.5);
}

float checkerboard (vec2 xy) {
// checker pattern with unit length squares
  vec2 f = fract(xy * 0.5) * 2.0 - 1.0;
  return f.x * f.y > 0.0 ? 1.0 : 0.0;
}

vec4 rectangularDomainColoring (vec4 f_df,
                     vec2 steps,
                     vec2 baseScale,
                     vec2 gridOpacity,
                     float shadingOpacity,
                     float lineWidth,
                     float lineFeather,
                     vec3 gridColor,
                     float phaseColoring
) {
  vec2 f = f_df.xy;
  vec2 df = f_df.zw;
  float absf = hypot(f);

  baseScale *= 10.0;

  // Computed as d|f|
  float absdf = hypot(df);

  float logdfx = log2(absdf) / log2(steps.x);  // logDx (|df|)
  float Nx = floor(logdfx);
  float fx = Nx + 1.0 - logdfx;

  float logdfy = log2(absdf) / log2(steps.y);
  float Ny = floor(logdfy);
  float fy = Ny + 1.0 - logdfy;

  // delta = D^{N} * m
  vec2 delta = vec2(pow(steps.x, Nx), pow(steps.y, Ny)) * baseScale;

  float width1 = max(0.0, lineWidth - lineFeather);
  float width2 = lineWidth + lineFeather;

  float totalWeight = 0.0;
  float shading = 0.0;

  vec2 octaveScale = vec2(1.0);
  vec2 grid = vec2(0.0);
  vec2 gridScaleBase = vec2(
    pow(steps.x, fx),
    pow(steps.y, fy)
  );

  for(float i = 0.; i < octaves; i++) {
    float w0 = i == 0. ? 1e-4 : i + 1.;
    float w1 = i == octaves - 1. ? 1e-4 : i + 2.;
    float w = mix(w0, w1, fx);

    totalWeight += w;
    vec2 value = f / delta * octaveScale;

    vec2 gridSlope = baseScale * gridScaleBase / octaveScale / steps;


    // draw contour lines for value = k, i.e. f / delta * octaveScale = k
    vec2 xygrid = complexContouringGridFunction(value) * gridSlope;

    grid += w * vec2(smoothstep(width2, width1, xygrid.x), smoothstep(width2, width1, xygrid.y));

    shading += w * checkerboard(value);

    octaveScale /= steps;
  }

  shading = shading / totalWeight;
  grid /= totalWeight;
  grid *= gridOpacity;

  float carg = atan(f_df.y, f_df.x) * HALF_PI_INV * 2.0;
  vec3 color = mix(vec3(1.0), clamp(colorscale(carg), 0.0, 1.0), phaseColoring);

  const float gamma = 0.454;
  color.r = pow(color.r, gamma);
  color.g = pow(color.g, gamma);
  color.b = pow(color.b, gamma);

  color = mix(vec3(1), color, 0.97);

  float mixedGrid = max(grid.x, grid.y);
  float shade = mix(1.0, shading, shadingOpacity);
  vec3 result = clamp(mix(color * shade, gridColor, mixedGrid), vec3(0), vec3(1));

  result.r = pow(result.r, 1.0 / gamma);
  result.g = pow(result.g, 1.0 / gamma);
  result.b = pow(result.b, 1.0 / gamma);

  return vec4(result, 1);
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 xy = pixelToXY(fragCoord);
    vec2 mouse = pixelToXY(iMouse.xy);

    vec2 fz = f(xy, iTime * 0.2, mouse);

    // fwidth(fz) works, but it adds ugly anisotropy in the width of lines near zeros/poles.
    // Insead, we compute the magnitude of the derivatives separately.
    //
    // Also *NOTE* that this is a very important place in which we use `hypot` instead of an
    // algebraically equivalent built-in `length`. Floating point is limited and we lose lots
    // of our floating point domain if we're not careful about over/underflow.
    vec4 fdf = vec4(fz, vec2(hypot(dFdx(fz)), hypot(dFdy(fz))));

   	float select = animate ? selector(iTime) : (grid ? 1.0 : 0.0);

    fragColor = rectangularDomainColoring(
        fdf,
        vec2(8.0), // steps
        vec2(1.0), // scale
        mix(vec2(0.4), vec2(1.0), select), // grid opacity
        mix(0.2, 0.0, select),       // shading opacity
        lineWidth,
        lineFeather,
        gridColor,
        mix(1.0, 0.0, select)        // phase coloring
    );
}

// Complex math! Beware that these functions are not all great for overflow,
// even though a GPU is precisely the sort of place where you ought to be 
// *very* concerned about overflow.
//
// This also includes a partial implementation of automatic differentiation
// for complex arithmetic using vec4 as a + bi --> vec4(a, b, da, db). This
// may be used to successfully avoid standard derivatives, though I just
// didn't find it worthwhile when standard derivatives are so easy and well
// supported.

#define PI 3.141592653589793238
#define TO_RADIANS 0.01745329251
#define HALF_PI 1.57079633
#define HALF_PI_INV 0.15915494309
#define PI_INV 0.31830988618
#define TWO_PI 6.28318530718

float hypot (vec2 z) {
  float x = abs(z.x);
  float y = abs(z.y);
  float t = min(x, y);
  x = max(x, y);
  t = t / x;
  return x * sqrt(1.0 + t * t);
}

vec2 cadd (vec2 a, vec2 b) {
  return a + b;
}

vec2 csub (vec2 a, vec2 b) {
  return a - b;
}

float cmod (vec2 z) {
  return hypot(z);
} 

vec2 csqrt (vec2 z) {
  float t = sqrt(2.0 * (cmod(z) + (z.x >= 0.0 ? z.x : -z.x)));
  vec2 f = vec2(0.5 * t, abs(z.y) / t);

  if (z.x < 0.0) f.xy = f.yx;
  if (z.y < 0.0) f.y = -f.y;

  return f;
}

/*
float sinh (float x) {
  return 0.5 * (exp(x) - exp(-x));
}

float cosh (float x) {
  return 0.5 * (exp(x) + exp(-x));
}*/

vec2 sinhcosh (float x) {
  vec2 ex = exp(vec2(x, -x));
  return 0.5 * (ex - vec2(ex.y, -ex.x));
}

float cabs (vec2 z) {
  return cmod(z);
}

vec2 clog(vec2 z) {
  return vec2(
    log(hypot(z)),
    atan(z.y, z.x)
  );
}

vec2 catan (vec2 z) {
  float a = z.x * z.x + (1.0 - z.y) * (1.0 - z.y);
  vec2 b = clog(vec2(1.0 - z.y * z.y - z.x * z.x, -2.0 * z.x) / a);
  return 0.5 * vec2(-b.y, b.x);
} 

vec2 catanh (vec2 z) {
  float oneMinus = 1.0 - z.x;
  float onePlus = 1.0 + z.x;
  float d = oneMinus * oneMinus + z.y * z.y;

  vec2 x = vec2(onePlus * oneMinus - z.y * z.y, z.y * 2.0) / d;

  vec2 result = vec2(log(hypot(x)), atan(x.y, x.x)) * 0.5;

  return result;
} 

vec2 cacos (vec2 z) {
  vec2 a = csqrt(vec2(
    z.y * z.y - z.x * z.x + 1.0,
    -2.0 * z.x * z.y
  ));

  vec2 b = clog(vec2(a.x - z.y, a.y + z.x));
  return vec2(HALF_PI - b.y, b.x);
} 

vec2 cacosh (vec2 z) {
  vec2 a = cacos(z);

  if (a.y <= 0.0) {
    return vec2(-a.y, a.x);
  }

  return vec2(a.y, -a.x);
} 

vec2 cacot (vec2 z) {
  return catan(vec2(z.x, -z.y) / dot(z, z));
} 

vec2 cacoth(vec2 z) {
  return catanh(vec2(z.x, -z.y) / dot(z, z));
} 

vec2 casin (vec2 z) {
  vec2 a = csqrt(vec2(
    z.y * z.y - z.x * z.x + 1.0,
    -2.0 * z.x * z.y
  ));

  vec2 b = clog(vec2(
    a.x - z.y,
    a.y + z.x
  ));

  return vec2(b.y, -b.x);
} 

vec2 casinh (vec2 z) {
  vec2 res = casin(vec2(z.y, -z.x));
  return vec2(-res.y, res.x);
} 

vec2 cacsch(vec2 z) {
  return casinh(vec2(z.x, -z.y) / dot(z, z));
