    return t;
}

vec3 calcNormal( in vec3 pos, in float t, in float s )
{
    float precis = 0.001 * t;

    vec2 e = vec2(1.0,-1.0)*precis;
    return normalize( e.xyy*map( pos + e.xyy, s ) + 
					  e.yyx*map( pos + e.yyx, s ) + 
					  e.yxy*map( pos + e.yxy, s ) + 
                      e.xxx*map( pos + e.xxx, s ) );
}

vec3 render( in vec3 ro, in vec3 rd, in float anim )
{
    // trace	
    vec3 col = vec3(0.0);
    float t = trace( ro, rd, anim );
    if( t>0.0 )
    {
        vec4 tra = orb;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, t, anim );

        // lighting
        vec3  light1 = vec3(  0.577, 0.577, -0.577 );
        vec3  light2 = vec3( -0.707, 0.000,  0.707 );
        float key = clamp( dot( light1, nor ), 0.0, 1.0 );
        float bac = clamp( 0.2 + 0.8*dot( light2, nor ), 0.0, 1.0 );
        float amb = (0.7+0.3*nor.y);
        float ao = pow( clamp(tra.w*2.0,0.0,1.0), 1.2 );

        vec3 brdf  = 1.0*vec3(0.40,0.40,0.40)*amb*ao;
        brdf += 1.0*vec3(1.00,1.00,1.00)*key*ao;
        brdf += 1.0*vec3(0.40,0.40,0.40)*bac*ao;

        // material		
        vec3 rgb = vec3(1.0);
        rgb = mix( rgb, vec3(1.0,0.80,0.2), clamp(6.0*tra.y,0.0,1.0) );
        rgb = mix( rgb, vec3(1.0,0.55,0.0), pow(clamp(1.0-2.0*tra.z,0.0,1.0),8.0) );

        // color
        col = rgb*brdf*exp(-0.2*t);
    }

    return sqrt(col);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    init();
    float time = iTime*0.25;
    float anim = 1.1 + 0.5*smoothstep( -0.3, 0.3, cos(0.1*iTime) );

    vec3 tot = vec3(0.0);
    #if AA>1
    for( int jj=0; jj<AA; jj++ )
    for( int ii=0; ii<AA; ii++ )
    #else
    int ii = 1, jj = 1;
    #endif
    {
        vec2 q = fragCoord.xy+vec2(float(ii),float(jj))/float(AA);
        vec2 p = (2.0*q-iResolution.xy)/iResolution.y;

        // camera
        vec3 ro = vec3( 2.8*cos(0.1+.33*time), 0.4 + 0.30*cos(0.37*time), 2.8*cos(0.5+0.35*time) ).xzy;
        vec3 ta = vec3( 1.9*cos(1.2+.41*time), 0.4 + 0.10*cos(0.27*time), 1.9*cos(2.0+0.38*time) ).xzy;
        float roll = 0.2*cos(0.1*time);
        vec3 cw = normalize(ta-ro);
        vec3 cp = vec3(sin(roll), 0., cos(roll));
        vec3 cu = normalize(cross(cw,cp));
        vec3 cv = normalize(cross(cu,cw));
        vec3 rd = normalize( p.x*cu + p.y*cv + 2.0*cw );

        tot += render( ro, rd, anim );
    }

    tot = tot/float(AA*AA);

	fragColor = vec4( tot, 1.0 );	

}

int euclideanTriangleType = 1;
vec3 dihedralAngles0_234 = vec3(3, 2, 7);
vec3 dihedralAngles1_234 = vec3(2, 3, 7);
float dihedralAngle0_1 = 4.;
vec4 isRealBall = vec4(0);
int Iterations = 39;
float DEcor = 1.;
vec4 orbitTrap;

#define inf           1.0
#define L2(x)         dot(x, x)
#define PI           3.141592653

//infinite foldings-----------------------------------------------------------//
//fold about line with normal ndir (normalized) and dist to origin d.
void fold(inout vec2 p, in vec2 ndir, in float d) {
    float t = dot(p, ndir) + d;
    t = 2.*min(t,0.);
    p -= t * ndir;
}

//fmod with step = stp
void infmod(inout float x, float stp) {
    x *= 1./stp;
    x -= floor(x);
    x *= stp;
}

//infinite fold. stp is the tile size.
//its like performing infinite folds about plane at 0 and plane at stp
void inffold(inout float x, float stp) {
    x *= 1./(2.*stp);
    x -= floor(x);
    x  = 0.5 - abs( x - 0.5 );
    x *= 2.*stp;
}

//Simplest: fold into unit square then about the diagonal
void fold244(inout vec2 p) {
    inffold( p.x , 1. );
    inffold( p.y , 1. );
#define VAL sqrt(2.)/2.
    fold( p, -vec2( VAL, VAL ), VAL);
#undef VAL
}

//fold into rectangle then a little sequence of line folds.
void fold236(inout vec2 p) {
#define S3 sqrt(3.)
    inffold( p.x , 3. );
    inffold( p.y , S3 );
    vec2 n = -0.5 * vec2(S3, 1.);
    float d = .5 * S3;
    fold(p, n, d);
    p = abs(p);
    fold(p, n, d);
#undef S3
}

//most complicated
void fold333(inout vec2 p) {
#define S3 sqrt(3.)
    //change origin
    p.x += 1.;
    //fold y into segment of height sqrt(3)
    inffold( p.y ,S3 );
    //change of coordinates to go to ( (2,0) ; (1,sqrt(3)) ) ish basis. We only nees x coordinate to be transformed
    p.x -= p.y * 1. / S3;
    //do an fmod instead for x.
    infmod(p.x, 6.);
    //undo the coordinates change
    p.x += p.y * 1./S3;
    //The folding sequence... 4 folds
    //There are other choices. I've choosen the one where I need only one direction instead of two.
    vec2 n = -0.5 * vec2(S3, 1.);
    float d = 2. * S3;
    //1st
    fold(p, n, d);
    //2nd
    d = S3;
    fold(p, n, d);
    //3rd
    p.y = abs(p.y);
    //4th same as 2nd
    fold(p, n, d);
    //restore origin
    p.x -=1.;
#undef S3
}

const float s2 = sqrt(2.);
const float s3 = sqrt(3.);

float dihedral(float x) {
    return x == inf ? inf : cos(PI / x);
}

vec3 dihedral(vec3 v) {
    return vec3(dihedral(v.x), dihedral(v.y), dihedral(v.z));
}

struct Ball {
    bool isplane;
    vec3 n;
    float r;
    bool invert;
    bool isRealBall;
};

// coclusters are mirror balls, they corresponde to root vectors (space-like)
Ball[5] coclusters;
// clusters are real balls, they corresponde to space-like weight vectors
Ball[5] clusters;

Ball defaultBall() {
    return Ball(false, vec3(0, 0, -1), 0., false, false);
}

// Distance from a point to a ball
float sdistanceToBall(vec3 p, Ball B) {
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        return k;
    }
    else
        return length(p - B.n) - B.r;
}

Ball from_plane(vec3 n, float d) {
    return Ball(true, n, d, false, false);
}

Ball from_sphere(vec3 cen, float r) {
    return Ball(false, cen, r, false, false);
}

void invertBall(inout Ball B) {
    B.invert = !B.invert;
}

bool try_reflect(inout vec3 p,
                 in Ball B,
                 inout float scale,
                 inout vec4 orb) {
    orb = min(orb, vec4(abs(p),dot(p,p)));
    if (B.isplane) {
        float k = dot(vec4(p, 1), vec4(B.n, B.r));
        if (k >= 0.)
            return true;
        p -= 2. * k  * B.n;
        return false;
    }
    else {
        vec3 cen = B.n;
        float r = B.r;
        vec3 q = p - cen;
        float d2 = dot(q, q);
        float k = (r * r) / d2;
        if ( (k < 1.0 && B.invert) || (k > 1. && !B.invert) )
            return true;
        else {
            scale *= k;
            p = k * q + cen;
            return false;
        }
    }
}

Ball solveBall(mat3 M, vec3 b) {
    vec3 p = b * inverse(M);
    return from_sphere(vec3(p.xy, 0.), p.z);
}

Ball solveBall(vec2 P, Ball B0, Ball B1) {
    if (B0.isplane) {
        float z = B0.r;
        vec3 cen = vec3(P, z);
        float R = sqrt(L2(cen - B1.n) - B1.r*B1.r);
        return from_sphere(cen, R);
    }
    else {
        float r1 = B1.r;
        float r0 = B0.r;
        float z0 = B0.n.z;
        float k0 = L2(P - B0.n.xy);
        float k1 = L2(P - B1.n.xy);
        float z = (r1*r1 - r0*r0 + z0*z0 + k0 - k1) / (2.*z0);
        float R = sqrt(k1 + z*z - r1*r1);
        return from_sphere(vec3(P, z), R);
    }
}

void init() {

    mat3 M0, M1;
    vec3 b;
    Ball B0, B1, B2, B3, B4;
    vec3 t0 = dihedral(dihedralAngles0_234);
    vec3 t1 = dihedral(dihedralAngles1_234);
    float t01 = dihedral(dihedralAngle0_1);
    // A, B, C are the vertices of the triangle formed by mirror plane v2, v3, v4 and z=0 plane
    vec2 A, B, C;

    // the 236 case
    if (euclideanTriangleType == 0) {
        A = vec2(0, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s3/2., -0.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(s3/2., 0.5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(s3/2., 0.5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s3/2., 0);
    }

    // the 244 case
    else if (euclideanTriangleType == 1) {
        A = vec2(0, 0), B = vec2(0, 1), C = vec2(1, 0);
        B2 = from_plane(vec3(1, 0, 0), 0.);
        B3 = from_plane(vec3(-s2/2., -s2/2., 0), s2/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(1, 0, -t1.x), vec3(1./s2, 1./s2, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(1, 0, -t0.x), vec3(1./s2, 1./s2, t0.y), vec3(0, 1, -t0.z));
        b = vec3(0, s2/2., 0);
    }

    // the 333 case
    else {
        A = vec2(-1, 0), B = vec2(0, s3), C = vec2(1, 0);
        B2 = from_plane(vec3(s3/2., -.5, 0), s3/2.);
        B3 = from_plane(vec3(-s3/2., -.5, 0), s3/2.);
        B4 = from_plane(vec3(0, 1, 0), 0.);
        M1 = mat3(vec3(-s3/2., 0.5, t1.x), vec3(s3/2., .5, t1.y), vec3(0, 1, -t1.z));
        M0 = mat3(vec3(-s3/2., 0.5, t0.x), vec3(s3/2., .5, t0.y), vec3(0, 1, -t0.z));
        b = vec3(s3, s3, 0)/2.;
    }

    // now we solve the virtual ball B1, this can't be a plane
    B1 = solveBall(M1, b);
    invertBall(B1);

    // now we solve the virtual ball B0, this can be either a plane or a sphere
    // this depends on if all entries in dihedralAngles0 are all 2
    if (dot(dihedralAngles0_234, vec3(1)) == 6.) {
        B0 = from_plane(vec3(0, 0, -1), B1.r*t01);
    }
    else {
        B0 = solveBall(M0, b);
        float r1 = B1.r, r0 = B0.r;
        B0.n.z = sqrt(r0*r0 + r1*r1 + 2.*r0*r1*t01 - L2(B1.n.xy - B0.n.xy));
        invertBall(B0);
    }
    coclusters = Ball[5] (B0, B1, B2, B3, B4);

    //now we process the real balls
    for (int k = 0; k < 5; k++) {
        clusters[k] = defaultBall();
    }

    clusters[1] = from_plane(vec3(0, 0, -1.), B0.n.z);
    clusters[1].isRealBall = isRealBall.x == 1.;

    clusters[2] = solveBall(C, B0, B1);
    clusters[2].isRealBall = isRealBall.y == 1.;

    clusters[3] = solveBall(A, B0, B1);
    clusters[3].isRealBall = isRealBall.z == 1.;

    clusters[4] = solveBall(B, B0, B1);
    clusters[4].isRealBall = isRealBall.w == 1.;
}

#if 1
// In this version, invisible (non active) real balls are used to reduce oerstepping.
// One may notice that when activating other balls the ovestepping is educed a lot.
// Not very good but better than nothing. :)
float dist2balls(vec3 p, float scale) {
    float d = abs(p.z); //Ball 0
    for (int j = 1; j < 5; j++) {
        float s = clusters[j].isRealBall ? 0. : DEcor*scale;
        d = min( s + abs( sdistanceToBall( p, clusters[j] ) ) , d );
    }
    return d/scale;
}
#else
// Normal version
float dist2balls( vec3 p, float scale) {
    float d = abs(p.z); //Ball 0
    for (int j = 1; j < 5; j++)
        if (clusters[j].isRealBall)
            d = min(abs(sdistanceToBall(p, clusters[j])), d);
    return d/scale;
}
#endif

void EuclideanFold(inout vec3 p) {
    if( euclideanTriangleType == 0)
        fold236(p.xy);
    else if ( euclideanTriangleType == 1)
        fold244(p.xy);
    else
        fold333(p.xy);
}

float map(inout vec3 p, inout float scale, inout vec4 orb) {
    float d = dist2balls(p, scale);
    // bool cond = true;
    // K: Very strange behaviour : doing cond && tru_reflect() cuts things out.
    // Remove all those tests and just see if the orbit stops. Now DE is "much" better.
    for (int i = 0; i < Iterations ; i++) {
        vec3 ap = p;
        EuclideanFold(p);
        for (int k = 0; k < 2; k++)
            try_reflect(p, coclusters[k], scale, orb);
        if (all(not(bvec3(p-ap))))
            break;
        d = min( d, dist2balls(p, scale));
    }
#if 0
    // Slow version. DEs to balls are each iterations
    return d;
#else
    // Fast version. distance to balls is done only once.
    return dist2balls(p, scale);
#endif
}

float DE(vec3 p) {
    float DEfactor=1.;
    //orbiTrap is not initialized by default. That's what was producing the strange haze and colors.
    orbitTrap = vec4(1);
    float d = map(p, DEfactor, orbitTrap);

    //Call basic shape and scale its DE
    return 0.3 * d ;// Fudging here because fudge factor desn't seem to affect shadows :-/
}

// Fork of "Neo three worlds" by neozhaoliang. https://shadertoy.com/view/ss2cz3
// 2023-07-21 12:05:54

vec3 get_floor_color(vec2 z) {
    // Modify P, Q, R to change the tiling
    const float P = 2.;
    const float Q = 3.;
    const float R = 7.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const vec2 mB = vec2(-cp, sp);
    const float k1 = cos(PI / Q);
    const float k2 = (cos(PI / R) + cp * k1) / sp;
    const float rad = 1. / sqrt(k1 * k1 + k2 * k2 - 1.);
    const vec2 cen = vec2(k1 * rad, k2 * rad);
    const vec2 v0 = vec2(0, cen.y - sqrt(rad * rad - cen.x * cen.x));
    const vec2 n_ = vec2(-mB.y, mB.x);
    const float b_ = dot(cen, n_);
    const float c_ = dot(cen, cen) - rad * rad;
    const float k_ = b_ + sqrt(b_ * b_ - c_);
    const vec2 m0 = k_ * n_;

    vec2 p = square_to_disc(z);
    vec2 invCtr = vec2(1);
    float t = 1. / dot(p -invCtr, p - invCtr);
    p = mix(invCtr, p, t);
    p.x = -p.x;
    p = rot2(iTime/12.)*p;

    if (length(p) > 1.)
        p /= dot(p, p);

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        float d = length(p - cen) - rad;
        if (d < 0.) {
            p -= cen;
            p *= rad * rad / dot(p, p);
            p += cen;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    ln = min(ln, lBox(p, vec2(0), v0, .007));    
    ln = min(ln, lBox(p, vec2(0), m0, .007));
    ln = min(ln, length(p - cen) - rad - 0.007);

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime*.5 + count)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 4. + vec3(0, 1, 2));
    oCol = mix(oCol*.1, clamp(oCol*3.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*50. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize)));

    pnt -= .032;
    pnt = min(pnt, length(p) - .032);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.8, .97, sin(iTime * 1.5)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

vec3 get_ceil_color(vec2 p) {
    const float P = 3.;
    const float Q = 3.;
    const float R = 3.;
    const float cp = cos(PI / P), sp = sin(PI / P);
    const float cq = cos(PI / Q), cr = cos(PI / R);
    const vec2 mB = vec2(-cp, sp);
    const vec3 mC = vec3(-cr, -(cq + cr * cp) / sp, 1.);
    const vec2 v0 = vec2(0, -1./mC.y);
    const float k_ = mB.x / mB.y;
    const float x_ = 1. / (k_ * mC.y - mC.x);
    const float y_ = -k_ * x_;
    const vec2 m0 = vec2(x_, y_);

    p = rot2(iTime/12.)*p;

    float count = 0.;
    for (int i = 0; i < MAX_TILING_REFLECTIONS; i++) {
        if (p.x < 0.) {
            p.x = -p.x;
            count += 1.;
        }
        float k = dot(p, mB);
        if (k < 0.) {
            p -= 2. * k * mB;
            count += 1.;
        }

        k = dot(vec3(p, 1.), mC);
        if (k < 0.) {
            p -= 2. * k * mC.xy;
            count += 1.;
        }
    }

    float ln = 1e5, ln2 = 1e5, pnt = 1e5;
    //ln = min(ln, lBox(p, vec2(0), v0, .05));    
    //ln = min(ln, lBox(p, vec2(0), m0, .05));
    ln = min(ln, lBox(p, v0, m0, .05));

    ln2 = min(ln2, lBox(p, vec2(0), m0, .007));
    pnt = min(pnt, length(p - v0)); 
    pnt = min(pnt, length(p - m0)); 

    float sf = 2. / iResolution.y;
    float rnd = smoothstep(.8, .97, sin(iTime + count * PI)*.5 + .5);

    vec3 oCol = .55 + .45*cos(count * PI / 8. + vec3(0, 1, 2)).yzx;
    oCol = mix(oCol*.1, clamp(oCol*1.5, 0., 1.), rnd);

    float pat = smoothstep(0., .25, abs(fract(ln2*15. - .2) - .5)*2. -.2);

    float sh = clamp(.65 + ln/v0.y*4., 0., 1.);
    vec3 col = min(oCol*(pat*.2 + .9)*sh, 1.);

    col = mix(col, vec3(0), 1. - smoothstep(0., sf, ln));
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, -(ln - BlackRegionSize * 16.)));

    pnt -= .15;
    pnt = min(pnt, length(p) - .15);
    col = mix(col, vec3(0), 1. - smoothstep(0., sf, pnt));

    rnd = smoothstep(.4, .5, sin(iTime)*.5 + .5);
    vec3 blink = vec3(1.3, 0.25, 0.35);
    blink = mix(blink * 0.5, blink * 2.2, rnd);
    col = mix(col, blink, 1. - smoothstep(0., sf, pnt + .02));
    return col;
}

// distance to the four sides of mirrors 
float sdMirror(vec3 p) {
    vec2 d = RoomSize.xz - abs(p.xz);
    return min(d.x, d.y);
}

// the floor and the ceil
float sdFloor(vec3 p) {
    return RoomSize.y - abs(p.y);
}

// wood framework
float sdRoomFrame(vec3 p) {
    return sdBoxFrame(p, RoomSize, vec3(RoomFrameSize));
}

float sdMirrorFrame(vec3 p) {    
    float d1 = sdBoxFrame(p, RoomSize, WindowFrameSize.xyy);
    float d2 = sdBoxFrame(p, RoomSize, WindowFrameSize.yxy);
    float d3 = sdBoxFrame(p, RoomSize, WindowFrameSize.yyx);
    return min(d1, min(d2, d3));
}

// polyhedra with faces and edges
vec2 sdPolyhedra(vec3 p) {
    p *= RotObj;
    p = fold_spherical(p);
    p -= v0S;
    float d1 = sdPolyhedraFaces(p);
    float d2 = sdPolyhedraEdges(p);
    float d = min(d1, d2);
    float id = d1 < d2 ? 1. : 2.;
    return vec2(d, id);
}

float sdScene(vec3 p) {
    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    float d5 = sdPolyhedra(p).x;
    return min(d1, min(d2, min(d3, min(d4, d5))));
}

vec3 trace(vec3 pos, vec3 rd) {
    float h = 1.0;
    for (int i = 0; i < MAX_TRACE_STEPS; i++) {
        if (h < PRECISION)
            break;
        h = sdScene(pos);
        pos += rd * min(h, MAX_MARCH_STEP);
    }
    return pos;
}

float soft_shadow(vec3 pos, vec3 lp) {
    const float softness = 16.;
    const float shadowStartDistance = .1;
    const int shadowLoopCount = 16;

    vec3 ld = lp - pos;
    float ldist = max(length(lp), 0.001);
    ld /= ldist;
    float epsilon = .001;
    float t = shadowStartDistance;
    float h = 1.;
    float shade = 1.0;
    for (int i=0; i < shadowLoopCount; i++ ) {
        if (h < epsilon || t > ldist)
            break;
        h = sdScene(pos + ld * t);
        shade = min(shade, smoothstep(0.0, 1.0, softness*h/t));
        t += h;
    }
    return clamp(shade + 0.25, 0., 1.);
}

vec3 get_normal(vec3 p) {
    const vec2 d = vec2(-1, 1) * .001;
    return normalize(
            sdScene(p + d.xxx)*d.xxx +
            sdScene(p + d.yyx)*d.yyx +
            sdScene(p + d.yxy)*d.yxy +
            sdScene(p + d.xyy)*d.xyy);
}

struct Light {
    vec3 pos;
    vec3 col;
};

Light lights[] = Light[] (
    Light(vec3(2.5, 2.2, 2.5), vec3(1) * 5.),
    Light(vec3(-1, -.5, -1), vec3(1)),
    Light(vec3(-2.5,-1.9,-2.5), vec3(0, .3, 1))
);

vec4 wallColor(vec3 dir, vec3 nor, vec3 eds) {
    float d = min3(eds.x, eds.y, eds.z);

    // Texturing of walls
    vec3 albedo = texture(iChannel1, vec2(eds.xy * 2.0)).rgb;
    albedo = pow(albedo, vec3(2.2)) * 0.5;

    // Simple diffuse lighting
    float lighting = 0.2 + max(dot(nor, vec3(0.8, 0.5, 0.0)), 0.0);

    if (dot(dir, nor) < 0.0) {
        // Outer walls, just add a black line to hide seams
        float f = clamp(d * 1000.0 - 3.0, 0.0, 1.0);
        albedo = mix(vec3(0.01), albedo, f);
        return vec4(albedo * lighting, f);
    } else {
        // Inner walls, add fancy lights
        float m = max3(eds.x, eds.y, eds.z);
        vec2 a = fract(vec2(d, m) * 40.6) - 0.5;
        float b = 1.0 - sqrt(dot(a, a));
        b = 0.2 / (dot(a, a) + 0.2);

        float lightShape = 1.0 - clamp(d * 100.0 - 2.0, 0.0, 1.0);
        lightShape *= b;

        vec3 emissive = vec3(3.5, 1.8, 1.0);
        return vec4(mix(albedo * lighting, emissive, lightShape), 0.0);
    }
	return vec4(1.0);
}


void ray_bounce(in vec3 p,
                in vec3 rd,
                out vec3 diffuse,
                out vec3 ref_dir,
                out vec3 bounceTint,
                in vec3 normal) {

    vec3 bCol = vec3(0);
    for( int i=0; i < lights.length(); i++ ) {
        vec3 ld = lights[i].pos - p;
        float ldist = max(length(ld), 0.001);
        ld /= ldist;
        float diff = max(0., dot(normal, ld));
        float at = 1.5 / (1. + ldist * 0.3 + ldist * ldist * 0.1);
        float sh = soft_shadow(p, lights[i].pos);
        bCol += lights[i].col * sh * (.2 + diff) * at;
    }
    // ambient light
    vec3 ao = vec3(.03,.05,.07);
    // sample SDF to approximate occlusion
    ao *= exp2(min(0., sdScene(p + normal * 0.3) / 0.3 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .15) / .15 - 1.));
    ao *= exp2(min(0., sdScene(p + normal * .07) / .07 - 1.));
    bCol += ao;

    // https://www.shadertoy.com/view/tlBGR1
    vec3 albedo = vec3(.3,.5,.6) * .4;
    vec4 specLevel = vec4(0.9, 0.9, 0.9, 1);

    float d1 = sdMirror(p);
    float d2 = sdFloor(p);
    float d3 = sdRoomFrame(p);
    float d4 = sdMirrorFrame(p);
    vec2 sdPoly = sdPolyhedra(p);
    float d5 = sdPoly.x, id = sdPoly.y;
    float d = min(d1, min(min(d2, d3), min(d4, d5)));

    if (d == d3) {  // room frame
        albedo = vec3(.25);
        specLevel = vec4(0.08);
    }
    else if (d == d4) {  // mirror border
        albedo = vec3(0.01);
        specLevel = vec4(0.2, 0.2, 0.2, 1.);
    }
    else if (d == d5) {  // polyhedra
        if (id == 2.) {  // polyhedra edges
            albedo = vec3(0.15, 0.3, 0.8);
            specLevel = vec4(vec3(0.05), 1.);
        }
        else {  // polyhedra faces
            specLevel = vec4(0.7);
        }
    }
    else if (d == d2) {  // floor and ceil
        vec2 uv = p.xz;
        albedo = p.y < 0. ? get_floor_color(uv / (RoomSize.x - RoomFrameSize))
                          : get_ceil_color(uv * 2.);
        float ior = 1.33;
        float schlick = pow((ior - 1.) / (ior + 1.), 2.);
	    specLevel = vec4(vec3(schlick), 1);
    	specLevel *= mix(.2, .7, p.x);
    	normal = normalize(normal + (texture(iChannel0, vec2(p.xz) /2.).rgb - .5) * .005);
    }

    float fresnel = pow(dot(normal, rd) + 1., 5.);
    vec3 spec = mix(specLevel.xyz, specLevel.www, fresnel);

    bounceTint = spec;
    diffuse = albedo * (vec3(1) - spec) * bCol;
    ref_dir = reflect(rd, normal);
}

vec3 get_ray_color(vec3 ro, vec3 rd) {
    vec3 pos = ro;
    vec3 tint = vec3(1);

    vec3 col = vec3(0);
    for (int i = 0; i < MAX_RAY_BOUNCES; i++) {
        pos = trace(pos, rd);
        vec3 normal = get_normal(pos);
        vec3 bounceTint, diffuse;
        ray_bounce(pos, rd, diffuse, rd, bounceTint, normal);
        pos += normal * PRECISION * 2.;
        col += diffuse * tint;
        tint *= bounceTint;
        if (length(tint) < .01)
            break;
    }
    return col;
}

mat3 camera_matrix(vec3 eye, vec3 lookat, vec3 up) {
    vec3 forward = normalize(lookat - eye);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, -forward);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    init_spherical();

    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    vec2 movement = vec2(iTime * 0.2, sin(iTime * 0.2) * 0.5);
    vec3 eye = 2.5 * vec3(
        cos(movement.x) * cos(movement.y),
        0.5*sin(movement.y),
        sin(movement.x) * cos(movement.y));
    eye += vec3(1, 0.3, 1) * cos(vec3(0.75, 0.3, 0.2) * iTime);

    vec2 mouse = vec2(0);
    if (iMouse.x > 0.) {
        mouse = 2. * iMouse.xy / iResolution.y - 1.;
        eye = rot_y(mouse.x) * rot_x(mouse.y) * eye;
    }
    vec3 lookat = vec3(0);
    vec3 up = vec3(0, 1, 0);
    mat3 M = camera_matrix(eye, lookat, up);

    vec3 ray = M * normalize(vec3(uv, -2.));
    vec3 col = get_ray_color(eye, ray);
    fragColor = vec4(clamp(sqrt(col), 0., 1.), 1.);
}

