#define max3(x, y, z)     (max(x, max(y, z)))


const vec3 pqr_wythoff = vec3(5, 2, 3);
const vec3 bary = vec3(1, 1, 1);

mat3 matS, triS;
vec3 v0S;

void init_spherical() {
    vec3 c = cos(PI / pqr_wythoff);
    float sp = sin(PI / pqr_wythoff.x);
    vec3 m1 = vec3(1, 0, 0);
    vec3 m2 = vec3(-c.x, sp, 0);
    float x3 = -c.z;
    float y3 = -(c.y + c.x*c.z)/sp;
    float z3 = sqrt(1.0 - x3*x3 - y3*y3);
    vec3 m3 = vec3(x3, y3, z3);
    matS = mat3(m1, m2, m3);
    triS[0] = normalize(cross(m2, m3));
    triS[1] = normalize(cross(m3, m1));
    triS[2] = normalize(cross(m1, m2));
    v0S = normalize(bary * inverse(matS));
}

vec3 fold_spherical(vec3 p) {
    for (int i = 0; i < 5; i++)
    for (int j = 0; j < 3; j++) {
        p -= 2. * min(dot(p, matS[j]), 0.) * matS[j];
    }
    return p;
}

vec3 tex3D(sampler2D tex, in vec3 p, in vec3 n ) {
    n = max(n * n, 0.001);
    n /= (n.x + n.y + n.z ); 
	return (
        texture(tex, p.yz) * n.x +
        texture(tex, p.zx) * n.y +
        texture(tex, p.xy) * n.z
    ).xyz;
}

mat2 rot2(in float a) {
    float c = cos(a), s = sin(a); return mat2(c, -s, s, c);
}

mat3 rot_x(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0,  0, 
                0, cx, sx,
                0, -sx, cx);
}

mat3 rot_y(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);
}

mat3 rot_z(in float t) {
    float cz = cos(t), sz = sin(t);
    return mat3(cz, -sz, 0.,
                sz, cz, 0.,
                0., 0., 1.);
}

float sBox(in vec2 p, in vec2 b) {
    vec2 d = abs(p) - b;
    return min(max(d.x, d.y), 0.) + length(max(d, 0.));
}

float lBox(vec2 p, vec2 a, vec2 b, float ew) {
    float ang = atan(b.y - a.y, b.x - a.x);
    p = rot2(ang)*(p - mix(a, b, .5));

    vec2 l = vec2(length(b - a), ew);
    return sBox(p, (l + ew)/2.) ;
}

// https://www.shadertoy.com/view/3ljcRh
// I changed e to vec3 so it works for non-uniform edge size
float sdBoxFrame(vec3 p, vec3 b, vec3 e) {
    p = abs(p) - b;
    vec3 q = abs(p + e) - e;
    return min(min(
        length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
        length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
        length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float sdPolyhedraEdges(vec3 p) {
    float d = 1e5;
    for (int i = 0; i < 3; i++) {
        d = min(d, L2(p - min(0., dot(p, matS[i])) * matS[i]));
    }
	return sqrt(d) - PolyhedraEdgeSize;
}

float sdPolyhedraFaces(vec3 p) {
   return max(dot(p, triS[0]), max(dot(p, triS[1]), dot(p, triS[2]))) - PolyhedraFaceOffset;
}

//complex multiplication
vec2 cMul(vec2 a, vec2 b) {
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

// complex inverse 
vec2 cDiv( vec2 a, vec2 b ) {
  float d = dot(b, b);
  return vec2(dot(a, b), a.y*b.x - a.x*b.y ) / d;
}

// complex inverse 
vec2 cInv(vec2 a) {
	return vec2(a.x, -a.y) / dot(a, a);
}

// code below taken from @mla's https://www.shadertoy.com/view/Mlsfzs
void sncndn(float u, float k2,
            out float sn, out float cn, out float dn) {
    float emc = 1.0 - k2;
    float a = 1.0, b, c;
    const int N = 4;
    float em[N], en[N];
    dn = 1.0;
    for (int i = 0; i < N; i++) {
        em[i] = a;
        emc = sqrt(emc);
        en[i] = emc;
        c = 0.5*(a + emc);
        emc *= a;
        a = c;
    }

    u = c*u;
    sn = sin(u);
    cn = cos(u);
    if (sn != 0.0) {
        a = cn / sn;
        c *= a;
        for(int i = N - 1; i >= 0; i--) {
            b = em[i];
            a *= c;
            c *= dn;
            dn = (en[i] + a) / (b + a);
            a = c/b;
        }
        a = 1.0 / sqrt(c*c + 1.0);
        if (sn < 0.0)
            sn = -a;
        else
            sn = a;
        cn = c * sn;
    }
}

vec2 cn(vec2 z, float k2) {
  float snu, cnu, dnu, snv, cnv, dnv;
  sncndn(z.x, k2, snu, cnu, dnu);
  sncndn(z.y, 1.0-k2, snv, cnv, dnv);
  return vec2(cnu * cnv, -snu*dnu*snv*dnv) / (1. - dnu*dnu*snv*snv);
}

vec2 square_to_disc(vec2 z) {     
    z = cDiv(z, vec2(1));
    z -= vec2(1, 0);
    z *= 1.854;
    z = cn(z, 0.5);
    z = cMul(z, vec2(0.70711));
    return z;
}

// Fork of "Simple & Cheap Die SDF" by fizzer. https://shadertoy.com/view/csySDG
// 2023-04-15 03:34:19

// Distance to hemisphere-shaped hole in a plane.
float hole(vec3 p, float r)
{
    float c = min(0., length(p.xz) - r);
    return (p.y > 0.) ? length(vec2(c, p.y)) : r - length(p);
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

// Die SDF.
float die(vec3 p)
{
    // Get index of closest face on die, and local point b.
    vec3 q = abs(p);

    bvec3 dots = sign(p.z) > 0. ? bvec3(false, true, true) : bvec3(true, true, false);
    vec3 b = p.xzy * sign(p.z);

    if(q.x > q.y && q.x > q.z)
    {
        b = p.zxy * sign(p.x);
        b.z = -b.z;
        dots = sign(p.x) > 0. ? bvec3(false, true, false) : bvec3(true, true, true);
    }
    else if(q.y > q.z)
    {
        b = p.zyx * sign(p.y);
        dots = sign(p.y) > 0. ? bvec3(false, false, true) : bvec3(false, false, false);
    }

    // Some parameteric constants.
    float r = .02, r2 = .04 ,r3 = .13;

    b.y -= .5 - r;

    float d;

    if(all(not(dots)))
    {
        // Special case for side 6.
        b.x = abs(b.x);
        b.z = mod(b.z + .3 / 2., .3) - .3 / 2.;
        d = hole(b - vec3(.2, 0, 0), r3);
    }
    else
    {
        // The sides other than 6 have reflective symmetry in two lines.
        if(b.x > -b.z)
            b.xz = -b.zx;

        if(b.x > b.z)
            b.xz = b.zx;

        // Thanks to this symmetry, the presence of only three dots account
        // for all of the sides. So the effective LUT contains 3 bits per face.
        if(b.z - b.x > .25)
            d = dots.y ? hole(b - vec3(-1, 0, +1) / 4., r3) : b.y;
        else if(-b.x - b.z > .25)
            d = dots.x ? hole(b - vec3(-1, 0, -1) / 4., r3) : b.y;
        else
            d = dots.z ? hole(b, r3): b.y;
    }

    // Use sdRoundBox to bevel the outer edges of the die.
    return max(d - r, sdRoundBox(p, vec3(.5) - r2, r2));
}

float dist(vec3 p)
{
    return die(p);
}

vec3 norm(vec3 p)
{
    // The usual.
    vec3 e = vec3(1e-4, 0, 0);
    float d = dist(p);
    return normalize(vec3(dist(p + e.xyy), dist(p + e.yxy), dist(p + e.yyx)) - d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    // Camera position and target.
    vec3 campos = vec3(cos(iTime) * 2., sin(iTime / 2.) * 2., sin(iTime) * 2.);

    if(iMouse.z > 0.)
    {
        float u = -iMouse.x / iResolution.x * 6.;
        float v = -(iMouse.y - iResolution.y / 2.) / iResolution.y * 3.;
        campos = vec3(cos(u) * 2., v, sin(u) * 2.);
    }

    vec3 camtarget = vec3(0);
	vec3 ro = campos, rd = normalize(vec3(uv, 1.));

    // Camera lookat.
    vec3 w = normalize(camtarget - campos);
    vec3 u = normalize(cross(vec3(0, 1, 0), w));
    vec3 v = cross(w, u);
    rd = mat3(u, v, w) * rd;

    float t = 0.;
    for(int i = 0; i < 100; ++i)
    {
        float d = dist(ro + rd * t);
        if(abs(d) < 1e-4 || t > 10.)
            break;
        t += d;
    }

    vec3 rp = ro + rd * t;
    vec3 col = vec3(.12);

    if(t < 10.)
    {
        // Typical lighting and shading.
        vec3 l = normalize(vec3(1, 2, 1.5));
        vec3 n = norm(rp);
        vec3 baseColour = mix(vec3(0), vec3(1), smoothstep(.01, .02, sdRoundBox(rp, vec3(.38), .1)));
        vec3 rr = reflect(rd, n);
        vec3 spec = textureLod(iChannel0, rr, 1.).rgb;
        vec3 diff = vec3(0.5 + 0.5 * dot(n, l)) + vec3(0.5 + 0.5 * -n.y) * vec3(.1, .1, .2) * 1.5;
        float fresnel = mix(0.01, .4, pow(clamp(1. + dot(rd, n), 0., 1.), 2.));

        col = mix(baseColour * diff, spec, fresnel);
    }

    // Output and gamma correction.
    fragColor = vec4(pow(col, vec3(1. / 2.2)), 1.);
}

