// 2d rotation
vec2 rot2d(vec2 p, float a) { return cos(a) * p + sin(a) * vec2(p.y, -p.x); }

void rot4d(inout vec4 p)
{
    p.xy = rot2d(p.xy, iTime*0.2);
    p.zw = rot2d(p.zw, iTime*0.2);
}



//--- camera stuff ---//

mat3 setCamera(in vec3 ro, in vec3 ta)
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(0.0, 1.0, sin(0.59*iTime));
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));

    return mat3(cu, cv, cw);
}


//--- scene description ---//

float distanceToScene(vec3 p)
{
    float r = length(p);
    vec4 q = vec4(2.*p, 1.-r*r) / (1.+r*r);
    rot4d(q);
    bool found = fold(q);
    if (found) {
        float id;
        float dvert = dVertex(q, r);
        float dedge = dSegments(q, r, id);
        id = (dvert < dedge) ? 4. : id;
        return min(dvert, dedge);
        }
        return 1e5;
}


//--- cheap normal computing ---//

vec3 computeSurfaceNormal(vec3 p)
{
    float d = distanceToScene(p);

    return normalize(vec3(
        distanceToScene(p + vec3(0.001, 0.0, 0.0)) - d,
        distanceToScene(p + vec3(0.0, 0.001, 0.0)) - d,
        distanceToScene(p + vec3(0.0, 0.0, 0.001)) - d));
}


//--- output color ---//

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
init();

    //--- camera setup ---//

    float rtime = 0.35*iTime;

    vec2 p   = (-iResolution.xy + 2.0*fragCoord - 1.0) / iResolution.y;
 	vec3 pos = vec3(5.0 + 5.0*cos(rtime), 10.0*cos(1.2*rtime), 6.0 + 5.0*sin(0.78*rtime));
    vec3 tar = vec3(0.0);
    vec3 dir = setCamera(pos, tar) * normalize(vec3(p.xy, 6.));  


    //--- distance to nearest object in the scene ---//

	float t = 0.0;
	for(int i = 0; i < 210; i++)
    {
		float d = distanceToScene(pos + t*dir);
		if(d < 0.003) break;

		t += d;


		//--- early skip of background pixels ---//

        if (t > 27.0)
        {
            fragColor = vec4(0.0);
            return;
        }
	}


    //--- output color depends on few things ---//

    vec3  sn = computeSurfaceNormal(pos + t*dir);			// surface normal
    float dc = clamp(dot(sn, normalize(pos)), 0.0, 1.0);	// diffuse component
    float sr = pow(dc, 100.0);								// specular reflection
    float od = length(pos + t*dir);							// distance to origin

	fragColor     = abs(dir.xzyz);
    fragColor    *= 0.2 + 0.8*dc;
    fragColor.yz *= clamp(od, 0.0, 1.0);
    fragColor    += sr;

}

#define PI    3.141592653
const float[6] diagram = float[6](3.0, 2.0, 2.0, 3.0, 2.0, 5.0);

// active mirrors, 1 for active, 0 for non-active. This determines the position
// of the initial vertex hence the truncation type of the honeycomb.
const vec4 active_mirrors = vec4(1.0, 0.0, 0.0, 0.0);

// normal vectors of the four mirrors
mat4 M;

// the initial vertex
vec4 v0;

// size of the vertex and segments
const float vradius = 0.035;
const float sradius = 0.01;

float cvr = cos(vradius), svr = sin(vradius);
float csr = cos(sradius), ssr = sin(sradius);

// reflection about a plane with normal `n`
float ref(inout vec4 p, in vec4 n)
{
    float k = min(0., dot(p, n));
    p -= 2. * k * n;
    return k;
}



float knightyDD(float ca, float sa, float r)
{
    float x = 1. - r * r;
    float y = 2. - x;
    return r - (2. * r * ca - x * sa) / (x * ca + 2. * r * sa + y);
}


void init()
{
    // Cartan matrix of the Coxeter group:
    // ( -1  c01  c02  c03)
    // (c01   -1  c12  c13)
    // (c02  c12   -1  c23)
    // (c03  c13  c23   -1)
    // cij = -cos(PI / (Coxeter label bewteen i-th and j-th mirror))
    float c01 = -cos(PI / diagram[0]);
    float c02 = -cos(PI / diagram[1]);
    float c03 = -cos(PI / diagram[2]);
    float c12 = -cos(PI / diagram[3]);
    float c13 = -cos(PI / diagram[4]);
    float c23 = -cos(PI / diagram[5]);

    vec4 A, B, C, D;
    // find the reflection mirrors A, B, C, D.
    // A can be always chosen as x-axis
    A = vec4(1., 0., 0., 0.);
    B = vec4(c01, sqrt(1. - c01*c01), 0., 0.);
    C = vec4(c02, 0., 0., 0.);
    C.y = (c12 - C.x * B.x) / B.y;
    C.z = sqrt(abs(1. - dot(C.xy, C.xy))); // avoid rounding error in paracompact case
    D = vec4(c03, 0., 0., 0.);
    D.y = (c13 - D.x * B.x) / B.y;
    D.z = (c23 - dot(D.xy, C.xy) ) / C.z;
    D.w = -sqrt(abs(1. - dot(D.xyz, D.xyz))); // !important: - sign is used here

    M = mat4(A, B, C, D);
    v0 = normalize(active_mirrors * inverse(M));

}

// fold a point p in 4d up to a given maximum of steps, return true if
// we successfully transformed p into the fundamental domain else return false.
bool fold(inout vec4 p)
{
    float k;
    for(int i=0; i<20; i++)
    {
        k = 0.;
        p.x = abs(p.x);
        k += ref(p, M[1]);
        k += ref(p, M[2]);
        k += ref(p, M[3]);
        // break as soon as we find it's already in the fundamental domain
        if(k == 0.) return true;
    }
    return false;
}

// if distance between p and q is a, C is a circle with radius VR centered at q,
// then the distance from p to C is a - VR, hence
// cosh(a - VR) = cosh(a)cosh(VR) - sinh(a)sinh(VR)
// sinh(a - VR) = sinh(a)cosh(VR) - cosh(a)sinh(VR)
float dVertex(vec4 p, float r)
{
    float ca = dot(p, v0);
    float sa = 0.5 * length(p-v0) * length(p+v0);
    return knightyDD(ca * cvr + sa * svr, sa * cvr - ca * svr, r);
}
float dSegment(vec4 p, vec4 n, float r)
{
    float pn = dot(p, n);
    float pv = dot(p, v0);
    float nv = dot(n, v0);

    float a = pv - pn*nv;
    float b = pn - pv*nv;
    vec4 pj = normalize(min(b, 0.) * n + a * v0);
    float ca = dot(p, pj);
    float sa = 0.5 * sqrt(1.-ca*ca);
    return knightyDD(ca * csr + sa * ssr, sa * csr - ca * ssr, r);
}

float dSegments(vec4 p, float r, inout float index)
{
    float d = 1.0;
    for(int i=0; i<4; i++)
    {
        float dseg = dSegment(p, M[i], r);
        if (dseg < d)
        {
            d = dseg;
            index = float(i);
        }
    }
    return d;
}
